          </Button>
        ) : (
          <Button
            onClick={handleNext}
            className="w-full btn-glass text-lg font-semibold py-4 bg-white/20 hover:bg-white/30 text-white border-0"
          >
            Next
            <ChevronRight className="w-5 h-5 ml-2" />
          </Button>
        )}
      </div>
    </div>
  );
};

