} from 'lucide-react';
import { Switch } from '@/components/ui/switch';

const Profile = () => {
  const navigate = useNavigate();
  const [userData, setUserData] = useState<any>(null);
  const [stats, setStats] = useState<UserStats>({
    totalEarnings: 0,
    activeListings: 0,
    successfulSales: 0,
    totalViews: 0,
    avgRating: 4.0,
    diagnosisCount: 0,
    advisorySaved: 0,
    level: 'New Farmer',
    joinedDate: new Date().toISOString()
  });
  const [isLoading, setIsLoading] = useState(true);
  const [notificationsEnabled, setNotificationsEnabled] = useState(true);

  const [achievements, setAchievements] = useState([
    { id: 1, title: 'First Sale', icon: Trophy, completed: false, color: 'text-yellow-500' },
    { id: 2, title: 'Top Seller', icon: Crown, completed: false, color: 'text-purple-500' },
    { id: 3, title: 'Plant Expert', icon: Leaf, completed: false, color: 'text-green-500' },
    { id: 4, title: 'Super Star', icon: Star, completed: false, color: 'text-gray-400' }
  ]);

  // Calculate achievements based on real user activity
  const calculateAchievements = (userStats: UserStats) => {
    const newAchievements = [
      {
        id: 1,
        title: 'First Sale',
        icon: Trophy,
        completed: userStats.successfulSales >= 1,
        color: userStats.successfulSales >= 1 ? 'text-yellow-500' : 'text-gray-400'
      },
      {
        id: 2,
        title: 'Top Seller',
        icon: Crown,
        completed: userStats.successfulSales >= 10,
        color: userStats.successfulSales >= 10 ? 'text-purple-500' : 'text-gray-400'
      },
      {
        id: 3,
        title: 'Plant Expert',
        icon: Leaf,
        completed: userStats.diagnosisCount >= 5,
        color: userStats.diagnosisCount >= 5 ? 'text-green-500' : 'text-gray-400'
      },
      {
        id: 4,
        title: 'Super Star',
        icon: Star,
        completed: userStats.totalEarnings >= 50000,
        color: userStats.totalEarnings >= 50000 ? 'text-indigo-500' : 'text-gray-400'
      }
    ];
    setAchievements(newAchievements);
    return newAchievements;
  };

  // Load user data from localStorage and calculate statistics
  useEffect(() => {
    const loadUserData = () => {
      setIsLoading(true);
      try {
        const currentUser = localStorage.getItem('kisanmitra_user');
        if (currentUser) {
          const user = JSON.parse(currentUser);
          setUserData(user);

          // Calculate basic statistics from stored data
          const basicStats: UserStats = {
            totalEarnings: user.totalEarnings || 0,
            activeListings: user.activeListings || 0,
            successfulSales: user.successfulSales || 0,
            totalViews: user.totalViews || 0,
            avgRating: user.avgRating || 4.0,
            diagnosisCount: user.diagnosisCount || 0,
            advisorySaved: user.advisorySaved || 0,
            level: user.level || 'New Farmer',
            joinedDate: user.joinedDate || user.createdAt || new Date().toISOString()
          };

          setStats(basicStats);
          calculateAchievements(basicStats);
        } else {
          // No user data found, redirect to login
          navigate('/login');
