import { ArrowLeft, Plus, Eye, Bookmark, Droplets, Beaker, Leaf, Wheat, TreePine, TestTube2, AlertCircle, CheckCircle, Info, X, Save } from "lucide-react";
import { useNavigate } from "react-router-dom";
