import { Pool, neonConfig } from '@neondatabase/serverless';
import { drizzle } from 'drizzle-orm/neon-serverless';
import ws from "ws";
import * as schema from "../shared/schema.js";

neonConfig.webSocketConstructor = ws;

if (!process.env.DATABASE_URL) {
  throw new Error(
    "DATABASE_URL must be set. Did you forget to provision a database?",
  );
}

export const pool = new Pool({ connectionString: process.env.DATABASE_URL });
export const db = drizzle({ client: pool, schema });

server/storage.ts.content:
import { users, listings, diagnoses, advisoryRecords, userActivities } from "../shared/schema";
import type { User, InsertUser, Listing, InsertListing, Diagnosis, InsertDiagnosis, AdvisoryRecord, InsertAdvisoryRecord, UserActivity, InsertUserActivity } from "../shared/schema";
import { db } from "./db";
import { eq, and, desc } from "drizzle-orm";

export interface IStorage {
  // User operations
  getUser(id: number): Promise<User | undefined>;
  getUserByPhone(phone: string): Promise<User | undefined>;
  createUser(insertUser: InsertUser): Promise<User>;
  updateUser(id: number, updates: Partial<InsertUser>): Promise<User>;

  // Listing operations
  getUserListings(userId: number): Promise<Listing[]>;
  createListing(insertListing: InsertListing): Promise<Listing>;
  updateListing(id: number, updates: Partial<InsertListing>): Promise<Listing>;
  markListingAsSold(id: number, soldPrice: number, buyer: string): Promise<Listing>;
  incrementListingViews(id: number): Promise<void>;
  incrementListingInquiries(id: number): Promise<void>;

  // Diagnosis operations
  getUserDiagnoses(userId: number): Promise<Diagnosis[]>;
  createDiagnosis(insertDiagnosis: InsertDiagnosis): Promise<Diagnosis>;

  // Advisory operations
  getUserAdvisories(userId: number): Promise<AdvisoryRecord[]>;
  createAdvisory(insertAdvisory: InsertAdvisoryRecord): Promise<AdvisoryRecord>;

  // Activity tracking
  createUserActivity(insertActivity: InsertUserActivity): Promise<UserActivity>;
  getUserActivities(userId: number): Promise<UserActivity[]>;
}

export class DatabaseStorage implements IStorage {
  async getUser(id: number): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user || undefined;
  }

  async getUserByPhone(phone: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.phone, phone));
    return user || undefined;
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const [user] = await db
      .insert(users)
      .values(insertUser)
      .returning();
    return user;
  }

  async updateUser(id: number, updates: Partial<InsertUser>): Promise<User> {
    const [user] = await db
      .update(users)
      .set({ ...updates, updatedAt: new Date() })
      .where(eq(users.id, id))
      .returning();
    return user;
  }

  async getUserListings(userId: number): Promise<Listing[]> {
    return await db
      .select()
      .from(listings)
      .where(eq(listings.userId, userId))
      .orderBy(desc(listings.createdAt));
  }

  async createListing(insertListing: InsertListing): Promise<Listing> {
    const [listing] = await db
      .insert(listings)
      .values(insertListing)
      .returning();

    // Track activity
    await this.createUserActivity({
      userId: insertListing.userId,
      action: 'listing_created',
      data: { listingId: listing.id, crop: insertListing.crop },
    });

    return listing;
  }

  async updateListing(id: number, updates: Partial<InsertListing>): Promise<Listing> {
    const [listing] = await db
      .update(listings)
      .set({ ...updates, updatedAt: new Date() })
      .where(eq(listings.id, id))
      .returning();
    return listing;
  }

  async markListingAsSold(id: number, soldPrice: number, buyer: string): Promise<Listing> {
    const [listing] = await db
      .update(listings)
      .set({
        status: 'sold',
        soldDate: new Date(),
        soldPrice: soldPrice.toString(),
        buyer,
        updatedAt: new Date(),
      })
      .where(eq(listings.id, id))
      .returning();

    // Track activity
    await this.createUserActivity({
      userId: listing.userId,
      action: 'listing_sold',
      data: { listingId: id, soldPrice, buyer },
    });

    return listing;
  }

  async incrementListingViews(id: number): Promise<void> {
    const [listing] = await db.select().from(listings).where(eq(listings.id, id));
    if (listing) {
      await db
        .update(listings)
        .set({
          views: listing.views + 1,
          updatedAt: new Date(),
        })
        .where(eq(listings.id, id));
    }
  }

  async incrementListingInquiries(id: number): Promise<void> {
    const [listing] = await db.select().from(listings).where(eq(listings.id, id));
    if (listing) {
      await db
        .update(listings)
        .set({
          inquiries: listing.inquiries + 1,
          updatedAt: new Date(),
        })
        .where(eq(listings.id, id));
    }
  }

  async getUserDiagnoses(userId: number): Promise<Diagnosis[]> {
    return await db
      .select()
      .from(diagnoses)
      .where(eq(diagnoses.userId, userId))
      .orderBy(desc(diagnoses.createdAt));
  }

  async createDiagnosis(insertDiagnosis: InsertDiagnosis): Promise<Diagnosis> {
    const [diagnosis] = await db
      .insert(diagnoses)
      .values(insertDiagnosis)
      .returning();

    // Track activity
    await this.createUserActivity({
      userId: insertDiagnosis.userId,
      action: 'diagnosis_created',
      data: { diagnosisId: diagnosis.id, crop: insertDiagnosis.cropName },
    });

    return diagnosis;
  }

  async getUserAdvisories(userId: number): Promise<AdvisoryRecord[]> {
    return await db
      .select()
      .from(advisoryRecords)
      .where(eq(advisoryRecords.userId, userId))
      .orderBy(desc(advisoryRecords.createdAt));
  }

  async createAdvisory(insertAdvisory: InsertAdvisoryRecord): Promise<AdvisoryRecord> {
    const [advisory] = await db
      .insert(advisoryRecords)
      .values(insertAdvisory)
      .returning();

    // Track activity
    await this.createUserActivity({
      userId: insertAdvisory.userId,
      action: 'advisory_saved',
      data: { advisoryId: advisory.id, title: insertAdvisory.title },
    });

    return advisory;
  }

  async createUserActivity(insertActivity: InsertUserActivity): Promise<UserActivity> {
    const [activity] = await db
      .insert(userActivities)
      .values(insertActivity)
      .returning();
    return activity;
  }

  async getUserActivities(userId: number): Promise<UserActivity[]> {
    return await db
      .select()
      .from(userActivities)
      .where(eq(userActivities.userId, userId))
      .orderBy(desc(userActivities.timestamp))
      .limit(100); // Keep only recent activities
  }
}

export const storage = new DatabaseStorage();

shared/schema.ts.content:
// KisanMitra Database Schema
import { pgTable, serial, text, integer, decimal, timestamp, boolean, varchar, jsonb } from 'drizzle-orm/pg-core';
import { relations } from 'drizzle-orm';

// Users table
export const users = pgTable('users', {
  id: serial('id').primaryKey(),
  name: varchar('name', { length: 255 }).notNull(),
  phone: varchar('phone', { length: 20 }).unique().notNull(),
  location: varchar('location', { length: 255 }).notNull(),
  state: varchar('state', { length: 100 }).notNull(),
  avatar: text('avatar'),
  joinedDate: timestamp('joined_date').defaultNow().notNull(),
  createdAt: timestamp('created_at').defaultNow().notNull(),
  updatedAt: timestamp('updated_at').defaultNow().notNull(),
});

// Listings table
export const listings = pgTable('listings', {
  id: serial('id').primaryKey(),
  userId: integer('user_id').references(() => users.id).notNull(),
  crop: varchar('crop', { length: 100 }).notNull(),
  quantity: varchar('quantity', { length: 50 }).notNull(),
  pricePerKg: decimal('price_per_kg', { precision: 10, scale: 2 }).notNull(),
  market: varchar('market', { length: 255 }).notNull(),
  transport: varchar('transport', { length: 10 }).notNull(),
  views: integer('views').default(0).notNull(),
  inquiries: integer('inquiries').default(0).notNull(),
  totalPrice: decimal('total_price', { precision: 12, scale: 2 }).notNull(),
  status: varchar('status', { length: 20 }).default('active').notNull(), // active, sold, expired
  postedDate: varchar('posted_date', { length: 50 }).notNull(),
  soldDate: timestamp('sold_date'),
  soldPrice: decimal('sold_price', { precision: 12, scale: 2 }),
  buyer: varchar('buyer', { length: 255 }),
  createdAt: timestamp('created_at').defaultNow().notNull(),
  updatedAt: timestamp('updated_at').defaultNow().notNull(),
});

// Diagnoses table
export const diagnoses = pgTable('diagnoses', {
  id: serial('id').primaryKey(),
  userId: integer('user_id').references(() => users.id).notNull(),
  cropName: varchar('crop_name', { length: 100 }).notNull(),
  diagnosis: text('diagnosis').notNull(),
  confidence: integer('confidence').notNull(),
  treatment: text('treatment').notNull(),
  date: timestamp('date').notNull(),
  createdAt: timestamp('created_at').defaultNow().notNull(),
});

// Advisory records table
export const advisoryRecords = pgTable('advisory_records', {
  id: serial('id').primaryKey(),
  userId: integer('user_id').references(() => users.id).notNull(),
  title: varchar('title', { length: 255 }).notNull(),
  content: text('content').notNull(),
  category: varchar('category', { length: 100 }).notNull(),
  savedDate: timestamp('saved_date').defaultNow().notNull(),
  createdAt: timestamp('created_at').defaultNow().notNull(),
});

// User activities table
export const userActivities = pgTable('user_activities', {
  id: serial('id').primaryKey(),
  userId: integer('user_id').references(() => users.id).notNull(),
  action: varchar('action', { length: 100 }).notNull(),
  data: jsonb('data'),
  timestamp: timestamp('timestamp').defaultNow().notNull(),
});

// Relations
export const usersRelations = relations(users, ({ many }) => ({
  listings: many(listings),
  diagnoses: many(diagnoses),
  advisoryRecords: many(advisoryRecords),
  activities: many(userActivities),
}));

export const listingsRelations = relations(listings, ({ one }) => ({
  user: one(users, {
    fields: [listings.userId],
    references: [users.id],
  }),
}));

export const diagnosesRelations = relations(diagnoses, ({ one }) => ({
  user: one(users, {
    fields: [diagnoses.userId],
    references: [users.id],
  }),
}));

export const advisoryRecordsRelations = relations(advisoryRecords, ({ one }) => ({
  user: one(users, {
    fields: [advisoryRecords.userId],
    references: [users.id],
  }),
}));

export const userActivitiesRelations = relations(userActivities, ({ one }) => ({
  user: one(users, {
    fields: [userActivities.userId],
    references: [users.id],
  }),
}));

// Export types
export type User = typeof users.$inferSelect;
export type InsertUser = typeof users.$inferInsert;
export type Listing = typeof listings.$inferSelect;
