src/App.tsx.content:
import { Toaster } from "@/components/ui/toaster";
import { Toaster as Sonner } from "@/components/ui/sonner";
import { TooltipProvider } from "@/components/ui/tooltip";
import { QueryClient, QueryClientProvider } from "@tanstack/react-query";
import { NotificationProvider } from "@/hooks/useNotifications";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import { useEffect } from "react";
import { StatusBar, Style } from '@capacitor/status-bar';
import { SplashScreen } from '@capacitor/splash-screen';
import { Keyboard } from '@capacitor/keyboard';
import { App as CapacitorApp } from '@capacitor/app';
import { Capacitor } from '@capacitor/core';
import Landing from "./pages/Landing";
import Splash from "./pages/Splash";
import Onboarding from "./pages/Onboarding";
import Signup from "./pages/Signup";
import Login from "./pages/Login";
import Home from "./pages/Home";
import Diagnose from "./pages/Diagnose";
import CameraCapture from "./pages/CameraCapture";
import AnalyzePlant from "./pages/AnalyzePlant";
import DiagnoseResults from "./pages/DiagnoseResults";
import Shop from "./pages/Shop";
import Orders from "./pages/Orders";
import Profile from "./pages/Profile";
import ProfileEdit from "./pages/ProfileEdit";
import Treatments from "./pages/Treatments";
import Guides from "./pages/Guides";
import NearbyShops from "./pages/NearbyShops";
import SellProduce from "./pages/SellProduce";
import Cart from "./pages/Cart";
import Checkout from "./pages/Checkout";
import OrderSuccess from "./pages/OrderSuccess";
import SoilHealth from "./pages/SoilHealth";
import Weather from "./pages/Weather";
import FertilizerGuide from "./pages/FertilizerGuide";
import Advisory from "./pages/Advisory";
import MarketPrices from "./pages/MarketPrices";
import MarketDetails from "./pages/MarketDetails";
import MyFertilizerGuidance from "./pages/MyFertilizerGuidance";
import CropGuideDetail from "./pages/CropGuideDetail";
import MySavedCultivationGuides from "./pages/MySavedCultivationGuides";
import Mitra from "./pages/Mitra";
import NotFound from "./pages/NotFound";

const queryClient = new QueryClient();

const App = () => {
  useEffect(() => {
    const initializeApp = async () => {
      if (Capacitor.isNativePlatform()) {
        // Configure status bar for Android
        await StatusBar.setStyle({ style: Style.Default });
        await StatusBar.setBackgroundColor({ color: '#22c55e' });

        // Hide splash screen after app loads
        await SplashScreen.hide();

        // Configure keyboard behavior
        Keyboard.setAccessoryBarVisible({ isVisible: false });

        // Handle app state changes
        CapacitorApp.addListener('appStateChange', ({ isActive }) => {
          console.log('App state changed. Is active:', isActive);
        });

        // Handle back button on Android
        CapacitorApp.addListener('backButton', ({ canGoBack }) => {
          if (!canGoBack) {
            CapacitorApp.exitApp();
          } else {
            window.history.back();
          }
        });
      }
    };

    initializeApp();
  }, []);

  return (
  <QueryClientProvider client={queryClient}>
    <TooltipProvider>
      <NotificationProvider>
        <Toaster />
        <Sonner />
        <BrowserRouter>
        <Routes>
          <Route path="/" element={<Landing />} />
          <Route path="/splash" element={<Splash />} />
          <Route path="/onboarding" element={<Onboarding />} />
          <Route path="/signup" element={<Signup />} />
          <Route path="/login" element={<Login />} />
          <Route path="/home" element={<Home />} />
          <Route path="/diagnose" element={<Diagnose />} />
          <Route path="/diagnose/capture" element={<CameraCapture />} />
          <Route path="/diagnose/analyze" element={<AnalyzePlant />} />
          <Route path="/diagnose/results" element={<DiagnoseResults />} />
          <Route path="/shop" element={<Shop />} />
          <Route path="/orders" element={<Orders />} />
          <Route path="/profile" element={<Profile />} />
          <Route path="/profile/edit" element={<ProfileEdit />} />
          <Route path="/treatments" element={<Treatments />} />
          <Route path="/guides" element={<Guides />} />
          <Route path="/guides/:cropId" element={<CropGuideDetail />} />
          <Route path="/my-saved-cultivation-guides" element={<MySavedCultivationGuides />} />
          <Route path="/shops" element={<NearbyShops />} />
          <Route path="/sell" element={<SellProduce />} />
          <Route path="/sell/new" element={<SellProduce />} />
          <Route path="/cart" element={<Cart />} />
          <Route path="/checkout" element={<Checkout />} />
          <Route path="/order-success" element={<OrderSuccess />} />
          <Route path="/soil-health" element={<SoilHealth />} />
          <Route path="/weather" element={<Weather />} />
          <Route path="/fertilizer-guide" element={<FertilizerGuide />} />
          <Route path="/advisory" element={<Advisory />} />
          <Route path="/market-prices" element={<MarketPrices />} />
          <Route path="/market-details/:marketId" element={<MarketDetails />} />
          <Route path="/my-fertilizer-guidance" element={<MyFertilizerGuidance />} />
          <Route path="/mitra" element={<Mitra />} />
          {/* ADD ALL CUSTOM ROUTES ABOVE THE CATCH-ALL "*" ROUTE */}
          <Route path="*" element={<NotFound />} />
        </Routes>
      </BrowserRouter>
      </NotificationProvider>
    </TooltipProvider>
  </QueryClientProvider>
  );
};

export default App;

src/index.css.content:
@import url('https://fonts.googleapis.com/css2?family=Inter:wght@300;400;500;600;700;800;900&display=swap');

@tailwind base;
@tailwind components;
@tailwind utilities;

/* KisanMitra Agricultural App - Android Mobile Optimized Design System */

/* Android Safe Areas & Mobile Viewport */
@supports (padding: max(0px)) {
  .safe-area-top { padding-top: max(20px, env(safe-area-inset-top)); }
  .safe-area-bottom { padding-bottom: max(20px, env(safe-area-inset-bottom)); }
  .safe-area-left { padding-left: max(0px, env(safe-area-inset-left)); }
  .safe-area-right { padding-right: max(0px, env(safe-area-inset-right)); }
}

/* Prevent overscroll bounce for better native feel */
html {
  overscroll-behavior: none;
  -webkit-overflow-scrolling: touch;
}

body {
  overscroll-behavior: none;
  -webkit-user-select: none;
  user-select: none;
}

/* Custom Floating Animation for FAB */
@keyframes floating {
  0% { 
    transform: translate(0, 0px) scale(1); 
    box-shadow: 0 8px 30px rgba(34, 197, 94, 0.4);
  }
  50% { 
    transform: translate(0, -12px) scale(1.05); 
    box-shadow: 0 15px 35px rgba(34, 197, 94, 0.6);
  }
  100% { 
    transform: translate(0, 0px) scale(1); 
    box-shadow: 0 8px 30px rgba(34, 197, 94, 0.4);
  }
}

.floating-fab {
  animation: floating 2.5s ease-in-out infinite;
}

@layer base {
  :root {
    /* Agricultural Color Palette */
    --primary-green: 158 64% 52%; /* #10B981 */
    --secondary-green: 160 84% 39%; /* #059669 */
    --accent-green: 157 69% 38%; /* #047857 */
    --light-green: 155 100% 97%; /* #ECFDF5 */
    --success-green: 142 71% 45%; /* #22C55E */

    /* Functional Colors */
    --warning-orange: 38 92% 50%; /* #F59E0B */
    --danger-red: 0 84% 60%; /* #EF4444 */
    --info-blue: 217 91% 60%; /* #3B82F6 */
    --light-blue: 214 100% 97%; /* #DBEAFE */
    --purple: 262 83% 58%; /* #8B5CF6 */
    --light-purple: 250 100% 97%; /* #EDE9FE */

    /* Neutral Colors */
    --text-primary: 220 14% 96%; /* #F8FAFC */
    --text-secondary: 215 25% 27%; /* #374151 */
    --gray: 215 20% 65%; /* #9CA3AF */
    --light-gray: 210 40% 96%; /* #F1F5F9 */
    --cream: 43 74% 91%; /* #FAF7F0 */
    --light-cream: 45 100% 97%; /* #FEF7ED */

    /* Gradients */
    --gradient-primary: linear-gradient(135deg, hsl(var(--primary-green)), hsl(var(--secondary-green)));
    --gradient-hero: linear-gradient(135deg, hsl(var(--primary-green)), hsl(var(--accent-green)));
    --gradient-splash: linear-gradient(180deg, hsl(var(--primary-green)), hsl(var(--accent-green)));
    --gradient-mesh: linear-gradient(135deg, hsl(var(--primary-green)), hsl(var(--secondary-green)), hsl(var(--success-green)));
    --gradient-glass: linear-gradient(135deg, hsla(var(--primary-green), 0.1), hsla(var(--secondary-green), 0.1));

    /* Shadows */
    --shadow-soft: 0 1px 3px 0 hsla(var(--text-secondary), 0.1), 0 1px 2px 0 hsla(var(--text-secondary), 0.06);
    --shadow-medium: 0 4px 6px -1px hsla(var(--text-secondary), 0.1), 0 2px 4px -1px hsla(var(--text-secondary), 0.06);
    --shadow-large: 0 10px 15px -3px hsla(var(--text-secondary), 0.1), 0 4px 6px -2px hsla(var(--text-secondary), 0.05);
    --shadow-glow: 0 0 20px hsla(var(--primary-green), 0.3);
    --shadow-inner: inset 0 2px 4px 0 hsla(var(--text-secondary), 0.06);

    /* Animations - Android Material Motion */
    --transition-fast: all 0.15s cubic-bezier(0.4, 0.0, 0.2, 1);
    --transition-smooth: all 0.3s cubic-bezier(0.4, 0.0, 0.2, 1);
    --transition-spring: all 0.4s cubic-bezier(0.175, 0.885, 0.32, 1.275);
    --transition-bounce: all 0.6s cubic-bezier(0.68, -0.55, 0.265, 1.55);

    /* Spacing System */
    --space-xs: 0.25rem; /* 4px */
    --space-sm: 0.5rem;  /* 8px */
    --space-md: 1rem;    /* 16px */
    --space-lg: 1.5rem;  /* 24px */
    --space-xl: 2rem;    /* 32px */
    --space-2xl: 3rem;   /* 48px */

    /* Border Radius */
    --radius-sm: 0.375rem; /* 6px */
    --radius-md: 0.75rem;  /* 12px */
    --radius-lg: 1rem;     /* 16px */
    --radius-xl: 1.25rem;  /* 20px */
    --radius-full: 9999px;

    /* Standard design tokens */
    --background: var(--text-primary);
    --foreground: var(--text-secondary);
    --card: var(--text-primary);
    --card-foreground: var(--text-secondary);
    --popover: var(--text-primary);
    --popover-foreground: var(--text-secondary);
    --primary: var(--primary-green);
    --primary-foreground: var(--text-primary);
    --secondary: var(--light-gray);
    --secondary-foreground: var(--text-secondary);
    --muted: var(--light-gray);
    --muted-foreground: var(--gray);
    --accent: var(--light-green);
    --accent-foreground: var(--text-secondary);
    --destructive: var(--danger-red);
    --destructive-foreground: var(--text-primary);
    --border: 214.3 31.8% 91.4%;
    --input: 214.3 31.8% 91.4%;
    --ring: var(--primary-green);
    --radius: var(--radius-md);
  }

  .dark {
    --background: 222.2 84% 4.9%;
    --foreground: 210 40% 98%;

    --card: 222.2 84% 4.9%;
    --card-foreground: 210 40% 98%;

    --popover: 222.2 84% 4.9%;
    --popover-foreground: 210 40% 98%;

    --primary: 210 40% 98%;
    --primary-foreground: 222.2 47.4% 11.2%;

    --secondary: 217.2 32.6% 17.5%;
    --secondary-foreground: 210 40% 98%;

    --muted: 217.2 32.6% 17.5%;
    --muted-foreground: 215 20.2% 65.1%;

    --accent: 217.2 32.6% 17.5%;
    --accent-foreground: 210 40% 98%;

    --destructive: 0 62.8% 30.6%;
    --destructive-foreground: 210 40% 98%;

    --border: 217.2 32.6% 17.5%;
    --input: 217.2 32.6% 17.5%;
    --ring: 212.7 26.8% 83.9%;
    --sidebar-background: 240 5.9% 10%;
    --sidebar-foreground: 240 4.8% 95.9%;
    --sidebar-primary: 224.3 76.3% 48%;
    --sidebar-primary-foreground: 0 0% 100%;
    --sidebar-accent: 240 3.7% 15.9%;
    --sidebar-accent-foreground: 240 4.8% 95.9%;
    --sidebar-border: 240 3.7% 15.9%;
    --sidebar-ring: 217.2 91.2% 59.8%;
  }
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply bg-background text-foreground font-inter;
    font-family: 'Inter', -apple-system, BlinkMacSystemFont, system-ui, sans-serif;
  }
}

@layer components {
  /* Agricultural App Components */
  .glass-card {
    @apply backdrop-blur-lg bg-white/80 border border-white/20 shadow-lg;
  }

  .gradient-primary {
    background: var(--gradient-primary);
  }

  .gradient-hero {
    background: var(--gradient-hero);
  }

  .gradient-splash {
    background: var(--gradient-splash);
  }

  .gradient-mesh {
    background: var(--gradient-mesh);
  }

  .gradient-glass {
    background: var(--gradient-glass);
  }

  .shadow-glow {
    box-shadow: var(--shadow-glow);
  }

  .shadow-soft {
    box-shadow: var(--shadow-soft);
  }

  .shadow-medium {
    box-shadow: var(--shadow-medium);
  }

  .shadow-large {
    box-shadow: var(--shadow-large);
  }

  .transition-smooth {
    transition: var(--transition-smooth);
  }

  .transition-spring {
    transition: var(--transition-spring);
  }

  /* Button Variants */
  .btn-primary {
    @apply bg-primary hover:bg-primary/90 text-primary-foreground font-semibold py-4 px-6 rounded-xl transition-all duration-300 transform hover:scale-105 active:scale-95 shadow-md hover:shadow-lg;
  }

  .btn-secondary {
    @apply bg-white border-2 border-primary text-primary hover:bg-primary hover:text-white font-semibold py-4 px-6 rounded-xl transition-all duration-300 transform hover:scale-105 active:scale-95;
  }

  .btn-glass {
    @apply glass-card text-primary hover:bg-white/90 font-semibold py-4 px-6 rounded-xl transition-all duration-300 transform hover:scale-105 active:scale-95;
  }

  /* Agricultural Theme Components */
  .plant-card {
    @apply bg-white rounded-2xl p-6 shadow-medium hover:shadow-large transition-all duration-300 border border-gray-100 hover:border-primary/20;
  }

  .weather-card {
    @apply bg-gradient-to-br from-blue-50 to-blue-100 rounded-2xl p-6 shadow-medium border border-blue-200;
  }

  .tip-card {
    @apply bg-gradient-to-r from-yellow-50 to-orange-50 rounded-2xl p-6 shadow-medium border-l-4 border-yellow-400;
  }

  .diagnosis-card {
    @apply bg-white rounded-2xl p-6 shadow-large border border-gray-100;
  }

  /* Floating Animation */
  .float-animation {
    animation: float 3s ease-in-out infinite;
  }

  .pulse-glow {
    animation: pulse-glow 2s ease-in-out infinite;
  }

  .grow-animation {
    animation: grow 2s ease-out forwards;
  }

  /* Android Mobile Optimizations */
  .mobile-container {
    @apply w-full max-w-full min-h-screen bg-background relative overflow-hidden;
    -webkit-tap-highlight-color: transparent;
    -webkit-overflow-scrolling: touch;
    overscroll-behavior: none;
  }

  .mobile-header {
    @apply flex items-center justify-between p-4 bg-white/95 backdrop-blur-md shadow-soft sticky top-0 z-50;
    padding-top: max(16px, env(safe-area-inset-top));
  }

  .mobile-content {
    @apply px-4 pb-24;
    -webkit-overflow-scrolling: touch;
    padding-bottom: max(96px, calc(env(safe-area-inset-bottom) + 96px));
  }

  .mobile-bottom-nav {
    @apply fixed bottom-0 left-0 right-0 w-full bg-white/95 backdrop-blur-md border-t border-gray-200 shadow-large z-50;
    padding-bottom: max(0px, env(safe-area-inset-bottom));
  }

  /* Android Button Animations */
  .android-ripple {
    @apply relative overflow-hidden;
    -webkit-tap-highlight-color: transparent;
    transition: all 0.2s cubic-bezier(0.4, 0.0, 0.2, 1);
  }

  .android-ripple:active {
    transform: scale(0.96);
  }

  /* Android Card Style */
  .android-card {
    @apply bg-white rounded-2xl shadow-sm border border-gray-100;
    transition: all 0.3s cubic-bezier(0.4, 0.0, 0.2, 1);
  }

  .android-card:active {
    transform: scale(0.98);
    box-shadow: 0 2px 8px rgba(0, 0, 0, 0.12);
  }

  /* Touch Targets */
  .touch-friendly {
    min-height: 48px;
    min-width: 48px;
    @apply flex items-center justify-center;
  }

  /* Agriculture Color Utilities */
  .text-agri-primary { color: hsl(var(--primary-green)); }
  .text-agri-secondary { color: hsl(var(--secondary-green)); }
  .text-agri-accent { color: hsl(var(--accent-green)); }
  .text-agri-success { color: hsl(var(--success-green)); }
  .text-agri-warning { color: hsl(var(--warning-orange)); }
  .text-agri-danger { color: hsl(var(--danger-red)); }
  .text-agri-info { color: hsl(var(--info-blue)); }
  .text-agri-purple { color: hsl(var(--purple)); }

  .bg-agri-primary { background-color: hsl(var(--primary-green)); }
  .bg-agri-secondary { background-color: hsl(var(--secondary-green)); }
  .bg-agri-accent { background-color: hsl(var(--accent-green)); }
  .bg-agri-light { background-color: hsl(var(--light-green)); }
  .bg-agri-success { background-color: hsl(var(--success-green)); }
  .bg-agri-warning { background-color: hsl(var(--warning-orange)); }
  .bg-agri-danger { background-color: hsl(var(--danger-red)); }
  .bg-agri-info { background-color: hsl(var(--info-blue)); }
  .bg-agri-light-blue { background-color: hsl(var(--light-blue)); }
  .bg-agri-purple { background-color: hsl(var(--purple)); }
  .bg-agri-light-purple { background-color: hsl(var(--light-purple)); }
  .bg-agri-cream { background-color: hsl(var(--cream)); }
  .bg-agri-light-cream { background-color: hsl(var(--light-cream)); }
}

@layer utilities {
  /* Keyframe Animations */
  @keyframes float {
    0%, 100% {
      transform: translateY(0px);
    }
    50% {
      transform: translateY(-10px);
    }
  }

  @keyframes pulse-glow {
    0%, 100% {
      box-shadow: 0 0 5px hsla(var(--primary-green), 0.5);
    }
    50% {
      box-shadow: 0 0 20px hsla(var(--primary-green), 0.8);
    }
  }

  @keyframes grow {
    0% {
      transform: scale(0) rotate(-180deg);
      opacity: 0;
    }
    100% {
      transform: scale(1) rotate(0deg);
      opacity: 1;
    }
  }

  @keyframes slideUp {
    0% {
      transform: translateY(100%);
      opacity: 0;
    }
    100% {
      transform: translateY(0);
      opacity: 1;
    }
  }

  @keyframes fadeIn {
    0% {
      opacity: 0;
      transform: translateY(20px);
    }
    100% {
      opacity: 1;
      transform: translateY(0);
    }
  }

  @keyframes typing {
    0% { width: 0; }
    100% { width: 100%; }
  }

  @keyframes shimmer {
    0% {
      background-position: -200% 0;
    }
    100% {
      background-position: 200% 0;
    }
  }

  .animate-slide-up {
    animation: slideUp 0.5s ease-out;
  }

  .animate-fade-in {
    animation: fadeIn 0.6s ease-out;
  }

  .animate-typing {
    animation: typing 2s steps(20, end);
  }

  .animate-shimmer {
    background: linear-gradient(90deg, transparent, rgba(255,255,255,0.4), transparent);
    background-size: 200% 100%;
    animation: shimmer 1.5s infinite;
  }
}

src/components/AuthButton.tsx.content:
import React, { ReactNode } from 'react';
import { Button } from '@/components/ui/button';
import { Loader2 } from 'lucide-react';

interface AuthButtonProps {
  children: ReactNode;
  loading?: boolean;
  loadingText?: string;
  disabled?: boolean;
  onClick?: () => void;
  type?: 'button' | 'submit';
  variant?: 'primary' | 'secondary' | 'outline';
  size?: 'sm' | 'md' | 'lg';
  className?: string;
  icon?: ReactNode;
}

const AuthButton: React.FC<AuthButtonProps> = ({
  children,
  loading = false,
  loadingText = 'Loading...',
  disabled = false,
  onClick,
  type = 'button',
  variant = 'primary',
  size = 'lg',
  className = '',
  icon
}) => {
  const getVariantClasses = () => {
    switch (variant) {
      case 'primary':
        return 'bg-agri-primary hover:bg-agri-secondary text-white shadow-large';
      case 'secondary':
        return 'bg-agri-secondary hover:bg-agri-primary text-white shadow-large';
      case 'outline':
        return 'border-2 border-agri-primary text-agri-primary hover:bg-agri-primary hover:text-white shadow-md';
      default:
        return 'bg-agri-primary hover:bg-agri-secondary text-white shadow-large';
    }
  };

  const getSizeClasses = () => {
    switch (size) {
      case 'sm':
        return 'h-12 text-base px-6';
      case 'md':
        return 'h-16 text-lg px-8';
      case 'lg':
        return 'h-20 text-2xl px-8';
      default:
        return 'h-20 text-2xl px-8';
    }
  };

  const isDisabled = disabled || loading;

  return (
    <Button
      type={type}
      onClick={onClick}
      disabled={isDisabled}
      className={`
        w-full font-bold rounded-2xl transition-all duration-200
        disabled:opacity-50 disabled:cursor-not-allowed
        active:scale-95 transform
        ${getVariantClasses()}
        ${getSizeClasses()}
        ${className}
      `}
    >
      {loading ? (
        <div className="flex items-center justify-center space-x-3">
          <Loader2 className="w-6 h-6 animate-spin" />
          <span>{loadingText}</span>
        </div>
      ) : (
        <div className="flex items-center justify-center space-x-3">
          {icon && <span className="w-8 h-8 flex items-center justify-center">{icon}</span>}
          <span>{children}</span>
        </div>
      )}
    </Button>
  );
};

export default AuthButton;

src/components/CustomInput.tsx.content:
import React, { ReactNode } from 'react';
import { Input } from '@/components/ui/input';
import { AlertCircle, CheckCircle } from 'lucide-react';

interface CustomInputProps {
  label: string;
  icon: ReactNode;
  value: string;
  onChange: (value: string) => void;
  placeholder: string;
  type?: 'text' | 'tel' | 'email';
  validation?: {
    isValid: boolean;
    message: string;
  };
  prefix?: string;
  maxLength?: number;
  className?: string;
  inputClassName?: string;
  disabled?: boolean;
  required?: boolean;
}

const CustomInput: React.FC<CustomInputProps> = ({
  label,
  icon,
  value,
  onChange,
  placeholder,
  type = 'text',
  validation,
  prefix,
  maxLength,
  className = '',
  inputClassName = '',
  disabled = false,
  required = false
}) => {
  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    let newValue = e.target.value;

    // Apply specific formatting based on type
    if (type === 'tel') {
      newValue = newValue.replace(/\D/g, '');
    } else if (type === 'text' && label.toLowerCase().includes('name')) {
      // Only allow alphabets and spaces for name fields
      newValue = newValue.replace(/[^A-Za-z\s]/g, '');
    }

    onChange(newValue);
  };

  const getValidationStyle = () => {
    if (!validation || value.length === 0) {
      return 'border-agri-primary/30 focus:border-agri-primary';
    }

    return validation.isValid 
      ? 'border-green-400 focus:border-green-500' 
      : 'border-red-400 focus:border-red-500';
  };

  const showValidationIcon = validation && value.length > 0;

  return (
    <div className={`space-y-2 ${className}`}>
      {/* Label */}
      <label className="text-lg font-bold text-agri-primary flex items-center">
        {icon}
        <span className="ml-3">{label}</span>
        {required && <span className="text-red-500 ml-1">*</span>}
      </label>

      {/* Input Container */}
      <div className="relative">
        {/* Prefix */}
        {prefix && (
          <div className="absolute left-4 top-1/2 transform -translate-y-1/2 bg-agri-light rounded-lg px-3 py-2">
            <span className="text-agri-primary font-bold text-lg">{prefix}</span>
          </div>
        )}

        {/* Input Field */}
        <Input
          type={type}
          placeholder={placeholder}
          value={value}
          onChange={handleChange}
          maxLength={maxLength}
          disabled={disabled}
          className={`
            ${prefix ? 'pl-24' : 'pl-4'} 
            ${showValidationIcon ? 'pr-16' : 'pr-4'}
            h-16 text-xl rounded-2xl border-2 
            ${getValidationStyle()}
            bg-agri-light/30 font-semibold
            disabled:opacity-50 disabled:cursor-not-allowed
            ${type === 'tel' ? 'text-center tracking-wider font-bold' : ''}
            ${inputClassName}
          `}
        />

        {/* Validation Icon */}
        {showValidationIcon && (
          <div className="absolute right-4 top-1/2 transform -translate-y-1/2">
            {validation.isValid ? (
              <CheckCircle className="w-6 h-6 text-green-600" />
            ) : (
              <AlertCircle className="w-6 h-6 text-red-600" />
            )}
          </div>
        )}
      </div>

      {/* Validation Message */}
      {validation && validation.message && value.length > 0 && (
        <div className={`text-sm px-3 py-2 rounded-lg ${
          validation.isValid 
            ? 'text-green-700 bg-green-50' 
            : 'text-red-700 bg-red-50'
        }`}>
          {validation.message}
        </div>
      )}

      {/* Character count for limited inputs */}
      {maxLength && value.length > 0 && (
        <div className="text-sm text-agri-gray text-right">
          {value.length}/{maxLength}
        </div>
      )}
    </div>
  );
};

export default CustomInput;

src/components/LanguageSwitcher.tsx.content:
import { useState } from 'react';

const languages = [
  { code: 'en', name: 'English', flag: '', short: 'EN' },
  { code: 'hi', name: '', flag: '', short: '' },
];

const LanguageSwitcher = () => {
  const [selectedLanguage, setSelectedLanguage] = useState('en');

  return (
    <div className="flex items-center bg-white/90 backdrop-blur-sm rounded-full p-1 shadow-sm border border-green-100">
      {languages.map((lang) => {
        const isSelected = selectedLanguage === lang.code;
        return (
          <button
            key={lang.code}
            onClick={() => setSelectedLanguage(lang.code)}
            className={`
              px-3 py-1.5 rounded-full text-sm font-medium transition-all duration-200 ease-in-out flex items-center space-x-1
              ${
                isSelected
                  ? 'bg-green-500 text-white shadow-sm transform scale-105'
                  : 'text-green-600 hover:bg-green-50'
              }
            `}
          >
            <span className="text-xs">{lang.flag}</span>
            <span className="text-xs font-semibold">{lang.short}</span>
          </button>
        );
      })}
    </div>
  );
};

export default LanguageSwitcher;

src/components/LocationPermissionModal.t.content:
import React from 'react';
import { MapPin, Shield, Navigation, AlertCircle } from 'lucide-react';
import { Button } from '@/components/ui/button';
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
} from '@/components/ui/dialog';

interface LocationPermissionModalProps {
  isOpen: boolean;
  onRequestPermission: () => void;
  onSkip: () => void;
  permissionStatus: 'granted' | 'denied' | 'prompt' | 'unsupported';
}

const LocationPermissionModal: React.FC<LocationPermissionModalProps> = ({
  isOpen,
  onRequestPermission,
  onSkip,
  permissionStatus
}) => {
  const getModalContent = () => {
    switch (permissionStatus) {
      case 'denied':
        return {
          icon: <AlertCircle className="w-16 h-16 text-amber-500 mx-auto mb-4" />,
          title: "Location Access Blocked",
          description: "Location access has been denied. To auto-fill your location, please:",
          instructions: [
            "Click the location icon in your browser's address bar",
            "Select 'Allow' for location access",
            "Refresh this page and try again"
          ],
          buttonText: "Manual Entry Instead",
          buttonAction: onSkip
        };

      case 'unsupported':
        return {
          icon: <MapPin className="w-16 h-16 text-gray-500 mx-auto mb-4" />,
          title: "Location Not Supported",
          description: "Your device doesn't support automatic location detection.",
          instructions: [
            "You can manually select your state and district",
            "This won't affect your farming experience"
          ],
          buttonText: "Continue Manually",
          buttonAction: onSkip
        };

      default:
        return {
          icon: <Navigation className="w-16 h-16 text-agri-primary mx-auto mb-4" />,
          title: "Help Us Serve You Better",
          description: "KisanMitra would like to access your location to:",
          instructions: [
            " Auto-fill your state and district",
            " Provide local weather information",
            " Show nearby farming services",
            " Display regional market prices"
          ],
          buttonText: "Allow Location Access",
          buttonAction: onRequestPermission
        };
    }
  };

  const { icon, title, description, instructions, buttonText, buttonAction } = getModalContent();

  return (
    <Dialog open={isOpen}>
      <DialogContent className="max-w-sm mx-auto bg-white rounded-3xl p-6 border-2 border-agri-primary/20">
        <DialogHeader className="text-center">
          {icon}
          <DialogTitle className="text-2xl font-bold text-agri-primary mb-2">
            {title}
          </DialogTitle>
          <DialogDescription className="text-agri-gray text-base mb-4">
            {description}
          </DialogDescription>
        </DialogHeader>

        <div className="space-y-4">
          <div className="bg-agri-light/30 rounded-2xl p-4">
            <ul className="space-y-2">
              {instructions.map((instruction, index) => (
                <li key={index} className="flex items-start text-agri-gray text-sm">
                  <span className="inline-block w-2 h-2 rounded-full bg-agri-primary mr-3 mt-2 flex-shrink-0" />
                  <span>{instruction}</span>
                </li>
              ))}
            </ul>
          </div>

          <div className="flex flex-col space-y-3">
            <Button
              onClick={buttonAction}
              className="w-full h-14 bg-agri-primary hover:bg-agri-secondary text-white font-bold text-lg rounded-2xl"
            >
              {buttonText}
            </Button>

            {permissionStatus === 'prompt' && (
              <Button
                onClick={onSkip}
                variant="outline"
                className="w-full h-12 border-2 border-agri-primary/30 text-agri-primary font-semibold rounded-2xl hover:bg-agri-light/50"
              >
                Enter Manually
              </Button>
            )}
          </div>

          {permissionStatus === 'prompt' && (
            <div className="flex items-center justify-center text-center pt-2">
              <Shield className="w-4 h-4 text-agri-primary mr-2" />
              <p className="text-xs text-agri-gray">
                Your location data is secure and stays on your device
              </p>
            </div>
          )}
        </div>
      </DialogContent>
    </Dialog>
  );
};

export default LocationPermissionModal;

src/components/LocationPicker.tsx.content:
import React, { useState, useEffect } from 'react';
import { MapPin, Navigation, RefreshCw, AlertCircle, CheckCircle } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import useLocation from '@/hooks/useLocation';
import LocationPermissionModal from './LocationPermissionModal';

interface LocationPickerProps {
  state: string;
  district: string;
  onStateChange: (value: string) => void;
  onDistrictChange: (value: string) => void;
  className?: string;
}

const indianStates = [
  "Andhra Pradesh", "Arunachal Pradesh", "Assam", "Bihar", "Chhattisgarh", 
  "Goa", "Gujarat", "Haryana", "Himachal Pradesh", "Jharkhand", "Karnataka", 
  "Kerala", "Madhya Pradesh", "Maharashtra", "Manipur", "Meghalaya", "Mizoram", 
  "Nagaland", "Odisha", "Punjab", "Rajasthan", "Sikkim", "Tamil Nadu", 
  "Telangana", "Tripura", "Uttar Pradesh", "Uttarakhand", "West Bengal",
  "Delhi", "Puducherry", "Chandigarh", "Dadra and Nagar Haveli", "Daman and Diu",
  "Lakshadweep", "Ladakh", "Jammu and Kashmir"
];

const LocationPicker: React.FC<LocationPickerProps> = ({
  state,
  district,
  onStateChange,
  onDistrictChange,
  className = ""
}) => {
  const {
    loading,
    data,
    error,
    permissionStatus,
    getCurrentLocation,
    retryLocation,
    getAccuracyLevel,
    hasValidLocation,
    stateDistrictMapping
  } = useLocation();

  const [showPermissionModal, setShowPermissionModal] = useState(false);
  const [useManualEntry, setUseManualEntry] = useState(false);
  const [autoLocationAttempted, setAutoLocationAttempted] = useState(false);
  const [isSignupFlow, setIsSignupFlow] = useState(false);

  // Auto-detect location on component mount (for signup flow)
  useEffect(() => {
    // Check if this is likely a signup flow (no existing state/district values)
    const isLikelySignup = !state && !district;
    setIsSignupFlow(isLikelySignup);

    // Auto-attempt location detection for signup flow if not already attempted
    if (isLikelySignup && !autoLocationAttempted && permissionStatus !== 'denied' && !useManualEntry) {
      console.log('Auto-attempting location detection for signup...');
      setAutoLocationAttempted(true);
      getCurrentLocation().catch(() => {
        console.log('Auto location detection failed, user can try manually');
      });
    }
  }, []);

  // Auto-fill when location data is available (fixed to prevent infinite loop)
  useEffect(() => {
    if (data?.state && data?.district && !useManualEntry) {
      // Only update if values are different to prevent infinite loops
      if (state !== data.state) {
        onStateChange(data.state);
      }
      if (district !== data.district) {
        onDistrictChange(data.district);
      }
    }
    // Exclude handler functions from dependencies to prevent infinite loops
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [data?.state, data?.district, useManualEntry, state, district]);

  const handleLocationRequest = async () => {
    setShowPermissionModal(false);
    setAutoLocationAttempted(true);

    try {
      await getCurrentLocation();
    } catch (error) {
      console.error('Location request failed:', error);
    }
  };

  const handleSkipLocation = () => {
    setShowPermissionModal(false);
    setUseManualEntry(true);
  };

  const handleUseCurrentLocation = () => {
    if (permissionStatus === 'prompt' || permissionStatus === 'denied') {
      setShowPermissionModal(true);
    } else {
      getCurrentLocation();
    }
  };

  const getAccuracyIndicator = () => {
    if (!data) return null;

    // Simple accuracy determination based on available data
    const accuracy = data.accuracy || (data.state && data.district ? 'high' : 'medium');
    const colors = {
      high: 'text-green-600',
      medium: 'text-yellow-600',
      low: 'text-orange-600'
    };

    return (
      <div className="flex items-center space-x-2 text-sm">
        <div className={`w-2 h-2 rounded-full ${
          accuracy === 'high' ? 'bg-green-500' : 
          accuracy === 'medium' ? 'bg-yellow-500' : 'bg-orange-500'
        }`} />
        <span className={`text-xs ${colors[accuracy] || colors['low']}`}>
          {accuracy === 'high' ? 'High accuracy' : 
           accuracy === 'medium' ? 'Medium accuracy' : 'Low accuracy'}
        </span>
      </div>
    );
  };

  return (
    <div className={`space-y-4 ${className}`}>
      {/* Location Detection Section */}
      {!useManualEntry && (
        <div className="bg-agri-light/30 rounded-2xl p-4 border border-agri-primary/20">
          <div className="flex items-center justify-between mb-3">
            <div className="flex items-center space-x-2">
              <Navigation className="w-5 h-5 text-agri-primary" />
              <span className="font-semibold text-agri-primary">Auto Location</span>
            </div>

            {hasValidLocation && (
              <CheckCircle className="w-5 h-5 text-green-600" />
            )}
          </div>

          {loading && (
            <div className="flex items-center space-x-3 py-2">
              <RefreshCw className="w-4 h-4 text-agri-primary animate-spin" />
              <span className="text-sm text-agri-gray">Detecting your location...</span>
            </div>
          )}

          {error && (
            <div className="flex items-start space-x-2 py-2 text-sm text-red-600">
              <AlertCircle className="w-4 h-4 mt-0.5 flex-shrink-0" />
              <span>{error}</span>
            </div>
          )}

          {hasValidLocation && data && (
            <div className="space-y-2">
              <div className="flex items-center justify-between">
                <span className="text-sm text-agri-gray">Detected Location:</span>
                {getAccuracyIndicator()}
              </div>
              <div className="text-sm font-medium text-agri-primary">
                 {data.district}, {data.state}
              </div>
            </div>
          )}

          <div className="flex space-x-2 mt-3">
            <Button
              type="button"
              onClick={handleUseCurrentLocation}
              disabled={loading}
              variant="outline"
              size="sm"
              className="flex-1 h-10 border-agri-primary/50 text-agri-primary hover:bg-agri-primary hover:text-white rounded-xl"
            >
              <Navigation className="w-4 h-4 mr-2" />
              {loading ? 'Locating...' : 'Use Current Location'}
            </Button>

            {hasValidLocation && (
              <Button
                type="button"
                onClick={retryLocation}
                variant="outline"
                size="sm"
                className="h-10 border-agri-primary/50 text-agri-primary hover:bg-agri-primary hover:text-white rounded-xl px-3"
              >
                <RefreshCw className="w-4 h-4" />
              </Button>
            )}

            <Button
              type="button"
              onClick={() => setUseManualEntry(true)}
              variant="outline"
              size="sm"
              className="h-10 border-agri-primary/50 text-agri-primary hover:bg-agri-primary hover:text-white rounded-xl px-3"
            >
              Manual
            </Button>
          </div>
        </div>
      )}

      {/* Manual Entry Section */}
      {(useManualEntry || (!hasValidLocation && autoLocationAttempted)) && (
        <>
          {/* State Field */}
          <div className="space-y-2">
            <label className="text-lg font-bold text-agri-primary flex items-center">
              <MapPin className="w-6 h-6 mr-3" />
              State
              {!useManualEntry && <span className="text-sm font-normal text-agri-gray ml-2">(Manual Entry)</span>}
            </label>
            <Select onValueChange={onStateChange} value={state}>
              <SelectTrigger className="h-16 text-xl rounded-2xl border-2 border-agri-primary/30 focus:border-agri-primary bg-agri-light/30 font-semibold">
                <SelectValue placeholder="Select your state" />
              </SelectTrigger>
              <SelectContent className="bg-white border border-agri-primary/20 shadow-large rounded-lg max-h-48">
                {indianStates.map((stateName) => (
                  <SelectItem 
                    key={stateName} 
                    value={stateName}
                    className="text-lg py-3 px-4 hover:bg-agri-light"
                  >
                    {stateName}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>

          {/* District Field */}
          <div className="space-y-2">
            <label className="text-lg font-bold text-agri-primary flex items-center">
              <MapPin className="w-6 h-6 mr-3" />
              District
            </label>
            <Input
              type="text"
              placeholder="Enter your district name"
              value={district}
              onChange={(e) => onDistrictChange(e.target.value)}
              className="h-16 text-xl rounded-2xl border-2 border-agri-primary/30 focus:border-agri-primary bg-agri-light/30 font-semibold"
            />

            {/* District suggestions if state is selected */}
            {state && stateDistrictMapping[state] && (
              <div className="mt-2">
                <p className="text-sm text-agri-gray mb-2">Popular districts in {state}:</p>
                <div className="flex flex-wrap gap-2">
                  {stateDistrictMapping[state].slice(0, 4).map((dist) => (
                    <button
                      key={dist}
                      type="button"
                      onClick={() => onDistrictChange(dist)}
                      className="px-3 py-1 text-sm bg-agri-light text-agri-primary rounded-lg hover:bg-agri-primary hover:text-white transition-colors"
                    >
                      {dist}
                    </button>
                  ))}
                </div>
              </div>
            )}
          </div>

          {useManualEntry && (
            <Button
              type="button"
              onClick={() => {
                setUseManualEntry(false);
                setAutoLocationAttempted(false);
                handleUseCurrentLocation();
              }}
              variant="outline"
              size="sm"
              className="w-full h-10 border-agri-primary/50 text-agri-primary hover:bg-agri-primary hover:text-white rounded-xl"
            >
              <Navigation className="w-4 h-4 mr-2" />
              Try Auto Location Again
            </Button>
          )}
        </>
      )}

      <LocationPermissionModal
        isOpen={showPermissionModal}
        onRequestPermission={handleLocationRequest}
        onSkip={handleSkipLocation}
        permissionStatus={permissionStatus}
      />
    </div>
  );
};

export default LocationPicker;

