import React, { useState } from 'react';
import { Eye, EyeOff, Lock } from 'lucide-react';
import { Input } from '@/components/ui/input';

interface PINInputProps {
  value: string;
  onChange: (value: string) => void;
  placeholder?: string;
  label?: string;
  showStrengthIndicator?: boolean;
  className?: string;
}

const PINInput: React.FC<PINInputProps> = ({
  value,
  onChange,
  placeholder = "Enter PIN",
  label = "4-Digit PIN",
  showStrengthIndicator = false,
  className = ""
}) => {
  const [showPin, setShowPin] = useState(false);

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const newValue = e.target.value.replace(/\D/g, '').slice(0, 4);
    onChange(newValue);
  };

  const getPinStrength = (): 'weak' | 'medium' | 'strong' => {
    if (value.length < 4) return 'weak';

    const hasRepeating = /(.)\1{2,}/.test(value);
    const isSequential = /^(0123|1234|2345|3456|4567|5678|6789|9876|8765|7654|6543|5432|4321|3210)/.test(value);
    const isCommon = ['1111', '2222', '3333', '4444', '5555', '6666', '7777', '8888', '9999', '0000', '1234', '4321'].includes(value);

    if (hasRepeating || isSequential || isCommon) return 'weak';

    const uniqueDigits = new Set(value.split('')).size;
    return uniqueDigits >= 3 ? 'strong' : 'medium';
  };

  const getStrengthColor = () => {
    const strength = getPinStrength();
    switch (strength) {
      case 'weak': return 'text-red-500 bg-red-100';
      case 'medium': return 'text-yellow-600 bg-yellow-100';
      case 'strong': return 'text-green-600 bg-green-100';
    }
  };

  const getStrengthText = () => {
    const strength = getPinStrength();
    switch (strength) {
      case 'weak': return 'Weak PIN';
      case 'medium': return 'Medium PIN';
      case 'strong': return 'Strong PIN';
    }
  };

  return (
    <div className={`space-y-3 ${className}`}>
      {/* Label */}
      <label className="text-lg font-bold text-agri-primary flex items-center">
        <Lock className="w-6 h-6 mr-3" />
        {label}
      </label>

      {/* PIN Input */}
      <div className="relative">
        <Input
          type={showPin ? "text" : "password"}
          placeholder={placeholder}
          value={value}
          onChange={handleChange}
          maxLength={4}
          className="pr-16 h-16 text-2xl rounded-2xl border-2 border-agri-primary/30 focus:border-agri-primary bg-agri-light/30 font-bold text-center tracking-[0.8em] placeholder:tracking-normal placeholder:text-base"
        />

        {/* Show/Hide Button */}
        <button
          type="button"
          onClick={() => setShowPin(!showPin)}
          className="absolute right-4 top-1/2 transform -translate-y-1/2 p-2 rounded-xl hover:bg-agri-light transition-colors"
        >
          {showPin ? (
            <EyeOff className="w-6 h-6 text-agri-primary" />
          ) : (
            <Eye className="w-6 h-6 text-agri-primary" />
          )}
        </button>
      </div>

      {/* PIN Dots Indicator */}
      <div className="flex justify-center space-x-4 mt-4">
        {[...Array(4)].map((_, i) => (
          <div
            key={i}
            className={`w-4 h-4 rounded-full transition-all duration-300 ${
              i < value.length 
                ? 'bg-agri-primary scale-125 shadow-md' 
                : 'bg-agri-light'
            }`}
          />
        ))}
      </div>

      {/* PIN Strength Indicator */}
      {showStrengthIndicator && value.length > 0 && (
        <div className="mt-3">
          <div className={`inline-flex items-center px-3 py-2 rounded-xl text-sm font-medium ${getStrengthColor()}`}>
            <div className={`w-2 h-2 rounded-full mr-2 ${
              getPinStrength() === 'weak' ? 'bg-red-500' :
              getPinStrength() === 'medium' ? 'bg-yellow-500' : 'bg-green-500'
            }`} />
            {getStrengthText()}
          </div>

          {/* Strength Tips */}
          {value.length === 4 && getPinStrength() === 'weak' && (
            <div className="mt-2 text-sm text-red-600 bg-red-50 p-3 rounded-xl">
               <strong>Tip:</strong> Avoid repeating digits (1111), sequences (1234), or common patterns
            </div>
          )}
        </div>
      )}

      {/* PIN Requirements */}
      {value.length > 0 && value.length < 4 && (
        <div className="text-sm text-agri-gray">
          PIN must be exactly 4 digits ({value.length}/4)
        </div>
      )}
    </div>
  );
};

export default PINInput;

src/hooks/use-mobile.tsx.content:
import * as React from "react"

const MOBILE_BREAKPOINT = 768

export function useIsMobile() {
  const [isMobile, setIsMobile] = React.useState<boolean | undefined>(undefined)

  React.useEffect(() => {
    const mql = window.matchMedia(`(max-width: ${MOBILE_BREAKPOINT - 1}px)`)
    const onChange = () => {
      setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    }
    mql.addEventListener("change", onChange)
    setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    return () => mql.removeEventListener("change", onChange)
  }, [])

  return !!isMobile
}

src/hooks/use-toast.ts.content:
import * as React from "react"

import type {
  ToastActionElement,
  ToastProps,
} from "@/components/ui/toast"

const TOAST_LIMIT = 1
const TOAST_REMOVE_DELAY = 1000000

type ToasterToast = ToastProps & {
  id: string
  title?: React.ReactNode
  description?: React.ReactNode
  action?: ToastActionElement
}

const actionTypes = {
  ADD_TOAST: "ADD_TOAST",
  UPDATE_TOAST: "UPDATE_TOAST",
  DISMISS_TOAST: "DISMISS_TOAST",
  REMOVE_TOAST: "REMOVE_TOAST",
} as const

let count = 0

function genId() {
  count = (count + 1) % Number.MAX_SAFE_INTEGER
  return count.toString()
}

type ActionType = typeof actionTypes

type Action =
  | {
      type: ActionType["ADD_TOAST"]
      toast: ToasterToast
    }
  | {
      type: ActionType["UPDATE_TOAST"]
      toast: Partial<ToasterToast>
    }
  | {
      type: ActionType["DISMISS_TOAST"]
      toastId?: ToasterToast["id"]
    }
  | {
      type: ActionType["REMOVE_TOAST"]
      toastId?: ToasterToast["id"]
    }

interface State {
  toasts: ToasterToast[]
}

const toastTimeouts = new Map<string, ReturnType<typeof setTimeout>>()

const addToRemoveQueue = (toastId: string) => {
  if (toastTimeouts.has(toastId)) {
    return
  }

  const timeout = setTimeout(() => {
    toastTimeouts.delete(toastId)
    dispatch({
      type: "REMOVE_TOAST",
      toastId: toastId,
    })
  }, TOAST_REMOVE_DELAY)

  toastTimeouts.set(toastId, timeout)
}

export const reducer = (state: State, action: Action): State => {
  switch (action.type) {
    case "ADD_TOAST":
      return {
        ...state,
        toasts: [action.toast, ...state.toasts].slice(0, TOAST_LIMIT),
      }

    case "UPDATE_TOAST":
      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === action.toast.id ? { ...t, ...action.toast } : t
        ),
      }

    case "DISMISS_TOAST": {
      const { toastId } = action

      // ! Side effects ! - This could be extracted into a dismissToast() action,
      // but I'll keep it here for simplicity
      if (toastId) {
        addToRemoveQueue(toastId)
      } else {
        state.toasts.forEach((toast) => {
          addToRemoveQueue(toast.id)
        })
      }

      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === toastId || toastId === undefined
            ? {
                ...t,
                open: false,
              }
            : t
        ),
      }
    }
    case "REMOVE_TOAST":
      if (action.toastId === undefined) {
        return {
          ...state,
          toasts: [],
        }
      }
      return {
        ...state,
        toasts: state.toasts.filter((t) => t.id !== action.toastId),
      }
  }
}

const listeners: Array<(state: State) => void> = []

let memoryState: State = { toasts: [] }

function dispatch(action: Action) {
  memoryState = reducer(memoryState, action)
  listeners.forEach((listener) => {
    listener(memoryState)
  })
}

type Toast = Omit<ToasterToast, "id">

function toast({ ...props }: Toast) {
  const id = genId()

  const update = (props: ToasterToast) =>
    dispatch({
      type: "UPDATE_TOAST",
      toast: { ...props, id },
    })
  const dismiss = () => dispatch({ type: "DISMISS_TOAST", toastId: id })

  dispatch({
    type: "ADD_TOAST",
    toast: {
      ...props,
      id,
      open: true,
      onOpenChange: (open) => {
        if (!open) dismiss()
      },
    },
  })

  return {
    id: id,
    dismiss,
    update,
  }
}

function useToast() {
  const [state, setState] = React.useState<State>(memoryState)

  React.useEffect(() => {
    listeners.push(setState)
    return () => {
      const index = listeners.indexOf(setState)
      if (index > -1) {
        listeners.splice(index, 1)
      }
    }
  }, [state])

  return {
    ...state,
    toast,
    dismiss: (toastId?: string) => dispatch({ type: "DISMISS_TOAST", toastId }),
  }
}

export { useToast, toast }

src/hooks/useLocation.ts.content:
import { useState, useEffect } from 'react';

interface LocationData {
  latitude: number;
  longitude: number;
  state: string;
  district: string;
  accuracy: number;
}

interface LocationState {
  loading: boolean;
  data: LocationData | null;
  error: string | null;
  permissionStatus: 'granted' | 'denied' | 'prompt' | 'unsupported';
}

// Indian states and districts mapping for reverse geocoding fallback
const stateDistrictMapping: Record<string, string[]> = {
  "Maharashtra": ["Mumbai", "Pune", "Nagpur", "Nashik", "Aurangabad", "Solapur"],
  "Karnataka": ["Bangalore", "Mysore", "Hubli", "Mangalore", "Belgaum", "Gulbarga"],
  "Tamil Nadu": ["Chennai", "Coimbatore", "Madurai", "Tiruchirappalli", "Salem", "Tirunelveli"],
  "Gujarat": ["Ahmedabad", "Surat", "Vadodara", "Rajkot", "Bhavnagar", "Jamnagar"],
  "Punjab": ["Ludhiana", "Amritsar", "Jalandhar", "Patiala", "Bathinda", "Mohali"],
  "Uttar Pradesh": ["Lucknow", "Kanpur", "Agra", "Varanasi", "Allahabad", "Meerut"],
  "West Bengal": ["Kolkata", "Howrah", "Durgapur", "Asansol", "Siliguri", "Malda"],
  "Rajasthan": ["Jaipur", "Jodhpur", "Kota", "Bikaner", "Udaipur", "Ajmer"],
  "Madhya Pradesh": ["Bhopal", "Indore", "Gwalior", "Jabalpur", "Ujjain", "Sagar"],
  "Haryana": ["Gurugram", "Faridabad", "Panipat", "Ambala", "Yamunanagar", "Rohtak"]
};

const useLocation = () => {
  const [locationState, setLocationState] = useState<LocationState>({
    loading: false,
    data: null,
    error: null,
    permissionStatus: 'prompt'
  });

  const checkPermissionStatus = async (): Promise<'granted' | 'denied' | 'prompt' | 'unsupported'> => {
    if (!navigator.geolocation) {
      return 'unsupported';
    }

    try {
      const permission = await navigator.permissions.query({ name: 'geolocation' });
      return permission.state as 'granted' | 'denied' | 'prompt';
    } catch {
      return 'prompt';
    }
  };

  const reverseGeocode = async (lat: number, lon: number): Promise<{ state: string; district: string }> => {
    try {
      // Using a free geocoding service (OpenStreetMap Nominatim)
      const response = await fetch(
        `https://nominatim.openstreetmap.org/reverse?format=json&lat=${lat}&lon=${lon}&addressdetails=1&accept-language=en`
      );

      if (!response.ok) {
        throw new Error('Geocoding failed');
      }

      const data = await response.json();
      const address = data.address;

      let state = address.state || address.province || '';
      let district = address.city || address.town || address.village || address.suburb || '';

      // Fallback: Try to match with Indian states if exact match not found
      if (!state || !Object.keys(stateDistrictMapping).includes(state)) {
        // Simple matching logic for Indian context
        const stateName = Object.keys(stateDistrictMapping).find(s => 
          state.toLowerCase().includes(s.toLowerCase()) || 
          s.toLowerCase().includes(state.toLowerCase())
        );
        if (stateName) {
          state = stateName;
        }
      }

      return { 
        state: state || 'Unknown State', 
        district: district || 'Unknown District' 
      };
    } catch (error) {
      console.error('Reverse geocoding failed:', error);
      return { state: 'Unknown State', district: 'Unknown District' };
    }
  };

  const getCurrentLocation = async (): Promise<void> => {
    setLocationState(prev => ({ ...prev, loading: true, error: null }));

    try {
      const permissionStatus = await checkPermissionStatus();
      setLocationState(prev => ({ ...prev, permissionStatus }));

      if (permissionStatus === 'denied') {
        throw new Error('Location access denied. Please enable location services and try again.');
      }

      if (permissionStatus === 'unsupported') {
        throw new Error('Location services are not supported by your device.');
      }

      return new Promise((resolve, reject) => {
        const options: PositionOptions = {
          enableHighAccuracy: true,
          timeout: 15000,
          maximumAge: 300000 // 5 minutes cache
        };

        navigator.geolocation.getCurrentPosition(
          async (position) => {
            try {
              const { latitude, longitude, accuracy } = position.coords;
              const { state, district } = await reverseGeocode(latitude, longitude);

              const locationData: LocationData = {
                latitude,
                longitude,
                state,
                district,
                accuracy
              };

              setLocationState(prev => ({
                ...prev,
                loading: false,
                data: locationData,
                error: null,
                permissionStatus: 'granted'
              }));

              // Cache the location data
              localStorage.setItem('kisanmitra_location', JSON.stringify({
                ...locationData,
                timestamp: Date.now()
              }));

              resolve();
            } catch (error) {
              const errorMessage = error instanceof Error ? error.message : 'Failed to get location details';
              setLocationState(prev => ({
                ...prev,
                loading: false,
                error: errorMessage
              }));
              reject(new Error(errorMessage));
            }
          },
          (error) => {
            let errorMessage = 'Unable to retrieve your location. ';

            switch (error.code) {
              case error.PERMISSION_DENIED:
                errorMessage += 'Location access denied.';
                setLocationState(prev => ({ ...prev, permissionStatus: 'denied' }));
                break;
              case error.POSITION_UNAVAILABLE:
                errorMessage += 'Location information unavailable.';
                break;
              case error.TIMEOUT:
                errorMessage += 'Location request timed out.';
                break;
              default:
                errorMessage += 'An unknown error occurred.';
                break;
            }

            setLocationState(prev => ({
              ...prev,
              loading: false,
              error: errorMessage
            }));
            reject(new Error(errorMessage));
          },
          options
        );
      });
    } catch (error) {
      const errorMessage = error instanceof Error ? error.message : 'Location access failed';
      setLocationState(prev => ({
        ...prev,
        loading: false,
        error: errorMessage
      }));
    }
  };

  const loadCachedLocation = (): boolean => {
    try {
      const cached = localStorage.getItem('kisanmitra_location');
      if (cached) {
        const { timestamp, ...locationData } = JSON.parse(cached);
        const now = Date.now();
        const fiveMinutes = 5 * 60 * 1000;

        if (now - timestamp < fiveMinutes) {
          setLocationState(prev => ({
            ...prev,
            data: locationData,
            permissionStatus: 'granted'
          }));
          return true;
        }
      }
    } catch (error) {
      console.error('Failed to load cached location:', error);
    }
    return false;
  };

  const clearLocation = () => {
    setLocationState({
      loading: false,
      data: null,
      error: null,
      permissionStatus: 'prompt'
    });
    localStorage.removeItem('kisanmitra_location');
  };

  const retryLocation = () => {
    getCurrentLocation();
  };

  const getAccuracyLevel = (accuracy: number): 'high' | 'medium' | 'low' => {
    if (accuracy <= 100) return 'high';
    if (accuracy <= 1000) return 'medium';
    return 'low';
  };

  useEffect(() => {
    // Try to load cached location on mount
    loadCachedLocation();
  }, []);

  return {
    ...locationState,
    getCurrentLocation,
    retryLocation,
    clearLocation,
    getAccuracyLevel: locationState.data ? getAccuracyLevel(locationState.data.accuracy) : null,
    hasValidLocation: !!locationState.data && !locationState.error,
    stateDistrictMapping
  };
};

export default useLocation;

src/lib/cart.ts.content:
// Cart state management using localStorage
export interface CartItem {
  id: string;
  name: string;
  brand: string;
  price: number;
  originalPrice: number;
  quantity: number;
  size: string;
  category: string;
  features: string[];
  inStock: boolean;
}

export interface Order {
  id: string;
  status: 'processing' | 'shipped' | 'delivered' | 'cancelled';
  items: CartItem[];
  subtotal: number;
  discount: number;
  deliveryFee: number;
  total: number;
  orderDate: string;
  estimatedDelivery?: string;
  deliveryDate?: string;
  address: string;
  paymentMethod: string;
}

class CartManager {
  private static instance: CartManager;
  private cartKey = 'hariyali_mitra_cart';
  private ordersKey = 'hariyali_mitra_orders';

  static getInstance(): CartManager {
    if (!CartManager.instance) {
      CartManager.instance = new CartManager();
    }
    return CartManager.instance;
  }

  // Cart operations
  getCart(): CartItem[] {
    try {
      const cart = localStorage.getItem(this.cartKey);
      return cart ? JSON.parse(cart) : [];
    } catch {
      return [];
    }
  }

  addToCart(product: any): void {
    const cart = this.getCart();
    const existingItem = cart.find(item => item.id === product.id);

    if (existingItem) {
      existingItem.quantity += 1;
    } else {
      const cartItem: CartItem = {
        id: product.id,
        name: product.name,
        brand: product.brand,
        price: product.price,
        originalPrice: product.originalPrice,
        quantity: 1,
        size: product.size,
        category: product.category,
        features: product.features,
        inStock: product.inStock
      };
      cart.push(cartItem);
    }

    localStorage.setItem(this.cartKey, JSON.stringify(cart));
    this.notifyCartChange();
  }

  removeFromCart(productId: string): void {
    const cart = this.getCart();
    const existingItem = cart.find(item => item.id === productId);

    if (existingItem && existingItem.quantity > 1) {
      existingItem.quantity -= 1;
    } else {
      const index = cart.findIndex(item => item.id === productId);
      if (index > -1) {
        cart.splice(index, 1);
      }
    }

    localStorage.setItem(this.cartKey, JSON.stringify(cart));
    this.notifyCartChange();
  }

  updateQuantity(productId: string, quantity: number): void {
    if (quantity <= 0) {
      this.deleteItem(productId);
      return;
    }

    const cart = this.getCart();
    const existingItem = cart.find(item => item.id === productId);

    if (existingItem) {
      existingItem.quantity = quantity;
      localStorage.setItem(this.cartKey, JSON.stringify(cart));
      this.notifyCartChange();
    }
  }

  deleteItem(productId: string): void {
    const cart = this.getCart();
    const filteredCart = cart.filter(item => item.id !== productId);
    localStorage.setItem(this.cartKey, JSON.stringify(filteredCart));
    this.notifyCartChange();
  }

  clearCart(): void {
    localStorage.removeItem(this.cartKey);
    this.notifyCartChange();
  }

  getCartCount(): number {
    return this.getCart().reduce((total, item) => total + item.quantity, 0);
  }

  getCartTotal(): number {
    return this.getCart().reduce((total, item) => total + (item.price * item.quantity), 0);
  }

  getCartSubtotal(): number {
    return this.getCart().reduce((total, item) => total + (item.price * item.quantity), 0);
  }

  getCartSavings(): number {
    return this.getCart().reduce((total, item) => total + ((item.originalPrice - item.price) * item.quantity), 0);
  }

  // Order operations
  getOrders(): Order[] {
    try {
      const orders = localStorage.getItem(this.ordersKey);
      return orders ? JSON.parse(orders) : [];
    } catch {
      return [];
    }
  }

  createOrder(orderData: {
    address: string;
    paymentMethod: string;
    discount?: number;
  }): Order {
    const cart = this.getCart();
    if (cart.length === 0) {
      throw new Error('Cart is empty');
    }

    const subtotal = this.getCartSubtotal();
    const discount = orderData.discount || 0;
    const deliveryFee = subtotal >= 500 ? 0 : 40;
    const total = subtotal - discount + deliveryFee;

    const orderId = 'ORD' + Date.now();
    const orderDate = new Date().toISOString();
    const estimatedDelivery = new Date(Date.now() + 24 * 60 * 60 * 1000).toISOString(); // Tomorrow

    const order: Order = {
      id: orderId,
      status: 'processing',
      items: [...cart],
      subtotal,
      discount,
      deliveryFee,
      total,
      orderDate,
      estimatedDelivery,
      address: orderData.address,
      paymentMethod: orderData.paymentMethod
    };

    const orders = this.getOrders();
    orders.unshift(order); // Add to beginning
    localStorage.setItem(this.ordersKey, JSON.stringify(orders));

    // Clear cart after successful order
    this.clearCart();

    return order;
  }

  updateOrderStatus(orderId: string, status: Order['status']): void {
    const orders = this.getOrders();
    const order = orders.find(o => o.id === orderId);

    if (order) {
      order.status = status;
      if (status === 'delivered') {
        order.deliveryDate = new Date().toISOString();
      }
      localStorage.setItem(this.ordersKey, JSON.stringify(orders));
    }
  }

  // Event system for cart changes
  private notifyCartChange(): void {
    window.dispatchEvent(new CustomEvent('cartChanged'));
  }

  // Subscribe to cart changes
  onCartChange(callback: () => void): () => void {
    const handler = () => callback();
    window.addEventListener('cartChanged', handler);

    // Return unsubscribe function
    return () => window.removeEventListener('cartChanged', handler);
  }
}

export const cartManager = CartManager.getInstance();

