import { useState, useRef, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import { 
  Camera, 
  Upload, 
  ArrowLeft, 
  RefreshCw,
  Check,
  X,
  Sparkles
} from 'lucide-react';
import { Button } from '@/components/ui/button';
import { toast } from '@/components/ui/use-toast';

const CameraCapture = () => {
  const navigate = useNavigate();
  const videoRef = useRef<HTMLVideoElement>(null);
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const fileInputRef = useRef<HTMLInputElement>(null);
  const [stream, setStream] = useState<MediaStream | null>(null);
  const [capturedImage, setCapturedImage] = useState<string | null>(null);
  const [isCamera, setIsCamera] = useState(true);

  const startCamera = useCallback(async () => {
    try {
      const mediaStream = await navigator.mediaDevices.getUserMedia({
        video: { facingMode: 'environment' }
      });
      setStream(mediaStream);
      if (videoRef.current) {
        videoRef.current.srcObject = mediaStream;
      }
    } catch (error) {
      console.error('Error accessing camera:', error);
      toast({
        title: "Camera Error",
        description: "Unable to access camera. Please check permissions.",
        variant: "destructive"
      });
    }
  }, []);

  const stopCamera = useCallback(() => {
    if (stream) {
      stream.getTracks().forEach(track => track.stop());
      setStream(null);
    }
  }, [stream]);

  const capturePhoto = useCallback(() => {
    if (videoRef.current && canvasRef.current) {
      const canvas = canvasRef.current;
      const video = videoRef.current;
      const context = canvas.getContext('2d');

      if (context) {
        canvas.width = video.videoWidth;
        canvas.height = video.videoHeight;
        context.drawImage(video, 0, 0);

        const imageDataUrl = canvas.toDataURL('image/jpeg', 0.8);
        setCapturedImage(imageDataUrl);
        stopCamera();
      }
    }
  }, [stopCamera]);

  const handleFileUpload = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        setCapturedImage(e.target?.result as string);
        setIsCamera(false);
      };
      reader.readAsDataURL(file);
    }
  };

  const proceedWithAnalysis = () => {
    if (capturedImage) {
      // Store image in localStorage for the diagnosis process
      localStorage.setItem('plant_image', capturedImage);
      navigate('/diagnose/analyze');
    }
  };

  const retakePhoto = () => {
    setCapturedImage(null);
    if (isCamera) {
      startCamera();
    }
  };

  // Start camera when component mounts
  useState(() => {
    if (isCamera) {
      startCamera();
    }
  });

  return (
    <div className="mobile-container">
      {/* Header */}
      <div className="mobile-header bg-agri-cream">
        <div className="flex items-center space-x-3">
          <button 
            onClick={() => {
              stopCamera();
              navigate('/diagnose');
            }}
            className="p-2 hover:bg-agri-light rounded-full transition-colors"
          >
            <ArrowLeft className="w-6 h-6 text-agri-accent" />
          </button>
          <h1 className="text-xl font-bold text-agri-accent">
            {capturedImage ? 'Review Image' : 'Capture Plant Image'}
          </h1>
        </div>
      </div>

      <div className="mobile-content space-y-6 pt-4">
        {!capturedImage ? (
          <>
            {/* Camera View */}
            <div className="relative bg-black rounded-2xl overflow-hidden aspect-[4/3]">
              {isCamera ? (
                <video
                  ref={videoRef}
                  autoPlay
                  playsInline
                  className="w-full h-full object-cover"
                />
              ) : (
                <div className="w-full h-full flex items-center justify-center bg-agri-light">
                  <Upload className="w-16 h-16 text-agri-primary" />
                </div>
              )}

              {/* Camera Overlay */}
              <div className="absolute inset-0 pointer-events-none">
                <div className="absolute inset-6 border-2 border-white/50 rounded-2xl" />
                <div className="absolute top-4 left-4 bg-black/20 rounded-full px-3 py-1">
                  <div className="flex items-center space-x-2">
                    <div className="w-2 h-2 bg-red-500 rounded-full animate-pulse" />
                    <span className="text-white text-sm">Live</span>
                  </div>
                </div>
              </div>
            </div>

            {/* Controls */}
            <div className="space-y-4">
              {/* Capture Methods */}
              <div className="flex space-x-4">
                <Button
                  onClick={() => {
                    setIsCamera(true);
                    startCamera();
                  }}
                  variant={isCamera ? "default" : "outline"}
                  className="flex-1"
                >
                  <Camera className="w-4 h-4 mr-2" />
                  Camera
                </Button>
                <Button
                  onClick={() => {
                    setIsCamera(false);
                    stopCamera();
                    fileInputRef.current?.click();
                  }}
                  variant={!isCamera ? "default" : "outline"}
                  className="flex-1"
                >
                  <Upload className="w-4 h-4 mr-2" />
                  Gallery
                </Button>
              </div>

              {/* Capture Button */}
              {isCamera && stream && (
                <div className="flex justify-center">
                  <button
                    onClick={capturePhoto}
                    className="w-20 h-20 bg-white border-4 border-agri-primary rounded-full flex items-center justify-center shadow-lg transform transition-transform active:scale-95"
                  >
                    <div className="w-16 h-16 bg-agri-primary rounded-full flex items-center justify-center">
                      <Camera className="w-8 h-8 text-white" />
                    </div>
                  </button>
                </div>
              )}
            </div>

            {/* Hidden file input */}
            <input
              ref={fileInputRef}
              type="file"
              accept="image/*"
              onChange={handleFileUpload}
              className="hidden"
            />
          </>
        ) : (
          <>
            {/* Captured Image Preview */}
            <div className="space-y-4">
              <div className="relative rounded-2xl overflow-hidden">
                <img
                  src={capturedImage}
                  alt="Captured plant"
                  className="w-full aspect-[4/3] object-cover"
                />
                <div className="absolute top-4 right-4 bg-agri-success rounded-full p-2">
                  <Check className="w-5 h-5 text-white" />
                </div>
              </div>

              {/* Image Quality Check */}
              <div className="bg-agri-light rounded-xl p-4">
                <div className="flex items-center space-x-3 mb-3">
                  <Sparkles className="w-5 h-5 text-agri-primary" />
                  <span className="font-semibold text-agri-accent">Image Quality Check</span>
                </div>
                <div className="space-y-2">
                  <div className="flex items-center justify-between">
                    <span className="text-sm text-agri-gray">Clarity</span>
                    <span className="text-sm font-medium text-agri-success">Good </span>
                  </div>
                  <div className="flex items-center justify-between">
                    <span className="text-sm text-agri-gray">Lighting</span>
                    <span className="text-sm font-medium text-agri-success">Good </span>
                  </div>
                  <div className="flex items-center justify-between">
                    <span className="text-sm text-agri-gray">Plant Focus</span>
                    <span className="text-sm font-medium text-agri-success">Good </span>
                  </div>
                </div>
              </div>

              {/* Action Buttons */}
              <div className="flex space-x-4">
                <Button
                  onClick={retakePhoto}
                  variant="outline"
                  className="flex-1"
                >
                  <RefreshCw className="w-4 h-4 mr-2" />
                  Retake
                </Button>
                <Button
                  onClick={proceedWithAnalysis}
                  className="flex-1 bg-agri-primary hover:bg-agri-secondary"
                >
                  <Sparkles className="w-4 h-4 mr-2" />
                  Analyze
                </Button>
              </div>
            </div>
          </>
        )}

        {/* Tips */}
        <div className="bg-yellow-50 rounded-xl p-4 border border-yellow-200">
          <h3 className="font-semibold text-yellow-800 mb-2"> Best Photo Tips:</h3>
          <ul className="space-y-1 text-sm text-yellow-700">
            <li> Focus on affected leaves or areas</li>
            <li> Ensure good natural lighting</li>
            <li> Keep image clear and close-up</li>
            <li> Avoid shadows and reflections</li>
          </ul>
        </div>
      </div>

      <canvas ref={canvasRef} className="hidden" />
    </div>
  );
};

export default CameraCapture;

src/pages/Cart.tsx.content:
import { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { 
  ArrowLeft, 
  Plus, 
  Minus, 
  Trash2,
  Tag,
  Truck,
  ShoppingBag,
  Shield
} from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Badge } from '@/components/ui/badge';
import { cartManager, CartItem } from '@/lib/cart';

const Cart = () => {
  const navigate = useNavigate();
  const [promoCode, setPromoCode] = useState('');
  const [appliedPromo, setAppliedPromo] = useState('');

  const [cartItems, setCartItems] = useState<CartItem[]>([]);

  useEffect(() => {
    loadCart();
    const unsubscribe = cartManager.onCartChange(() => {
      loadCart();
    });
    return unsubscribe;
  }, []);

  const loadCart = () => {
    setCartItems(cartManager.getCart());
  };

  const updateQuantity = (id: string, newQuantity: number) => {
    cartManager.updateQuantity(id, newQuantity);
  };

  const removeItem = (id: string) => {
    cartManager.deleteItem(id);
  };

  const applyPromoCode = () => {
    if (promoCode === 'FARMER10') {
      setAppliedPromo(promoCode);
      setPromoCode('');
    }
  };

  const subtotal = cartManager.getCartSubtotal();
  const savings = cartManager.getCartSavings();
  const discount = appliedPromo ? Math.round(subtotal * 0.1) : 0;
  const deliveryFee = subtotal >= 500 ? 0 : 40;
  const total = subtotal - discount + deliveryFee;

  if (cartItems.length === 0) {
    return (
      <div className="mobile-container bg-background">
        <div className="mobile-header">
          <div className="flex items-center space-x-3">
            <button onClick={() => navigate('/shop')}>
              <ArrowLeft className="w-6 h-6 text-gray-700" />
            </button>
            <h1 className="text-xl font-bold text-text-secondary">Cart</h1>
          </div>
        </div>

        <div className="mobile-content flex flex-col items-center justify-center min-h-[60vh]">
          <ShoppingBag className="w-24 h-24 text-gray-300 mb-6" />
          <h2 className="text-xl font-bold text-gray-900 mb-2">Your cart is empty</h2>
          <p className="text-gray-500 mb-8 text-center">Add some products to get started</p>
          <Button 
            onClick={() => navigate('/shop')}
            className="bg-green-600 hover:bg-green-700 text-white px-8 py-3"
          >
            Continue Shopping
          </Button>
        </div>
      </div>
    );
  }

  return (
    <div className="mobile-container bg-background">
      {/* Header */}
      <div className="mobile-header">
        <div className="flex items-center space-x-3">
          <button onClick={() => navigate('/shop')}>
            <ArrowLeft className="w-6 h-6 text-gray-700" />
          </button>
          <h1 className="text-xl font-bold text-text-secondary">Cart ({cartItems.length})</h1>
        </div>
      </div>

      <div className="mobile-content space-y-6 pt-4">
        {/* Delivery Info */}
        <div className="bg-green-50 border border-green-200 rounded-2xl p-4">
          <div className="flex items-center space-x-3">
            <Truck className="w-5 h-5 text-green-600" />
            <div>
              <p className="font-semibold text-green-800">
                {deliveryFee === 0 ? 'Free Delivery!' : `${40 - (subtotal/500*40)} more for free delivery`}
              </p>
              <p className="text-sm text-green-600">
                {deliveryFee === 0 ? 'Your order qualifies for free delivery' : 'Add more items to get free delivery'}
              </p>
            </div>
          </div>
        </div>

        {/* Cart Items */}
        <div className="space-y-4">
          {cartItems.map((item) => (
            <div key={item.id} className="bg-white rounded-2xl p-4 shadow-sm border border-gray-100">
              <div className="flex space-x-4">
                <div className="w-20 h-20 bg-gray-100 rounded-xl flex items-center justify-center">
                  <ShoppingBag className="w-8 h-8 text-green-600" />
                </div>

                <div className="flex-1">
                  <div className="flex items-start justify-between">
                    <div className="flex-1">
                      <h3 className="font-semibold text-gray-900 text-sm">{item.name}</h3>
                      <p className="text-xs text-gray-500 mt-1">{item.brand}  {item.size}</p>

                      <div className="flex items-center space-x-2 mt-2">
                        <span className="text-lg font-bold text-gray-900">{item.price}</span>
                        <span className="text-sm text-gray-400 line-through">{item.originalPrice}</span>
                        <Badge className="bg-green-100 text-green-800 text-xs">
                          Save {item.originalPrice - item.price}
                        </Badge>
                      </div>
                    </div>

                    <button 
                      onClick={() => removeItem(item.id)}
                      className="p-2 text-gray-400 hover:text-red-500"
                    >
                      <Trash2 className="w-4 h-4" />
                    </button>
                  </div>

                  <div className="flex items-center justify-between mt-4">
                    <div className="flex items-center space-x-3">
                      <button
                        onClick={() => updateQuantity(item.id, item.quantity - 1)}
                        className="w-8 h-8 bg-gray-100 rounded-lg flex items-center justify-center"
                      >
                        <Minus className="w-4 h-4 text-gray-600" />
                      </button>
                      <span className="font-semibold text-gray-900 min-w-[20px] text-center">
                        {item.quantity}
                      </span>
                      <button
                        onClick={() => updateQuantity(item.id, item.quantity + 1)}
                        className="w-8 h-8 bg-green-600 rounded-lg flex items-center justify-center"
                      >
                        <Plus className="w-4 h-4 text-white" />
                      </button>
                    </div>

                    <div className="text-right">
                      <p className="font-bold text-gray-900">{item.price * item.quantity}</p>
                      {item.quantity > 1 && (
                        <p className="text-xs text-gray-500">{item.price} each</p>
                      )}
                    </div>
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>

        {/* Promo Code */}
        <div className="bg-white rounded-2xl p-4 shadow-sm border border-gray-100">
          <div className="flex items-center space-x-2 mb-3">
            <Tag className="w-5 h-5 text-green-600" />
            <h3 className="font-semibold text-gray-900">Promo Code</h3>
          </div>

          {appliedPromo ? (
            <div className="flex items-center justify-between p-3 bg-green-50 rounded-xl">
              <div className="flex items-center space-x-2">
                <Badge className="bg-green-600 text-white">{appliedPromo}</Badge>
                <span className="text-sm text-green-700">10% discount applied</span>
              </div>
              <button 
                onClick={() => setAppliedPromo('')}
                className="text-sm text-green-600 hover:text-green-800"
              >
                Remove
              </button>
            </div>
          ) : (
            <div className="flex space-x-2">
              <Input
                placeholder="Enter promo code"
                value={promoCode}
                onChange={(e) => setPromoCode(e.target.value)}
                className="flex-1"
              />
              <Button 
                onClick={applyPromoCode}
                variant="outline"
                className="border-green-600 text-green-600"
              >
                Apply
              </Button>
            </div>
          )}
        </div>

        {/* Order Summary */}
        <div className="bg-white rounded-2xl p-6 shadow-sm border border-gray-100">
          <h3 className="font-semibold text-gray-900 mb-4">Order Summary</h3>

          <div className="space-y-3">
            <div className="flex justify-between">
              <span className="text-gray-600">Subtotal ({cartItems.reduce((sum, item) => sum + item.quantity, 0)} items)</span>
              <span className="font-medium">{subtotal}</span>
            </div>

            {savings > 0 && (
              <div className="flex justify-between text-green-600">
                <span>You saved</span>
                <span className="font-medium">-{savings}</span>
              </div>
            )}

            {discount > 0 && (
              <div className="flex justify-between text-green-600">
                <span>Promo discount</span>
                <span className="font-medium">-{discount}</span>
              </div>
            )}

            <div className="flex justify-between">
              <span className="text-gray-600">Delivery fee</span>
              <span className={`font-medium ${deliveryFee === 0 ? 'text-green-600' : ''}`}>
                {deliveryFee === 0 ? 'FREE' : `${deliveryFee}`}
              </span>
            </div>

            <div className="border-t pt-3">
              <div className="flex justify-between items-center">
                <span className="text-lg font-bold text-gray-900">Total</span>
                <span className="text-xl font-bold text-green-600">{total}</span>
              </div>
            </div>
          </div>
        </div>

        {/* Security Badge */}
        <div className="flex items-center justify-center space-x-2 text-gray-500">
          <Shield className="w-4 h-4" />
          <span className="text-sm">100% Secure Checkout</span>
        </div>
      </div>

      {/* Checkout Button */}
      <div className="fixed bottom-0 left-0 right-0 bg-white border-t border-gray-200 p-4">
        <Button 
          onClick={() => {
            // Pass cart data to checkout
            navigate('/checkout', { 
              state: { 
                cartItems,
                subtotal,
                savings,
                discount,
                deliveryFee,
                total,
                appliedPromo
              }
            })
          }}
          className="w-full bg-green-600 hover:bg-green-700 text-white py-4 rounded-xl text-lg font-semibold"
        >
          Proceed to Checkout  {total}
        </Button>
      </div>
    </div>
  );
};

export default Cart;

src/pages/Checkout.tsx.content:
import { useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { 
  ArrowLeft, 
  MapPin, 
  Edit3,
  CreditCard,
  Smartphone,
  Banknote,
  CheckCircle,
  Clock,
  Shield,
  Truck,
  User,
  Phone
} from 'lucide-react';
import { cartManager } from '@/lib/cart';

const Checkout = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const [selectedAddress, setSelectedAddress] = useState('home');
  const [selectedPayment, setSelectedPayment] = useState('upi');
  const [isProcessing, setIsProcessing] = useState(false);

  // Get cart data from location state or cart manager
  const cartData = location.state || {};
  const cartItems = cartData.cartItems || cartManager.getCart();
  const subtotal = cartData.subtotal || cartManager.getCartSubtotal();
  const discount = cartData.discount || 0;
  const deliveryFee = cartData.deliveryFee || (subtotal >= 500 ? 0 : 40);
  const total = cartData.total || (subtotal - discount + deliveryFee);

  const addresses = [
    {
      id: 'home',
      type: 'Home',
      name: 'Ramu Ji',
      address: 'Village Rampur, Dist. Hardoi, UP 241001',
      phone: '+91 98765 43210'
    },
    {
      id: 'farm',
      type: 'Farm',
      name: 'Ramu Ji',
      address: 'Plot No. 45, Sector 12, Village Rampur, UP 241001',
      phone: '+91 98765 43210'
    }
  ];

  const paymentMethods = [
    {
      id: 'upi',
      name: 'UPI Payment',
      description: 'Pay using PhonePe, Google Pay, Paytm',
      icon: Smartphone,
      recommended: true,
      discount: 5
    },
    {
      id: 'card',
      name: 'Credit/Debit Card',
      description: 'Visa, Mastercard, RuPay',
      icon: CreditCard,
      recommended: false,
      discount: 0
    },
    {
      id: 'cod',
      name: 'Cash on Delivery',
      description: 'Pay when you receive',
      icon: Banknote,
      recommended: false,
      discount: 0
    }
  ];

  const orderSummary = {
    items: cartItems.map(item => ({
      name: item.name,
      quantity: item.quantity,
      price: item.price * item.quantity,
      image: item.category
    })),
    subtotal,
    discount,
    delivery: deliveryFee,
    total
  };

  const handlePlaceOrder = async () => {
    setIsProcessing(true);

    try {
      // Simulate payment processing
      await new Promise(resolve => setTimeout(resolve, 3000));

      // Get selected address
      const address = addresses.find(addr => addr.id === selectedAddress);
      const addressString = `${address?.name}, ${address?.address}`;

      // Create order
      const order = cartManager.createOrder({
        address: addressString,
        paymentMethod: selectedPayment,
        discount
      });

      // Navigate to order success page
      navigate('/order-success', { 
        state: { 
          orderId: order.id,
          order,
          selectedAddress: address
        } 
      });
    } catch (error) {
      alert('Failed to place order. Please try again.');
    } finally {
      setIsProcessing(false);
    }
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <div className="sticky top-0 z-40 bg-white shadow-sm border-b border-gray-100">
        <div className="flex items-center justify-between px-4 py-4">
          <div className="flex items-center space-x-3">
            <button 
              onClick={() => navigate('/cart')}
              className="p-2 -ml-2 hover:bg-gray-100 rounded-full transition-colors"
            >
              <ArrowLeft className="w-6 h-6 text-gray-700" />
            </button>
            <h1 className="text-xl font-bold text-gray-900">Checkout</h1>
          </div>
          <div className="flex items-center space-x-2 bg-green-50 px-3 py-1 rounded-full">
            <Shield className="w-4 h-4 text-green-600" />
            <span className="text-sm font-medium text-green-800">Secure</span>
          </div>
        </div>
      </div>

      <div className="px-4 py-6 space-y-6 pb-32">
        {/* Order Summary - Compact */}
        <div className="bg-white rounded-2xl p-6 shadow-sm border border-gray-100">
          <div className="flex items-center justify-between mb-4">
            <h3 className="font-bold text-gray-900 flex items-center space-x-2">
              <CheckCircle className="w-5 h-5 text-green-600" />
              <span>Order Summary</span>
            </h3>
            <span className="text-sm text-gray-500">{orderSummary.items.length} items</span>
          </div>

          <div className="space-y-3 mb-4">
            {orderSummary.items.map((item, index) => (
              <div key={index} className="flex items-center space-x-3 py-2 border-b border-gray-50 last:border-0">
                <div className="w-12 h-12 bg-green-100 rounded-lg flex items-center justify-center">
                  <span className="text-lg font-bold text-green-600">{item.quantity}</span>
                </div>
                <div className="flex-1">
                  <p className="font-medium text-gray-900 text-sm">{item.name}</p>
                  <p className="text-sm text-gray-500">{item.price}</p>
                </div>
              </div>
            ))}
          </div>

          <div className="border-t border-gray-100 pt-4 space-y-2">
            <div className="flex justify-between text-sm">
              <span className="text-gray-600">Subtotal</span>
              <span className="font-medium">{orderSummary.subtotal}</span>
            </div>
            <div className="flex justify-between text-sm text-green-600">
              <span>Discount</span>
              <span className="font-medium">-{orderSummary.discount}</span>
            </div>
            <div className="flex justify-between text-sm">
              <span className="text-gray-600">Delivery</span>
              <span className="font-medium text-green-600">FREE</span>
            </div>
            <div className="flex justify-between items-center pt-2 border-t border-gray-100">
              <span className="text-lg font-bold text-gray-900">Total</span>
              <span className="text-2xl font-bold text-green-600">{orderSummary.total}</span>
            </div>
          </div>
        </div>

        {/* Delivery Address */}
        <div className="bg-white rounded-2xl p-6 shadow-sm border border-gray-100">
          <div className="flex items-center justify-between mb-5">
            <div className="flex items-center space-x-3">
              <div className="w-10 h-10 bg-green-100 rounded-full flex items-center justify-center">
                <MapPin className="w-5 h-5 text-green-600" />
              </div>
              <h3 className="font-bold text-gray-900">Delivery Address</h3>
            </div>
            <button className="text-green-600 hover:text-green-700 p-2 hover:bg-green-50 rounded-lg transition-colors">
              <Edit3 className="w-4 h-4" />
            </button>
          </div>

          <div className="space-y-3">
            {addresses.map((address) => (
              <div key={address.id} className={`border-2 rounded-xl p-4 cursor-pointer transition-all ${
                selectedAddress === address.id 
                  ? 'border-green-500 bg-green-50' 
                  : 'border-gray-200 hover:border-gray-300'
              }`} onClick={() => setSelectedAddress(address.id)}>
                <div className="flex items-start space-x-3">
                  <div className={`w-5 h-5 rounded-full border-2 mt-1 ${
                    selectedAddress === address.id 
                      ? 'border-green-500 bg-green-500' 
                      : 'border-gray-300'
                  }`}>
                    {selectedAddress === address.id && (
                      <div className="w-full h-full rounded-full bg-white scale-50"></div>
                    )}
                  </div>
                  <div className="flex-1">
                    <div className="flex items-center space-x-2 mb-2">
                      <User className="w-4 h-4 text-gray-600" />
                      <span className="font-semibold text-gray-900">{address.name}</span>
                      <span className="text-xs bg-green-100 text-green-800 px-2 py-1 rounded-full font-medium">
                        {address.type}
                      </span>
                    </div>
                    <p className="text-gray-600 text-sm mb-2">{address.address}</p>
                    <div className="flex items-center space-x-1">
                      <Phone className="w-3 h-3 text-gray-500" />
                      <span className="text-sm text-gray-500">{address.phone}</span>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>

          <button className="w-full mt-4 p-4 border-2 border-dashed border-green-300 rounded-xl text-green-600 font-semibold hover:border-green-400 hover:bg-green-50 transition-colors">
            + Add New Address
          </button>
        </div>

        {/* Delivery Time */}
        <div className="bg-gradient-to-r from-green-50 to-blue-50 rounded-2xl p-6 border border-green-100">
          <div className="flex items-center space-x-3 mb-4">
            <div className="w-10 h-10 bg-green-100 rounded-full flex items-center justify-center">
              <Truck className="w-5 h-5 text-green-600" />
            </div>
            <h3 className="font-bold text-gray-900">Delivery Information</h3>
          </div>

          <div className="bg-white rounded-xl p-4 border border-green-200">
            <div className="flex items-center space-x-3">
              <CheckCircle className="w-6 h-6 text-green-600" />
              <div>
                <p className="font-semibold text-green-800">Expected Delivery</p>
                <p className="text-green-600 text-sm">Tomorrow by 6:00 PM</p>
                <p className="text-xs text-gray-500 mt-1">Free delivery on orders above 500</p>
              </div>
            </div>
          </div>
        </div>

        {/* Payment Method */}
        <div className="bg-white rounded-2xl p-6 shadow-sm border border-gray-100">
          <div className="flex items-center space-x-3 mb-5">
            <div className="w-10 h-10 bg-green-100 rounded-full flex items-center justify-center">
              <CreditCard className="w-5 h-5 text-green-600" />
            </div>
            <h3 className="font-bold text-gray-900">Payment Method</h3>
          </div>

          <div className="space-y-3">
            {paymentMethods.map((method) => (
              <div 
                key={method.id} 
                className={`border-2 rounded-xl p-4 cursor-pointer transition-all ${
                  selectedPayment === method.id 
                    ? 'border-green-500 bg-green-50' 
                    : 'border-gray-200 hover:border-gray-300'
                }`}
                onClick={() => setSelectedPayment(method.id)}
              >
                <div className="flex items-center space-x-4">
                  <div className={`w-5 h-5 rounded-full border-2 ${
                    selectedPayment === method.id 
                      ? 'border-green-500 bg-green-500' 
                      : 'border-gray-300'
                  }`}>
                    {selectedPayment === method.id && (
                      <div className="w-full h-full rounded-full bg-white scale-50"></div>
                    )}
                  </div>
                  <div className={`w-10 h-10 rounded-full flex items-center justify-center ${
                    selectedPayment === method.id ? 'bg-green-100' : 'bg-gray-100'
                  }`}>
                    <method.icon className={`w-5 h-5 ${
                      selectedPayment === method.id ? 'text-green-600' : 'text-gray-600'
                    }`} />
                  </div>
                  <div className="flex-1">
                    <div className="flex items-center space-x-2">
                      <span className="font-semibold text-gray-900">{method.name}</span>
                      {method.recommended && (
                        <span className="text-xs bg-orange-100 text-orange-800 px-2 py-1 rounded-full font-medium">
                          Recommended
                        </span>
                      )}
                      {method.discount > 0 && (
                        <span className="text-xs bg-green-100 text-green-800 px-2 py-1 rounded-full font-medium">
                          {method.discount}% Off
                        </span>
                      )}
                    </div>
                    <p className="text-sm text-gray-600 mt-1">{method.description}</p>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>

        {/* Security Info */}
        <div className="bg-gradient-to-r from-green-50 to-blue-50 rounded-2xl p-5 border border-green-100">
          <div className="flex items-center space-x-3">
            <div className="w-10 h-10 bg-green-100 rounded-full flex items-center justify-center">
              <Shield className="w-5 h-5 text-green-600" />
            </div>
            <div>
              <p className="font-semibold text-green-800">100% Secure Payment</p>
              <p className="text-sm text-green-600">Your payment information is protected with 256-bit SSL encryption</p>
            </div>
          </div>
        </div>
      </div>

      {/* Place Order Button */}
      <div className="fixed bottom-0 left-0 right-0 bg-white border-t border-gray-200 p-4 shadow-lg">
        <button 
          onClick={handlePlaceOrder}
          disabled={isProcessing}
          className="w-full bg-green-600 hover:bg-green-700 text-white py-4 rounded-2xl text-lg font-bold disabled:opacity-50 disabled:cursor-not-allowed transition-all duration-200 shadow-lg"
        >
          {isProcessing ? (
            <div className="flex items-center justify-center space-x-2">
              <Clock className="w-5 h-5 animate-spin" />
              <span>Processing Payment...</span>
            </div>
          ) : (
            <div className="flex items-center justify-between px-4">
              <span>Place Order</span>
              <span className="bg-white bg-opacity-20 px-3 py-1 rounded-lg">{orderSummary.total}</span>
            </div>
          )}
        </button>
        <p className="text-center text-xs text-gray-500 mt-2">
          By placing this order, you agree to our Terms & Conditions
        </p>
      </div>
    </div>
  );
};

export default Checkout;

src/pages/CropGuideDetail.tsx.content:
import { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { 
  ArrowLeft, 
  Bookmark,
  BookmarkCheck,
  ChevronDown,
  ChevronUp,
  Sprout,
  Droplets,
  Sun,
  Scissors,
  Beaker,
  Shield,
  Heart,
  Leaf,
  CheckCircle
} from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Collapsible, CollapsibleContent, CollapsibleTrigger } from '@/components/ui/collapsible';

const CropGuideDetail = () => {
  const navigate = useNavigate();
  const { cropId } = useParams();
  const [savedGuides, setSavedGuides] = useState<string[]>([]);
  const [expandedSections, setExpandedSections] = useState<string[]>([]);

  // Load saved guides from localStorage
  useEffect(() => {
    const saved = localStorage.getItem('kisanmitra_saved_cultivation_guides');
    setSavedGuides(saved ? JSON.parse(saved) : []);
  }, []);

  const toggleSaveGuide = (guideId: string) => {
    const updatedSaved = savedGuides.includes(guideId)
      ? savedGuides.filter(id => id !== guideId)
      : [...savedGuides, guideId];

    setSavedGuides(updatedSaved);
    localStorage.setItem('kisanmitra_saved_cultivation_guides', JSON.stringify(updatedSaved));
  };

  const toggleSection = (sectionId: string) => {
    setExpandedSections(prev => 
      prev.includes(sectionId) 
        ? prev.filter(id => id !== sectionId)
        : [...prev, sectionId]
    );
  };

  const cropData = {
    tomato: {
      name: 'Tomato',
      image: '',
      description: 'Complete step-by-step guide for growing healthy tomatoes',
      sections: [
        {
          id: 'variety-seed',
          title: 'Variety & Seed Treatment',
          icon: Sprout,
          points: [
            'Choose disease-resistant varieties like Indeterminate or Determinate types',
            'Treat seeds with fungicide (Thiram 2g/kg) before sowing',
            'Soak seeds in warm water for 6-8 hours for better germination'
          ],
          illustration: ''
        },
        {
          id: 'land-prep',
          title: 'Land Preparation & Sowing',
          icon: Sun,
          points: [
            'Prepare well-drained beds with 15-20cm height',
            'Mix organic compost (5-10 tons/hectare) in soil',
            'Maintain spacing of 60cm x 45cm between plants'
          ],
          illustration: ''
        },
        {
          id: 'fertilization',
          title: 'Fertilization & Nutrient Management',
          icon: Beaker,
          points: [
            'Apply NPK (19:19:19) at 400kg/hectare as basal dose',
            'First top dressing with Urea (50kg/hectare) after 20 days',
            'Apply Calcium Nitrate during flowering to prevent blossom end rot'
          ],
          illustration: ''
        },
        {
          id: 'irrigation',
          title: 'Irrigation Schedule',
          icon: Droplets,
          points: [
            'Install drip irrigation system for water efficiency',
            'Water daily for first 2 weeks, then alternate days',
            'Maintain soil moisture at 70-80% field capacity'
          ],
          illustration: ''
        },
        {
          id: 'support-control',
          title: 'Support/Weed/Disease Control',
          icon: Shield,
          points: [
            'Install bamboo stakes or trellis system for plant support',
            'Manual weeding or use herbicides like Pendimethalin',
            'Apply Copper Oxychloride for early blight prevention'
          ],
          illustration: ''
        }
      ]
    },
    wheat: {
      name: 'Wheat',
      image: '',
      description: 'Comprehensive guide for wheat cultivation and management',
      sections: [
        {
          id: 'variety-seed',
          title: 'Variety & Seed Treatment',
          icon: Sprout,
          points: [
            'Select high-yielding varieties like HD-2967, PBW-343, or DBW-88',
            'Treat seeds with fungicide (Vitavax 2.5g/kg) against seed-borne diseases',
            'Use certified seeds with 90%+ germination rate'
          ],
          illustration: ''
        },
        {
          id: 'land-prep',
          title: 'Land Preparation & Sowing',
          icon: Sun,
          points: [
            'Deep plowing followed by 2-3 cross harrowing for fine tilth',
            'Maintain seed rate of 100-125 kg/hectare for irrigated conditions',
            'Sow at 2-3cm depth with row spacing of 20-22.5cm'
          ],
          illustration: ''
        },
        {
          id: 'fertilization',
          title: 'Fertilization & Nutrient Management',
          icon: Beaker,
          points: [
            'Apply DAP (150kg/hectare) and MOP (50kg/hectare) at sowing',
            'First Urea application (65kg/hectare) at 21 days (Crown Root stage)',
            'Second Urea application (65kg/hectare) at 45 days (Tillering stage)'
          ],
          illustration: ''
        },
        {
          id: 'irrigation',
          title: 'Six-Stage Irrigation Schedule',
          icon: Droplets,
          points: [
            'Crown Root stage (20-25 days) - First irrigation',
            'Tillering stage (40-45 days) - Second irrigation',
            'Jointing stage (60-65 days) - Third irrigation',
            'Flowering stage (80-85 days) - Fourth irrigation',
            'Milk stage (100-105 days) - Fifth irrigation',
            'Dough stage (115-120 days) - Sixth irrigation'
          ],
          illustration: ''
        },
        {
          id: 'weed-control',
          title: 'Weed & Disease Control',
          icon: Shield,
          points: [
            'Use pre-emergence herbicide Pendimethalin @ 1kg/hectare',
            'Post-emergence application of 2,4-D for broad-leaf weeds',
            'Monitor for Yellow Rust and apply Propiconazole if needed'
          ],
          illustration: ''
        }
      ]
    },
    maize: {
      name: 'Maize',
      image: '',
      description: 'Step-by-step maize cultivation guide for optimal yield',
      sections: [
        {
          id: 'land-prep',
          title: 'Land Preparation & Raised Beds',
          icon: Sun,
          points: [
            'Prepare field with deep plowing (20-25cm) followed by harrowing',
            'Create raised beds of 3-4 meter width with proper drainage',
            'Apply farmyard manure (10-15 tons/hectare) during land preparation'
          ],
          illustration: ''
        },
        {
          id: 'seed-treatment',
          title: 'Seed Rate & Treatment',
          icon: Beaker,
          points: [
            'Use 20-25 kg/hectare seed rate for optimal plant population',
            'Treat seeds with fungicide (Thiram @ 2g/kg) and insecticide',
            'Select high-yielding hybrids suitable for local climate conditions'
          ],
          illustration: ''
        },
        {
          id: 'spacing',
          title: 'Spacing & Plant Management',
          icon: Sprout,
          points: [
            'Maintain row-to-row spacing of 60cm and plant-to-plant 20cm',
            'Sow 2-3 seeds per hill and thin to single healthy plant',
            'Ensure plant population of 75,000-80,000 plants/hectare'
          ],
          illustration: ''
        },
        {
          id: 'fertilizer',
          title: 'Trio Fertilizer Schedule',
          icon: Heart,
          points: [
            'Basal dose: NPK (120:60:40 kg/hectare) at sowing',
            'First top dressing: Urea (65kg/hectare) at 25-30 days',
            'Second top dressing: Urea (65kg/hectare) at knee-high stage (45-50 days)'
          ],
          illustration: ''
        },
        {
          id: 'monitoring',
          title: 'Growth Monitoring & Care',
          icon: Leaf,
          points: [
            'Regular monitoring for stem borer and apply appropriate insecticides',
            'Earthing up operation at 30-35 days for better root development',
            'Maintain adequate soil moisture throughout vegetative growth'
          ],
          illustration: ''
        }
      ]
    }
  };

  const currentCrop = cropData[cropId as keyof typeof cropData];

  if (!currentCrop) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-green-50 to-emerald-50 flex items-center justify-center">
        <div className="text-center">
          <h2 className="text-2xl font-bold text-gray-700 mb-4">Guide Not Found</h2>
          <Button onClick={() => navigate('/guides')} className="bg-green-600 hover:bg-green-700">
            Back to Guides
          </Button>
        </div>
      </div>
    );
  }

  const isSaved = savedGuides.includes(cropId!);

  return (
    <div className="min-h-screen bg-gradient-to-br from-green-50 to-emerald-50">
      {/* Header */}
      <div className="bg-gradient-to-r from-green-600 to-emerald-600 px-4 py-4 shadow-lg sticky top-0 z-10">
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-3">
            <button onClick={() => navigate('/guides')} className="text-white hover:bg-white/20 rounded-full p-2">
              <ArrowLeft className="w-6 h-6" />
            </button>
            <div className="flex items-center space-x-3">
              <div className="text-3xl">{currentCrop.image}</div>
              <div>
                <h1 className="text-xl font-bold text-white">{currentCrop.name}</h1>
                <p className="text-green-100 text-sm">{currentCrop.description}</p>
              </div>
            </div>
          </div>
          <button
            onClick={() => toggleSaveGuide(cropId!)}
            className={`p-2 rounded-lg transition-colors ${
              isSaved
                ? 'bg-white/20 text-white'
                : 'bg-white/10 text-white hover:bg-white/20'
            }`}
          >
            {isSaved ? (
              <BookmarkCheck className="w-6 h-6" />
            ) : (
              <Bookmark className="w-6 h-6" />
            )}
          </button>
        </div>
      </div>

      {/* Content */}
      <div className="px-4 py-6 space-y-4">
        {currentCrop.sections.map((section, index) => (
          <Card key={section.id} className="bg-white shadow-lg border border-green-100 overflow-hidden">
            <Collapsible
              open={expandedSections.includes(section.id)}
              onOpenChange={() => toggleSection(section.id)}
            >
              <CollapsibleTrigger asChild>
                <CardHeader className="cursor-pointer hover:bg-green-50 transition-colors">
                  <div className="flex items-center justify-between">
                    <div className="flex items-center space-x-4">
                      <div className="p-3 bg-green-100 rounded-lg">
                        <section.icon className="w-6 h-6 text-green-600" />
                      </div>
                      <div>
                        <CardTitle className="text-lg font-bold text-gray-900">{section.title}</CardTitle>
                        <p className="text-sm text-gray-500">Step {index + 1} of {currentCrop.sections.length}</p>
                      </div>
                    </div>
                    <div className="flex items-center space-x-2">
                      <div className="text-2xl">{section.illustration}</div>
                      {expandedSections.includes(section.id) ? (
                        <ChevronUp className="w-5 h-5 text-gray-400" />
                      ) : (
                        <ChevronDown className="w-5 h-5 text-gray-400" />
                      )}
                    </div>
                  </div>
                </CardHeader>
              </CollapsibleTrigger>
              <CollapsibleContent>
                <CardContent className="pt-0">
                  <div className="space-y-3">
                    {section.points.map((point, pointIndex) => (
                      <div key={pointIndex} className="flex items-start space-x-3 p-3 bg-green-50 rounded-lg">
                        <CheckCircle className="w-5 h-5 text-green-600 mt-0.5 flex-shrink-0" />
                        <p className="text-gray-700 text-sm leading-relaxed">{point}</p>
                      </div>
                    ))}
                  </div>
                </CardContent>
              </CollapsibleContent>
            </Collapsible>
          </Card>
        ))}

        {/* Save Guide Button */}
        <div className="sticky bottom-4 pt-4">
          <Button 
            onClick={() => toggleSaveGuide(cropId!)}
            className={`w-full py-4 rounded-xl shadow-lg transition-all ${
              isSaved
                ? 'bg-green-100 text-green-700 border-2 border-green-600 hover:bg-green-50'
                : 'bg-green-600 text-white hover:bg-green-700'
            }`}
          >
            {isSaved ? (
              <>
                <BookmarkCheck className="w-5 h-5 mr-2" />
                Saved to My Guides
              </>
            ) : (
              <>
                <Bookmark className="w-5 h-5 mr-2" />
                Save This Guide
              </>
            )}
          </Button>
        </div>
      </div>
    </div>
  );
};

export default CropGuideDetail;

src/pages/Diagnose.tsx.content:
import { useState, useRef, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import { 
  Camera, 
  Upload, 
  Mic,
  ChevronDown,
  ChevronUp,
  Sun,
  Eye,
  Shield,
  Sparkles,
  Globe,
  Check,
  Leaf,
  ArrowLeft,
  Volume2
} from 'lucide-react';
import { Button } from '@/components/ui/button';

const PlantDiagnosis = () => {
  const navigate = useNavigate();
  const [selectedImage, setSelectedImage] = useState(null);
  const [isExpanded, setIsExpanded] = useState(false);
  const [currentLanguage, setCurrentLanguage] = useState('English');
  const [isListening, setIsListening] = useState(false);
  const fileInputRef = useRef(null);
  const cameraInputRef = useRef(null);

  const languages = ['English', '', '', '', ''];

  const handleImageUpload = (event) => {
    const file = event.target.files?.[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        setSelectedImage(e.target?.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const toggleVoiceAssist = () => {
    setIsListening(!isListening);
    // Voice assistance logic would go here
  };

  const startDiagnosis = () => {
    if (selectedImage) {
      console.log('Starting diagnosis...');
      // Store the selected image for analysis
      localStorage.setItem('plant_image', selectedImage);
      // Navigate to analysis page
      navigate('/diagnose/analyze');
    }
  };

  const tips = [
    {
      icon: <Sun className="w-5 h-5 text-yellow-500" />,
      title: "Good Lighting",
      description: "Take photos in natural daylight"
    },
    {
      icon: <Eye className="w-5 h-5 text-blue-500" />,
      title: "Focus on Affected Area",
      description: "Zoom in on diseased or damaged parts"
    },
    {
      icon: <Shield className="w-5 h-5 text-green-500" />,
      title: "Avoid Shadows",
      description: "Keep the plant well-lit and clear"
    },
    {
      icon: <Sparkles className="w-5 h-5 text-purple-500" />,
      title: "Clear & Sharp",
      description: "Ensure the image is not blurry"
    }
  ];

  return (
    <div className="min-h-screen bg-gradient-to-br from-green-50 to-blue-50">
      {/* Header */}
      <div className="bg-gradient-to-r from-green-600 to-emerald-600 px-4 py-4 shadow-lg">
        <div className="flex items-center justify-between">
          <Button
            variant="ghost"
            size="sm"
            onClick={() => navigate('/home')}
            className="text-white hover:bg-white/20 rounded-full p-3"
          >
            <ArrowLeft className="w-5 h-5" />
          </Button>

          <div className="flex items-center space-x-3">
            <h1 className="text-xl font-bold text-white flex items-center">
              <Leaf className="w-6 h-6 mr-2" />
              Plant Diagnosis
            </h1>
            <button
              onClick={toggleVoiceAssist}
              className={`p-2 rounded-lg transition-all duration-200 ${
                isListening 
                  ? 'bg-white/30 text-white animate-pulse' 
                  : 'bg-white/20 text-white hover:bg-white/30'
              }`}
            >
              <Volume2 className="w-5 h-5" />
            </button>
          </div>

          <div className="w-12" />
        </div>
      </div>

      <div className="px-4 py-6 space-y-6">
        {/* Main Action Card */}
        <div className="bg-white rounded-3xl shadow-xl p-8 border border-gray-100">
          <div className="text-center mb-8">
            <div className="inline-flex items-center justify-center w-20 h-20 bg-gradient-to-br from-green-100 to-green-200 rounded-2xl mb-6">
              <Camera className="w-10 h-10 text-green-600" />
            </div>
            <h2 className="text-2xl font-bold text-gray-900 mb-3">
              Capture Plant Image
            </h2>
            <p className="text-gray-600 text-base leading-relaxed">
              Take a clear photo or upload from your gallery
            </p>
          </div>

          {selectedImage && (
            <div className="mb-8">
              <div className="relative rounded-2xl overflow-hidden bg-gray-100 border-4 border-green-200">
                <img
                  src={selectedImage}
                  alt="Selected plant"
                  className="w-full h-64 object-cover"
                />
                <div className="absolute top-4 right-4 bg-green-500 rounded-xl p-2">
                  <Check className="w-5 h-5 text-white" />
                </div>
              </div>
              <p className="text-center text-base text-green-600 mt-4 font-bold">
                 Image ready for diagnosis
              </p>
            </div>
          )}

          <div className="grid grid-cols-1 gap-6">
            {/* Take Photo Button */}
            <button
              onClick={() => cameraInputRef.current?.click()}
              className="flex items-center justify-center space-x-4 bg-gradient-to-r from-green-500 to-green-600 hover:from-green-600 hover:to-green-700 text-white p-6 rounded-2xl transition-all duration-300 shadow-lg hover:shadow-xl transform hover:scale-[1.02]"
            >
              <Camera className="w-7 h-7" />
              <span className="font-bold text-xl">Take a Photo</span>
            </button>

            {/* Upload from Gallery Button */}
            <button
              onClick={() => fileInputRef.current?.click()}
              className="flex items-center justify-center space-x-4 bg-white border-3 border-green-500 text-green-600 hover:bg-green-50 p-6 rounded-2xl transition-all duration-300 shadow-md hover:shadow-lg transform hover:scale-[1.02]"
            >
              <Upload className="w-7 h-7" />
              <span className="font-bold text-xl">Upload from Gallery</span>
            </button>
          </div>

          {/* Hidden file inputs */}
          <input
            ref={fileInputRef}
            type="file"
            accept="image/*"
            onChange={handleImageUpload}
            className="hidden"
          />
          <input
            ref={cameraInputRef}
            type="file"
            accept="image/*"
            capture="environment"
            onChange={handleImageUpload}
            className="hidden"
          />
        </div>

        {/* Photography Tips Card */}
        <div className="bg-white rounded-3xl shadow-xl border border-gray-100 overflow-hidden">
          <button
            onClick={() => setIsExpanded(!isExpanded)}
            className="w-full px-8 py-6 flex items-center justify-between bg-gradient-to-r from-blue-50 to-purple-50 hover:from-blue-100 hover:to-purple-100 transition-colors duration-200"
          >
            <div className="flex items-center space-x-4">
              <div className="p-3 bg-white rounded-xl shadow-sm">
                <Sparkles className="w-6 h-6 text-blue-600" />
              </div>
              <div className="text-left">
                <h3 className="font-bold text-gray-900 text-lg">Photography Tips</h3>
                <p className="text-base text-gray-600">Get better diagnosis results</p>
              </div>
            </div>
            {isExpanded ? (
              <ChevronUp className="w-6 h-6 text-gray-500" />
            ) : (
              <ChevronDown className="w-6 h-6 text-gray-500" />
            )}
          </button>

          {isExpanded && (
            <div className="px-8 py-6 space-y-6 bg-gray-50">
              {tips.map((tip, index) => (
                <div key={index} className="flex items-start space-x-4">
                  <div className="mt-1">
                    {tip.icon}
                  </div>
                  <div>
                    <h4 className="font-bold text-gray-900 text-base">
                      {tip.title}
                    </h4>
                    <p className="text-gray-600 text-sm mt-2 leading-relaxed">
                      {tip.description}
                    </p>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>

      </div>

      {/* Fixed Bottom Button */}
      <div className="fixed bottom-0 left-0 right-0 bg-white border-t-2 border-gray-200 p-6 shadow-2xl">
        <button
          onClick={startDiagnosis}
          disabled={!selectedImage}
          className={`w-full py-5 rounded-2xl font-bold text-xl transition-all duration-300 ${
            selectedImage
              ? 'bg-gradient-to-r from-green-500 to-blue-500 hover:from-green-600 hover:to-blue-600 text-white shadow-xl hover:shadow-2xl transform hover:scale-[1.02]'
              : 'bg-gray-300 text-gray-500 cursor-not-allowed'
          }`}
        >
          {selectedImage ? (
            <div className="flex items-center justify-center space-x-3">
              <Sparkles className="w-6 h-6" />
              <span>Start Diagnosis</span>
            </div>
          ) : (
            'Select an Image First'
          )}
        </button>
      </div>

      {/* Voice Assistant Feedback */}
      {isListening && (
        <div className="fixed top-20 left-4 right-4 bg-blue-600 text-white p-4 rounded-xl shadow-lg z-50">
          <div className="flex items-center space-x-3">
            <div className="w-3 h-3 bg-white rounded-full animate-pulse"></div>
            <span className="font-medium">Listening... Speak your question</span>
          </div>
        </div>
      )}
    </div>
  );
};

export default PlantDiagnosis;

src/pages/DiagnoseResults.tsx.content:
import { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { 
  ArrowLeft, 
  CheckCircle,
  AlertTriangle,
  Clock,
  Target,
  TrendingUp,
  Store,
  MapPin,
  Plus,
  Leaf,
  Calendar,
  Award,
  ShoppingCart,
  Loader2
} from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Progress } from '@/components/ui/progress';
import { toast } from '@/components/ui/use-toast';

interface DiagnosisResult {
  disease: string;
  confidence: number;
  affectedArea: string;
  stage: string;
  severity: 'low' | 'medium' | 'high';
  detectedDate: string;
  status?: string;
  isHealthy?: boolean;
}

interface Fertilizer {
  name: string;
  price: string;
  availability: string;
}

interface TreatmentStep {
  step: number;
  title: string;
  description: string;
}

const DiagnoseResults = () => {
  const navigate = useNavigate();
  const [results, setResults] = useState<DiagnosisResult | null>(null);
  const [plantImage, setPlantImage] = useState<string | null>(null);
  const [fertilizers, setFertilizers] = useState<Fertilizer[]>([]);
  const [treatmentSteps, setTreatmentSteps] = useState<TreatmentStep[]>([]);
  const [treatmentDuration, setTreatmentDuration] = useState<string>('14-21 days');
  const [successRate, setSuccessRate] = useState<number>(87);
  const [loadingFertilizers, setLoadingFertilizers] = useState(false);
  const [loadingSteps, setLoadingSteps] = useState(false);
  const [loadingDuration, setLoadingDuration] = useState(false);

  useEffect(() => {
    const savedResults = localStorage.getItem('diagnosis_results');
    const savedImage = localStorage.getItem('plant_image');

    if (savedResults) {
      const parsedResults = JSON.parse(savedResults);
      setResults(parsedResults);

      // Load Gemini AI data for the diagnosed disease
      if (parsedResults.disease && !parsedResults.isHealthy) {
        loadFertilizerRecommendations(parsedResults.disease);
        loadTreatmentSteps(parsedResults.disease);
        loadTreatmentDuration(parsedResults.disease);
      }
    }
    if (savedImage) {
      setPlantImage(savedImage);
    }
  }, []);

  const loadFertilizerRecommendations = async (diseaseName: string) => {
    setLoadingFertilizers(true);
    try {
      const response = await fetch('/api/treatment/fertilizers', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ disease: diseaseName }),
      });

      if (response.ok) {
        const data = await response.json();
        if (data.success && data.fertilizers) {
          setFertilizers(data.fertilizers);
        }
      }
    } catch (error) {
      console.error('Error loading fertilizers:', error);
      // Use fallback fertilizers
      setFertilizers([
        { name: 'Copper Fungicide Spray', price: '450', availability: 'In Stock' },
        { name: 'Organic Disease Control', price: '320', availability: 'In Stock' },
        { name: 'Plant Immunity Booster', price: '280', availability: 'Out of Stock' }
      ]);
    } finally {
      setLoadingFertilizers(false);
    }
  };

  const loadTreatmentSteps = async (diseaseName: string) => {
    setLoadingSteps(true);
    try {
      const response = await fetch('/api/treatment/steps', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ disease: diseaseName }),
      });

      if (response.ok) {
        const data = await response.json();
        if (data.success && data.steps) {
          setTreatmentSteps(data.steps);
        }
      }
    } catch (error) {
      console.error('Error loading treatment steps:', error);
      // Use fallback steps
      setTreatmentSteps([
        { step: 1, title: 'Remove Affected Parts', description: 'Carefully remove all affected leaves and stems. Dispose away from healthy plants.' },
        { step: 2, title: 'Apply Treatment', description: 'Apply appropriate fungicide or treatment as recommended. Follow label instructions.' },
        { step: 3, title: 'Improve Conditions', description: 'Improve air circulation and avoid overhead watering to prevent reinfection.' },
        { step: 4, title: 'Monitor Progress', description: 'Check daily for new symptoms. Recovery should begin within 5-7 days.' },
        { step: 5, title: 'Follow-up Care', description: 'Continue monitoring and apply follow-up treatments as needed.' }
      ]);
    } finally {
      setLoadingSteps(false);
    }
  };

  const loadTreatmentDuration = async (diseaseName: string) => {
    setLoadingDuration(true);
    try {
      const response = await fetch('/api/treatment/duration', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ disease: diseaseName }),
      });

      if (response.ok) {
        const data = await response.json();
        if (data.success) {
          setTreatmentDuration(data.duration || '14-21 days');
          setSuccessRate(data.success_rate || 87);
        }
      }
    } catch (error) {
      console.error('Error loading treatment duration:', error);
      // Keep fallback values
    } finally {
      setLoadingDuration(false);
    }
  };

  const addToActiveTreatments = () => {
    if (results) {
      const treatment = {
        id: Date.now().toString(),
        disease: results.disease,
        startDate: new Date().toISOString(),
        status: 'active',
        progress: 0,
        duration: treatmentDuration,
        successRate: successRate,
        nextAction: treatmentSteps.length > 0 ? treatmentSteps[0].title : 'Remove affected parts and apply treatment',
        image: plantImage,
        severity: results.severity,
        confidence: results.confidence,
        fertilizers: fertilizers,
        steps: treatmentSteps.map((step, index) => ({
          step: step.step,
          title: step.title,
          description: step.description,
          completed: false,
          dueDate: new Date(Date.now() + index * 24 * 60 * 60 * 1000).toISOString()
        }))
      };

      const existingTreatments = JSON.parse(localStorage.getItem('active_treatments') || '[]');
      existingTreatments.push(treatment);
      localStorage.setItem('active_treatments', JSON.stringify(existingTreatments));

      toast({
        title: " Treatment Saved!",
        description: "Added to your active treatments with AI-powered guidance and fertilizer recommendations."
      });

      // No automatic navigation - user stays on results page
    }
  };

  const handleFertilizerRecommendation = (fertilizerName: string) => {
    // Store fertilizer search query and navigate to shop
    localStorage.setItem('fertilizer_search', fertilizerName);
    navigate('/shop?search=' + encodeURIComponent(fertilizerName));
  };

  const getStepColor = (stepNumber: number) => {
    const colors = ['bg-green-500', 'bg-blue-500', 'bg-purple-500', 'bg-orange-500', 'bg-pink-500'];
    return colors[(stepNumber - 1) % colors.length];
  };

  if (!results) {
    return (
      <div className="mobile-container">
        <div className="mobile-content flex items-center justify-center min-h-[50vh]">
          <div className="text-center">
            <AlertTriangle className="w-16 h-16 text-agri-warning mx-auto mb-4" />
            <h2 className="text-xl font-bold text-agri-accent mb-2">No Results Found</h2>
            <p className="text-agri-gray mb-4">Please diagnose a plant first.</p>
            <Button onClick={() => navigate('/diagnose')}>
              Start Diagnosis
            </Button>
          </div>
        </div>
      </div>
    );
  }

  const getSeverityColor = (severity: string) => {
    switch (severity) {
      case 'low': return 'text-green-600 bg-green-100';
      case 'medium': return 'text-yellow-600 bg-yellow-100';
      case 'high': return 'text-red-600 bg-red-100';
      default: return 'text-gray-600 bg-gray-100';
    }
  };

  return (
    <div className="mobile-container">
      {/* Header */}
      <div className="mobile-header bg-agri-cream">
        <div className="flex items-center space-x-3">
          <button 
            onClick={() => navigate('/diagnose')}
            className="p-2 hover:bg-agri-light rounded-full transition-colors"
          >
            <ArrowLeft className="w-6 h-6 text-agri-accent" />
          </button>
          <h1 className="text-xl font-bold text-agri-accent">Diagnosis Results</h1>
        </div>
      </div>

      <div className="mobile-content space-y-6 pt-4">
        {/* Success Header */}
        <div className="text-center">
          <div className="w-16 h-16 bg-agri-success/20 rounded-full flex items-center justify-center mx-auto mb-4">
            <CheckCircle className="w-8 h-8 text-agri-success animate-bounce" />
          </div>
          <h1 className="text-2xl font-bold text-agri-accent mb-2">Diagnosis Complete</h1>
          <p className="text-agri-gray">
            Analyzed on {new Date(results.detectedDate).toLocaleDateString()}
          </p>
        </div>

        {/* Plant Image */}
        {plantImage && (
          <div className="relative rounded-3xl overflow-hidden shadow-2xl border border-gray-200">
            <img 
              src={plantImage} 
              alt="Diagnosed plant" 
              className="w-full aspect-[4/3] object-cover"
            />
            <div className="absolute inset-0 bg-gradient-to-t from-black/30 via-transparent to-transparent" />
            <div className="absolute top-4 right-4 bg-green-500 rounded-full p-3 shadow-lg">
              <Leaf className="w-6 h-6 text-white" />
            </div>
            <div className="absolute bottom-4 left-4 text-white">
              <div className="text-sm font-medium opacity-90">Analyzed Image</div>
              <div className="text-xs opacity-70">{new Date().toLocaleDateString()}</div>
            </div>
          </div>
        )}

        {/* Disease Detection */}
        <div className={`plant-card border-l-4 ${results.isHealthy ? 'border-green-500' : 'border-red-500'}`}>
          <div className="flex items-start justify-between mb-4">
            <div className="flex-1">
              <h2 className={`text-xl font-bold mb-2 ${results.isHealthy ? 'text-green-700' : 'text-red-700'}`}>
                {results.disease}
              </h2>
              <div className="flex items-center space-x-3 mb-3">
                <div className="flex items-center space-x-2">
                  <Progress value={100} className="w-20 h-2" />
                  <span className={`text-sm font-medium ${results.isHealthy ? 'text-green-600' : 'text-red-600'}`}>
                    100% Confidence
                  </span>
                </div>
                <span className={`px-2 py-1 rounded-full text-xs font-medium ${
                  results.isHealthy ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800'
                }`}>
                  {results.isHealthy ? 'HEALTHY' : 'NEEDS ATTENTION'}
                </span>
              </div>

              <div className="grid grid-cols-2 gap-4 text-sm">
                <div>
                  <span className="font-medium text-agri-accent">Affected Area:</span>
                  <p className="text-agri-gray">{results.affectedArea}</p>
                </div>
                <div>
                  <span className="font-medium text-agri-accent">Stage:</span>
                  <p className="text-agri-gray">{results.stage}</p>
                </div>
              </div>
            </div>
            <AlertTriangle className={`w-8 h-8 ${results.isHealthy ? 'text-green-500' : 'text-red-500'}`} />
          </div>
        </div>

        {/* Treatment Timeline */}
        <div className="bg-blue-50 rounded-2xl p-6 border border-blue-200">
          <div className="flex items-center space-x-3 mb-4">
            {loadingDuration ? (
              <Loader2 className="w-6 h-6 text-blue-600 animate-spin" />
            ) : (
              <Clock className="w-6 h-6 text-blue-600" />
            )}
            <h3 className="font-bold text-blue-900">Treatment Timeline</h3>
            {loadingDuration && <span className="text-xs text-blue-600">Loading AI insights...</span>}
          </div>

          <div className="space-y-3">
            <div className="flex items-center justify-between">
              <span className="text-blue-800">Recovery Duration:</span>
              <span className="font-semibold text-blue-900">{treatmentDuration}</span>
            </div>
            <div className="flex items-center justify-between">
              <span className="text-blue-800">Success Rate:</span>
              <span className="font-semibold text-agri-success">{successRate}% farmers recovered</span>
            </div>
            <div className="flex items-center justify-between">
              <span className="text-blue-800">Best Treatment Time:</span>
              <span className="font-semibold text-blue-900">Early morning</span>
            </div>
          </div>

          <Progress value={successRate} className="w-full h-2 mt-4" />
        </div>

        {/* Treatment Steps */}
        <div className="bg-gradient-to-r from-green-50 to-blue-50 rounded-3xl p-6 border border-green-200">
          <div className="flex items-center space-x-3 mb-6">
            <div className="bg-green-500 rounded-full p-3">
              {loadingSteps ? (
                <Loader2 className="w-6 h-6 text-white animate-spin" />
              ) : (
                <Target className="w-6 h-6 text-white" />
              )}
            </div>
            <div>
              <h3 className="font-bold text-gray-900 text-lg">Treatment Steps</h3>
              <p className="text-sm text-gray-600">
                {loadingSteps ? 'Loading AI-powered treatment plan...' : 'AI-generated steps for best results'}
              </p>
            </div>
          </div>

          <div className="space-y-4">
            {loadingSteps ? (
              <div className="flex items-center justify-center py-8">
                <Loader2 className="w-8 h-8 text-green-500 animate-spin" />
                <span className="ml-2 text-gray-600">Generating personalized treatment steps...</span>
              </div>
            ) : (
              treatmentSteps.map((step) => (
                <div key={step.step} className="flex items-start space-x-4 p-4 bg-white rounded-2xl shadow-sm">
                  <div className={`${getStepColor(step.step)} text-white rounded-full p-2 text-sm font-bold flex-shrink-0`}>
                    {step.step}
                  </div>
                  <div>
                    <h4 className="font-semibold text-gray-900 mb-1">{step.title}</h4>
                    <p className="text-sm text-gray-600">{step.description}</p>
                  </div>
                </div>
              ))
            )}
          </div>
        </div>

        {/* Add to Active Treatments */}
        <div className="bg-gradient-to-r from-green-500 to-green-600 rounded-3xl p-6 text-white shadow-xl">
          <div className="flex items-center justify-between mb-4">
            <div>
              <h3 className="font-bold text-white text-lg mb-2">Save Treatment Plan</h3>
              <p className="text-green-100 text-sm">Track progress with daily reminders and monitoring</p>
            </div>
            <Calendar className="w-10 h-10 text-green-200" />
          </div>

          <div className="grid grid-cols-2 gap-4 mb-6 text-sm">
            <div className="bg-white/20 rounded-2xl p-3">
              <div className="font-semibold mb-1">Duration</div>
              <div className="text-green-100">{treatmentDuration}</div>
            </div>
            <div className="bg-white/20 rounded-2xl p-3">
              <div className="font-semibold mb-1">Success Rate</div>
              <div className="text-green-100">{successRate}% recovery</div>
            </div>
          </div>

          <Button 
            onClick={addToActiveTreatments}
            className="w-full bg-white text-green-600 hover:bg-green-50 font-semibold py-3 rounded-2xl transition-all duration-300 transform hover:scale-105"
          >
            <Plus className="w-5 h-5 mr-2" />
            Save to My Treatments
          </Button>
        </div>

        {/* Fertilizer Recommendations */}
        <div className="space-y-4">
          <div className="flex items-center space-x-3">
            <h2 className="text-xl font-bold text-agri-accent">Recommended Fertilizers</h2>
            {loadingFertilizers && (
              <Loader2 className="w-5 h-5 text-agri-accent animate-spin" />
            )}
          </div>
          {loadingFertilizers && (
            <p className="text-sm text-gray-600">Getting AI-powered fertilizer recommendations...</p>
          )}

          {loadingFertilizers ? (
            <div className="flex items-center justify-center py-8">
              <Loader2 className="w-8 h-8 text-green-500 animate-spin" />
              <span className="ml-2 text-gray-600">Loading fertilizer recommendations...</span>
            </div>
          ) : (
            fertilizers.map((fertilizer, index) => (
              <div key={index} className="plant-card">
                <div className="flex items-center justify-between mb-3">
                  <div className="flex-1">
                    <h3 className="font-semibold text-agri-accent">{fertilizer.name}</h3>
                    <p className="text-lg font-bold text-agri-primary">{fertilizer.price}</p>
                  </div>
                  <div className="text-right">
                    <span className={`px-2 py-1 rounded-full text-xs font-medium ${
                      fertilizer.availability === 'In Stock' ? 'bg-green-100 text-green-600' : 'bg-red-100 text-red-600'
                    }`}>
                      {fertilizer.availability}
                    </span>
                  </div>
                </div>

                <Button 
                  onClick={() => handleFertilizerRecommendation(fertilizer.name)}
                  disabled={fertilizer.availability !== 'In Stock'}
                  className="w-full"
                  variant={fertilizer.availability === 'In Stock' ? "default" : "outline"}
                >
                  <ShoppingCart className="w-4 h-4 mr-2" />
                  {fertilizer.availability === 'In Stock' ? 'Order Now' : 'Check Alternatives'}
                </Button>
              </div>
            ))
          )}
        </div>

        {/* Alternative Options */}
        <div className="space-y-4">
          <h2 className="text-xl font-bold text-agri-accent">Alternative Options</h2>

          {/* Nearby Shops */}
          <div className="plant-card bg-gradient-to-r from-purple-500 to-purple-600 text-white">
            <div className="flex items-start justify-between mb-4">
              <div>
                <h3 className="text-lg font-bold mb-2">Visit Nearby Fertilizer Shops</h3>
                <p className="text-white/90 text-sm mb-4">Get directions and contact details</p>

                <div className="space-y-1 text-sm">
                  <div className="flex items-center space-x-2">
                    <CheckCircle className="w-4 h-4" />
                    <span>3 shops within 5km</span>
                  </div>
                  <div className="flex items-center space-x-2">
                    <CheckCircle className="w-4 h-4" />
                    <span>GPS navigation available</span>
                  </div>
                  <div className="flex items-center space-x-2">
                    <CheckCircle className="w-4 h-4" />
                    <span>Expert advice included</span>
                  </div>
                </div>
              </div>
              <MapPin className="w-8 h-8 opacity-80" />
            </div>

            <Button 
              onClick={() => navigate('/nearby-shops')}
              className="w-full bg-white text-purple-600 hover:bg-white/90 font-semibold"
            >
              <MapPin className="w-4 h-4 mr-2" />
              Find Nearest Shops
            </Button>
          </div>

        </div>

        {/* Action Summary */}
        <div className="bg-agri-cream rounded-xl p-4 border border-agri-primary/20">
          <h3 className="font-semibold text-agri-accent mb-2">Next Steps Summary:</h3>
          <ol className="space-y-1 text-sm text-agri-gray">
            <li>1. Add to active treatments for monitoring</li>
            <li>2. Order recommended fertilizers</li>
            <li>3. Apply treatment in early morning</li>
            <li>4. Monitor progress daily</li>
            <li>5. Expect recovery in {treatmentDuration}</li>
          </ol>
        </div>
      </div>
    </div>
  );
};

export default DiagnoseResults;

