import { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { 
  Search, 
  Filter, 
  TrendingUp, 
  TrendingDown, 
  Bell, 
  ArrowLeft,
  MapPin,
  Clock,
  RefreshCw,
  Plus
} from 'lucide-react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { marketData, getTopHighestPricedCrops } from '@/lib/marketData';
import { useNotifications } from '@/hooks/useNotifications';
import { useGeolocation, calculateDistance, formatDistance } from '@/hooks/useGeolocation';

const MarketPrices = () => {
  const navigate = useNavigate();
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedFilter, setSelectedFilter] = useState('all');
  const [lastUpdated, setLastUpdated] = useState(new Date());
  const [isRefreshing, setIsRefreshing] = useState(false);
  const { isNotificationEnabled } = useNotifications();
  const { location, error: locationError, loading: locationLoading } = useGeolocation();

  // Using shared market data with complete vegetable lists

  // Calculate distances for all markets based on user location
  const marketsWithDistance = marketData.map(market => ({
    ...market,
    distance: location 
      ? formatDistance(calculateDistance(location.latitude, location.longitude, market.latitude, market.longitude))
      : locationError 
      ? 'Location unavailable'
      : locationLoading 
      ? 'Calculating...'
      : 'Enable location'
  }));

  const filteredMarkets = marketsWithDistance.filter(market => 
    market.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
    market.crops.some(crop => crop.name.toLowerCase().includes(searchQuery.toLowerCase()))
  );

  const handleRefresh = async () => {
    setIsRefreshing(true);
    // Simulate API call
    await new Promise(resolve => setTimeout(resolve, 1500));
    setLastUpdated(new Date());
    setIsRefreshing(false);
  };

  const getTrendIcon = (trend: string) => {
    switch (trend) {
      case 'up':
        return <TrendingUp className="w-4 h-4 text-green-600" />;
      case 'down':
        return <TrendingDown className="w-4 h-4 text-red-600" />;
      default:
        return <div className="w-4 h-4 rounded-full bg-gray-400" />;
    }
  };

  const getTrendColor = (trend: string) => {
    switch (trend) {
      case 'up':
        return 'text-green-600';
      case 'down':
        return 'text-red-600';
      default:
        return 'text-gray-600';
    }
  };

  // Using shared helper functions from marketData

  return (
    <div className="min-h-screen bg-gradient-to-br from-green-50 to-blue-50">
      {/* Header */}
      <div className="bg-white shadow-sm border-b border-green-100 sticky top-0 z-20">
        <div className="px-6 py-4">
          <div className="flex items-center space-x-4 mb-4">
            <Button
              variant="ghost"
              size="sm"
              onClick={() => navigate(-1)}
              className="p-2 hover:bg-gray-100 rounded-full"
            >
              <ArrowLeft className="w-5 h-5 text-gray-600" />
            </Button>
            <div className="flex-1">
              <h1 className="text-2xl font-bold text-gray-900">Market Prices</h1>
              <div className="flex items-center space-x-2 text-sm text-gray-600">
                <Clock className="w-4 h-4" />
                <span>Last updated: {lastUpdated.toLocaleTimeString()}</span>
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={handleRefresh}
                  disabled={isRefreshing}
                  className="p-1 hover:bg-gray-100 rounded-full"
                >
                  <RefreshCw className={`w-4 h-4 text-green-600 ${isRefreshing ? 'animate-spin' : ''}`} />
                </Button>
              </div>
            </div>
          </div>

          {/* Search and Filter */}
          <div className="flex items-center space-x-3">
            <div className="flex-1 relative">
              <Search className="w-5 h-5 text-gray-400 absolute left-3 top-1/2 transform -translate-y-1/2" />
              <Input
                placeholder="Search crops or markets..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                className="pl-10 pr-4 py-3 rounded-xl border-2 border-gray-200 focus:border-green-500 bg-white"
              />
            </div>
            <Button
              variant="outline"
              size="sm"
              className="px-4 py-3 rounded-xl border-2 border-gray-200 hover:border-green-500"
            >
              <Filter className="w-4 h-4 text-gray-600" />
            </Button>
          </div>
        </div>
      </div>

      {/* Content */}
      <div className="px-6 py-6 space-y-6">
        {/* Market Cards */}
        {filteredMarkets.map((market) => (
          <Card key={market.id} className="bg-white rounded-3xl shadow-lg border border-gray-100 overflow-hidden">
            {/* Market Header */}
            <CardHeader className="bg-gradient-to-r from-green-50 to-blue-50 border-b border-gray-200 p-5">
              <div className="flex items-center justify-between">
                <div className="flex items-center space-x-4">
                  <div className="p-3 bg-white rounded-xl shadow-sm">
                    <MapPin className="w-6 h-6 text-green-600" />
                  </div>
                  <div>
                    <CardTitle className="text-lg font-bold text-gray-900">{market.name}</CardTitle>
                    <div className="flex items-center space-x-2 text-sm text-gray-600">
                      <span>{market.location}</span>
                      <span></span>
                      <span className="bg-blue-100 text-blue-700 px-2 py-1 rounded-full text-xs font-medium">
                        {market.distance}
                      </span>
                      <span></span>
                      <Bell className={`w-4 h-4 ${isNotificationEnabled(market.id.toString()) ? 'text-green-600' : 'text-gray-400'}`} />
                    </div>
                  </div>
                </div>
              </div>
            </CardHeader>

            {/* Crops List - Top 5 */}
            <CardContent className="p-0">
              <div className="space-y-0">
                {getTopHighestPricedCrops(market.crops).map((crop, index) => (
                  <div key={index} className="flex items-center justify-between p-5 border-b border-gray-100 last:border-b-0 hover:bg-gray-50 transition-colors">
                    <div className="flex-1">
                      <h4 className="font-semibold text-gray-900 text-base">{crop.name}</h4>
                      <p className="text-sm text-gray-500">Previous: {crop.previousPrice}</p>
                    </div>
                    <div className="flex items-center space-x-4">
                      <div className="text-right">
                        <div className="font-bold text-lg text-gray-900">{crop.price}</div>
                        <div className={`text-sm font-medium flex items-center space-x-1 ${getTrendColor(crop.trend)}`}>
                          {getTrendIcon(crop.trend)}
                          <span>{crop.change}</span>
                        </div>
                      </div>
                    </div>
                  </div>
                ))}
                {/* More Info Button */}
                <div className="p-4 border-t border-gray-200 bg-gray-50">
                  <Button
                    variant="outline"
                    size="sm"
                    className="w-full"
                    onClick={() => navigate(`/market-details/${market.id}`)}
                  >
                    More Info
                  </Button>
                </div>
              </div>
            </CardContent>
          </Card>
        ))}

        {/* No Results */}
        {filteredMarkets.length === 0 && (
          <div className="text-center py-12">
            <div className="p-4 bg-gray-100 rounded-full inline-block mb-4">
              <Search className="w-8 h-8 text-gray-400" />
            </div>
            <h3 className="text-lg font-medium text-gray-600 mb-2">No markets found</h3>
            <p className="text-gray-500">Try adjusting your search terms</p>
          </div>
        )}
      </div>


      {/* Pull to refresh hint (for mobile) */}
      <div className="pb-20"> {/* Extra padding for mobile navigation */}
        <div className="text-center py-4 text-gray-500 text-sm">
          Pull down to refresh prices
        </div>
      </div>
    </div>
  );
};

export default MarketPrices;

src/pages/Mitra.tsx.content:
import { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import { ArrowLeft, Send } from 'lucide-react';

interface Message {
  id: number;
  text: string;
  isUser: boolean;
}

const Mitra = () => {
  const navigate = useNavigate();
  const [messages, setMessages] = useState<Message[]>([]);
  const [inputMessage, setInputMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const messagesEndRef = useRef<HTMLDivElement>(null);
  const [messageIdCounter, setMessageIdCounter] = useState(1);

  // Get API key from environment
  const API_KEY = import.meta.env.VITE_GEMINI_API_KEY || "AIzaSyCzOBeYUKB6MsPt3bKkUZ0vS8_4LDYmf4Y";

  // Scroll to bottom when messages change
  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  // Initial welcome message
  useEffect(() => {
    const welcomeMessage: Message = {
      id: 0,
      text: "Hello! I'm Kisan Mitra, your digital assistant for farming. How can I help you today?",
      isUser: false
    };
    setMessages([welcomeMessage]);
  }, []);

  const sendMessage = async () => {
    if (!inputMessage.trim()) return;

    // Add user message
    const userMessage: Message = {
      id: messageIdCounter,
      text: inputMessage,
      isUser: true
    };

    setMessages(prev => [...prev, userMessage]);
    setMessageIdCounter(prev => prev + 1);
    const currentMessage = inputMessage;
    setInputMessage('');
    setIsLoading(true);

    const apiUrl = `https://generativelanguage.googleapis.com/v1beta/models/gemini-2.5-flash-preview-05-20:generateContent?key=${API_KEY}`;
    const systemPrompt = "You are Hariyali Mitra, an expert AI farming assistant for Indian farmers using the KisanMitra agricultural support app. You help farmers with: crop cultivation, plant disease, soil health, weather, pest control, market prices, and agricultural best practices for Indian conditions. Speak in simple, everyday language and be supportive. Provide actionable, practical advice relevant to Indian farming.";

    try {
      const response = await fetch(apiUrl, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          contents: [{
            role: "user",
            parts: [{ text: currentMessage }]
          }],
          tools: [{ "google_search": {} }],
          systemInstruction: {
            parts: [{ text: systemPrompt }]
          },
        }),
      });

      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }

      const result = await response.json();
      const botResponse = result.candidates[0].content.parts[0].text;

      // Add bot response
      const botMessage: Message = {
        id: messageIdCounter + 1,
        text: botResponse,
        isUser: false
      };

      setMessages(prev => [...prev, botMessage]);
      setMessageIdCounter(prev => prev + 2);

    } catch (error) {
      console.error('Error sending message:', error);

      // Add error message
      const errorMessage: Message = {
        id: messageIdCounter + 1,
        text: "I am having trouble connecting to the network. Please check your internet connection.",
        isUser: false
      };

      setMessages(prev => [...prev, errorMessage]);
      setMessageIdCounter(prev => prev + 2);
    } finally {
      setIsLoading(false);
    }
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      sendMessage();
    }
  };

  return (
    <div className="bg-gray-100 min-h-screen flex items-center justify-center p-4">
      <div className="bg-white rounded-2xl shadow-xl w-full max-w-md h-[80vh] flex flex-col overflow-hidden">

        {/* Chat Header */}
        <div className="bg-green-600 text-white p-4 flex items-center justify-between rounded-t-2xl shadow-md">
          <button
            onClick={() => navigate(-1)}
            className="text-white hover:bg-white/20 rounded-full p-2 transition-colors"
          >
            <ArrowLeft className="w-5 h-5" />
          </button>
          <h1 className="text-xl font-bold">Kisan Mitra</h1>
          <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="currentColor" className="w-6 h-6">
            <path fillRule="evenodd" d="M12 2.25c-5.385 0-9.75 4.365-9.75 9.75s4.365 9.75 9.75 9.75 9.75-4.365 9.75-9.75S17.385 2.25 12 2.25Zm-3.25 9a.75.75 0 0 0 0 1.5h6.5a.75.75 0 0 0 0-1.5h-6.5Z" clipRule="evenodd" />
          </svg>
        </div>

        {/* Chat Messages Container */}
        <div className="flex-1 overflow-y-auto p-4 space-y-4">
          {messages.map((message) => (
            <div key={message.id} className={`flex ${message.isUser ? 'justify-end' : 'justify-start'}`}>
              <div className={`${
                message.isUser 
                  ? 'bg-green-500 text-white' 
                  : 'bg-gray-200 text-gray-800'
              } p-3 rounded-xl max-w-[80%] shadow-sm`}>
                {message.text}
              </div>
            </div>
          ))}

          {isLoading && (
            <div className="flex justify-start">
              <div className="bg-gray-200 text-gray-800 p-3 rounded-xl shadow-sm animate-pulse">
                Typing...
              </div>
            </div>
          )}
          <div ref={messagesEndRef} />
        </div>

        {/* Chat Input and Send Button */}
        <div className="p-4 border-t border-gray-200 flex items-center space-x-2">
          <input 
            type="text" 
            placeholder="Type your message here..." 
            value={inputMessage}
            onChange={(e) => setInputMessage(e.target.value)}
            onKeyUp={handleKeyPress}
            disabled={isLoading}
            className="flex-1 p-3 rounded-full border border-gray-300 focus:outline-none focus:ring-2 focus:ring-green-500 transition-all duration-200"
          />
          <button 
            onClick={sendMessage}
            disabled={isLoading || !inputMessage.trim()}
            className="bg-green-600 text-white p-3 rounded-full shadow-lg hover:bg-green-700 transition-colors duration-200 disabled:opacity-50"
          >
            <Send className="w-6 h-6 rotate-90" />
          </button>
        </div>

      </div>
    </div>
  );
};

export default Mitra;

