src/pages/Onboarding.tsx.content:
import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { ChevronLeft, ChevronRight, Sparkles, Star } from 'lucide-react';
import { Button } from '@/components/ui/button';
import onboarding1 from '@/assets/onboarding-1.png';
import onboarding2 from '@/assets/onboarding-2.png';
import onboarding3 from '@/assets/onboarding-3.png';

const onboardingData = [
  {
    id: 1,
    image: onboarding1,
    title: "Welcome to Smart Farming Revolution",
    subtitle: "Diagnose Plant Diseases with AI Magic",
    description: "Simply capture a photo and get instant expert plant health analysis",
    color: "from-green-400 to-emerald-600",
  },
  {
    id: 2,
    image: onboarding2,
    title: "Your Digital Agricultural Marketplace",
    subtitle: "Buy Smart, Sell Smarter",
    description: "Access premium fertilizers, quality seeds, and connect with buyers",
    color: "from-blue-400 to-indigo-600",
  },
  {
    id: 3,
    image: onboarding3,
    title: "Personalized Farming Intelligence",
    subtitle: "Weather Wisdom & Expert Guidance",
    description: "Get hyper-local weather updates and AI-powered farming recommendations",
    color: "from-purple-400 to-pink-600",
  },
];

const Onboarding = () => {
  const [currentStep, setCurrentStep] = useState(0);
  const navigate = useNavigate();

  const handleNext = () => {
    if (currentStep < onboardingData.length - 1) {
      setCurrentStep(currentStep + 1);
    } else {
      handleGetStarted();
    }
  };

  const handlePrevious = () => {
    if (currentStep > 0) {
      setCurrentStep(currentStep - 1);
    }
  };

  const handleSkip = () => {
    handleGetStarted();
  };

  const handleGetStarted = () => {
    localStorage.setItem('kisanmitra_onboarding_seen', 'true');
    navigate('/signup');
  };

  const currentData = onboardingData[currentStep];

  return (
    <div className="mobile-container">
      {/* Background with animated gradient */}
      <div className={`absolute inset-0 bg-gradient-to-br ${currentData.color} transition-all duration-700`} />

      {/* Floating decorative elements */}
      <div className="absolute inset-0 overflow-hidden">
        {[...Array(15)].map((_, i) => (
          <div
            key={i}
            className="absolute"
            style={{
              left: Math.random() * 100 + '%',
              top: Math.random() * 100 + '%',
              animationDelay: Math.random() * 4 + 's',
            }}
          >
            {i % 3 === 0 ? (
              <Sparkles className="w-4 h-4 text-white/30 animate-pulse" />
            ) : (
              <Star className="w-3 h-3 text-white/20 animate-bounce" />
            )}
          </div>
        ))}
      </div>

      {/* Header */}
      <div className="relative z-10 flex justify-between items-center p-6">
        <button
          onClick={handlePrevious}
          className={`p-2 rounded-full bg-white/20 backdrop-blur-sm transition-all ${
            currentStep === 0 ? 'opacity-0 pointer-events-none' : 'opacity-100'
          }`}
        >
          <ChevronLeft className="w-6 h-6 text-white" />
        </button>

        <button
          onClick={handleSkip}
          className="text-white/80 font-medium hover:text-white transition-colors"
        >
          Skip
        </button>
      </div>

      {/* Content */}
      <div className="relative z-10 flex flex-col items-center justify-center min-h-[calc(100vh-200px)] px-6">
        {/* Image */}
        <div className="mb-8 relative">
          <div className="w-80 h-60 relative animate-fade-in">
            <img
              src={currentData.image}
              alt={currentData.title}
              className="w-full h-full object-contain drop-shadow-xl"
            />

            {/* Floating UI elements animation */}
            <div className="absolute -top-4 -right-4 float-animation">
              <div className="w-8 h-8 bg-white/20 rounded-lg backdrop-blur-sm flex items-center justify-center">
                <Sparkles className="w-4 h-4 text-white" />
              </div>
            </div>

            <div className="absolute -bottom-2 -left-4 float-animation" style={{ animationDelay: '1s' }}>
              <div className="w-6 h-6 bg-white/20 rounded-full backdrop-blur-sm" />
            </div>
          </div>
        </div>

        {/* Text Content */}
        <div className="text-center mb-12 animate-fade-in">
          <h1 className="text-3xl font-bold text-white mb-4 leading-tight">
            {currentData.title}
          </h1>

          <div className="mb-4">
            <h2 className="text-xl font-semibold text-white/90 mb-2 bg-gradient-to-r from-white to-white/80 bg-clip-text text-transparent">
              {currentData.subtitle}
            </h2>
          </div>

          <p className="text-lg text-white/80 leading-relaxed max-w-xs">
            {currentData.description}
          </p>
        </div>

        {/* Progress Indicators */}
        <div className="flex space-x-3 mb-8">
          {onboardingData.map((_, index) => (
            <div
              key={index}
              className={`h-2 rounded-full transition-all duration-500 ${
                index === currentStep 
                  ? 'w-8 bg-white shadow-glow' 
                  : 'w-2 bg-white/40'
              }`}
            />
          ))}
        </div>
      </div>

      {/* Navigation */}
      <div className="relative z-10 p-6">
        {currentStep === onboardingData.length - 1 ? (
          <Button
            onClick={handleGetStarted}
            className="w-full btn-glass text-lg font-semibold py-4 bg-white/20 hover:bg-white/30 text-white border-0"
          >
            <Sparkles className="w-5 h-5 mr-2" />
            Begin Your Journey
          </Button>
        ) : (
          <Button
            onClick={handleNext}
            className="w-full btn-glass text-lg font-semibold py-4 bg-white/20 hover:bg-white/30 text-white border-0"
          >
            Next
            <ChevronRight className="w-5 h-5 ml-2" />
          </Button>
        )}
      </div>
    </div>
  );
};

export default Onboarding;

src/pages/Orders.tsx.content:
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { 
  Package, 
  Truck, 
  CheckCircle, 
  Clock, 
  MapPin,
  Phone,
  Star,
  RotateCcw,
  ShoppingBag,
  HelpCircle,
  ChevronRight,
  X,
  ArrowLeft
} from 'lucide-react';
import { cartManager, Order } from '@/lib/cart';

const FarmerOrdersPage = () => {
  const navigate = useNavigate();
  const [activeFilter, setActiveFilter] = useState('all');
  const [orders, setOrders] = useState<Order[]>([]);

  useEffect(() => {
    loadOrders();
  }, []);

  const loadOrders = () => {
    const allOrders = cartManager.getOrders();
    setOrders(allOrders);
  };

  const handleReorder = (order: Order) => {
    // Add all items from the order back to cart
    order.items.forEach(item => {
      cartManager.addToCart(item);
    });
    navigate('/cart');
  };

  const handleTrackOrder = (orderId: string) => {
    // In a real app, this would navigate to a tracking page
    alert(`Tracking order ${orderId}`);
  };

  const getItemIcon = (category: string) => {
    const icons = {
      fertilizers: '',
      seeds: '',
      pesticides: '',
      tools: ''
    };
    return icons[category] || '';
  };

  // Calculate summary stats
  const totalOrders = orders.length;
  const deliveredOrders = orders.filter(order => order.status === 'delivered').length;
  const totalSpent = orders.reduce((sum, order) => sum + order.total, 0);

  // Filter orders based on active filter
  const filteredOrders = activeFilter === 'all' 
    ? orders 
    : orders.filter(order => order.status === activeFilter);

  // Status configuration
  const statusConfig = {
    delivered: { color: 'bg-green-500', textColor: 'text-green-500', icon: CheckCircle, bgLight: 'bg-green-50' },
    shipped: { color: 'bg-blue-500', textColor: 'text-blue-500', icon: Truck, bgLight: 'bg-blue-50' },
    processing: { color: 'bg-orange-500', textColor: 'text-orange-500', icon: Clock, bgLight: 'bg-orange-50' },
    cancelled: { color: 'bg-red-500', textColor: 'text-red-500', icon: X, bgLight: 'bg-red-50' }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-IN', {
      day: 'numeric',
      month: 'short',
      year: 'numeric'
    });
  };

  const renderStatusBadge = (status) => {
    const config = statusConfig[status];
    const IconComponent = config.icon;
    return (
      <div className={`flex items-center gap-1 px-3 py-1 rounded-full ${config.color} text-white text-sm font-medium`}>
        <IconComponent className="w-4 h-4" />
        {status.charAt(0).toUpperCase() + status.slice(1)}
      </div>
    );
  };

  const renderActionButtons = (order: Order) => {
    const buttons = [];

    if (order.status === 'shipped' || order.status === 'processing') {
      buttons.push(
        <button 
          key="track" 
          onClick={() => handleTrackOrder(order.id)}
          className="flex items-center gap-2 px-4 py-2 bg-blue-500 text-white rounded-full text-sm font-medium hover:bg-blue-600 transition-colors"
        >
          <Truck className="w-4 h-4" />
          Track Order
        </button>
      );
    }

    if (order.status === 'delivered') {
      buttons.push(
        <button key="review" className="flex items-center gap-2 px-4 py-2 bg-orange-500 text-white rounded-full text-sm font-medium hover:bg-orange-600 transition-colors">
          <Star className="w-4 h-4" />
          Rate & Review
        </button>
      );
    }

    buttons.push(
      <button 
        key="reorder" 
        onClick={() => handleReorder(order)}
        className="flex items-center gap-2 px-4 py-2 bg-green-500 text-white rounded-full text-sm font-medium hover:bg-green-600 transition-colors"
      >
        <RotateCcw className="w-4 h-4" />
        Reorder
      </button>
    );

    return (
      <div className="flex gap-2 flex-wrap">
        {buttons}
      </div>
    );
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <div className="bg-white shadow-sm border-b">
        <div className="max-w-md mx-auto px-4 py-4">
          <div className="flex items-center space-x-3 mb-2">
            <button 
              onClick={() => navigate('/home')}
              className="p-2 -ml-2 hover:bg-gray-100 rounded-full transition-colors"
            >
              <ArrowLeft className="w-6 h-6 text-gray-700" />
            </button>
            <h1 className="text-2xl font-bold text-gray-900">My Orders</h1>
          </div>
          <p className="text-gray-600 text-sm">Track your agricultural purchases</p>
        </div>
      </div>

      <div className="max-w-md mx-auto px-4 py-6 space-y-6">
        {/* Summary Cards */}
        <div className="grid grid-cols-3 gap-3">
          <div className="bg-white rounded-2xl p-4 shadow-sm border border-gray-100">
            <div className="flex items-center gap-2 mb-2">
              <div className="w-8 h-8 bg-blue-50 rounded-lg flex items-center justify-center">
                <Package className="w-4 h-4 text-blue-500" />
              </div>
            </div>
            <div className="text-2xl font-bold text-gray-900">{totalOrders}</div>
            <div className="text-sm text-gray-600">Total Orders</div>
          </div>

          <div className="bg-white rounded-2xl p-4 shadow-sm border border-gray-100">
            <div className="flex items-center gap-2 mb-2">
              <div className="w-8 h-8 bg-green-50 rounded-lg flex items-center justify-center">
                <CheckCircle className="w-4 h-4 text-green-500" />
              </div>
            </div>
            <div className="text-2xl font-bold text-gray-900">{deliveredOrders}</div>
            <div className="text-sm text-gray-600">Delivered</div>
          </div>

          <div className="bg-white rounded-2xl p-4 shadow-sm border border-gray-100">
            <div className="flex items-center gap-2 mb-2">
              <div className="w-8 h-8 bg-yellow-50 rounded-lg flex items-center justify-center">
                <span className="text-lg"></span>
              </div>
            </div>
            <div className="text-2xl font-bold text-gray-900">{totalSpent.toLocaleString()}</div>
            <div className="text-sm text-gray-600">Total Spent</div>
          </div>
        </div>

        {/* Filter Pills */}
        <div className="flex gap-2 overflow-x-auto pb-2">
          {['all', 'processing', 'shipped', 'delivered'].map((filter) => (
            <button
              key={filter}
              onClick={() => setActiveFilter(filter)}
              className={`px-6 py-2 rounded-full text-sm font-medium whitespace-nowrap transition-colors ${
                activeFilter === filter
                  ? 'bg-green-500 text-white'
                  : 'bg-white text-gray-600 border border-gray-200 hover:bg-gray-50'
              }`}
            >
              {filter.charAt(0).toUpperCase() + filter.slice(1)}
            </button>
          ))}
        </div>

        {/* Orders List */}
        {filteredOrders.length > 0 ? (
          <div className="space-y-4">
            {filteredOrders.map((order) => (
              <div key={order.id} className="bg-white rounded-2xl p-5 shadow-sm border border-gray-100">
                {/* Order Header */}
                <div className="flex justify-between items-start mb-4">
                  <div>
                    <h3 className="font-semibold text-gray-900">Order #{order.id}</h3>
                    <p className="text-sm text-gray-600">Placed on {formatDate(order.orderDate)}</p>
                  </div>
                  {renderStatusBadge(order.status)}
                </div>

                {/* Products */}
                <div className="space-y-3 mb-4">
                  {order.items.slice(0, 2).map((product, index) => (
                    <div key={index} className="flex justify-between items-center">
                      <div className="flex items-center gap-3">
                        <div className="w-10 h-10 bg-green-50 rounded-lg flex items-center justify-center">
                          <span className="text-lg">{getItemIcon(product.category)}</span>
                        </div>
                        <div>
                          <p className="text-sm font-medium text-gray-900">{product.name}</p>
                          <p className="text-xs text-gray-500">{product.quantity} x {product.price}</p>
                        </div>
                      </div>
                      <p className="font-semibold text-gray-900">{product.price * product.quantity}</p>
                    </div>
                  ))}
                  {order.items.length > 2 && (
                    <button className="text-sm text-green-500 font-medium">
                      + {order.items.length - 2} more items
                    </button>
                  )}
                </div>

                {/* Total Amount */}
                <div className="flex justify-between items-center py-3 border-t border-gray-100">
                  <span className="font-semibold text-gray-900">Total Amount</span>
                  <span className="text-lg font-bold text-green-600">{order.total}</span>
                </div>

                {/* Address & Delivery Info */}
                <div className="bg-gray-50 rounded-xl p-3 mb-4">
                  <div className="flex items-start gap-2">
                    <MapPin className="w-4 h-4 text-gray-500 mt-0.5" />
                    <div className="flex-1">
                      <p className="text-sm text-gray-900">{order.address}</p>
                      {order.status === 'delivered' && order.deliveryDate && (
                        <p className="text-sm text-green-600 mt-1">
                          Delivered on {formatDate(order.deliveryDate)}
                        </p>
                      )}
                      {order.status !== 'delivered' && order.estimatedDelivery && (
                        <p className="text-sm text-blue-600 mt-1">
                          Expected by {formatDate(order.estimatedDelivery)}
                        </p>
                      )}
                    </div>
                  </div>
                </div>

                {/* Action Buttons */}
                {renderActionButtons(order)}
              </div>
            ))}
          </div>
        ) : (
          /* Empty State */
          <div className="text-center py-12 bg-white rounded-2xl shadow-sm border border-gray-100">
            <div className="w-20 h-20 bg-gray-50 rounded-full flex items-center justify-center mx-auto mb-4">
              <ShoppingBag className="w-10 h-10 text-gray-400" />
            </div>
            <h3 className="text-lg font-semibold text-gray-900 mb-2">No orders yet</h3>
            <p className="text-gray-600 mb-6">Start shopping for agricultural supplies today!</p>
            <button 
              onClick={() => navigate('/shop')}
              className="bg-green-500 text-white px-6 py-3 rounded-full font-medium hover:bg-green-600 transition-colors"
            >
              Shop Now
            </button>
          </div>
        )}

        {/* Support Section */}
        <div className="bg-white rounded-2xl p-5 shadow-sm border border-gray-100">
          <div className="flex items-center gap-2 mb-4">
            <HelpCircle className="w-5 h-5 text-gray-600" />
            <h3 className="text-lg font-semibold text-gray-900">Need Help?</h3>
          </div>

          <div className="space-y-2">
            <button className="w-full flex items-center justify-between p-3 bg-gray-50 rounded-xl hover:bg-gray-100 transition-colors">
              <div className="flex items-center gap-3">
                <Phone className="w-5 h-5 text-green-500" />
                <span className="font-medium text-gray-900">Contact Support</span>
              </div>
              <ChevronRight className="w-5 h-5 text-gray-400" />
            </button>

            <button className="w-full flex items-center justify-between p-3 bg-gray-50 rounded-xl hover:bg-gray-100 transition-colors">
              <div className="flex items-center gap-3">
                <RotateCcw className="w-5 h-5 text-green-500" />
                <span className="font-medium text-gray-900">Return Policy</span>
              </div>
              <ChevronRight className="w-5 h-5 text-gray-400" />
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default FarmerOrdersPage;

