import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { 
  ArrowLeft, 
  Edit3, 
  Phone, 
  MapPin, 
  Calendar,
  TrendingUp,
  Package,
  Trophy,
  Store,
  ShoppingCart,
  Cloud,
  Activity,
  Star,
  Users,
  Leaf,
  BarChart3,
  Settings,
  Bell,
  HelpCircle,
  LogOut,
  Crown,
  ShoppingBag,
  Wallet,
  ChevronRight,
  Share2,
  BadgeCheck,
  MessageCircle,
  Shield,
  Gift,
  FileText,
  DollarSign
} from 'lucide-react';
import { dataService } from '../lib/dataService';
import { marketData } from '../lib/marketData';

const Profile = () => {
  const navigate = useNavigate();
  const [userData, setUserData] = useState(null);
  const [stats, setStats] = useState(null);
  const [achievements, setAchievements] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [notificationsEnabled, setNotificationsEnabled] = useState(true);

  useEffect(() => {
    const loadUserData = async () => {
      setIsLoading(true);
      try {
        const user = dataService.getCurrentUser();
        if (user) {
          setUserData(user);
          const userStats = dataService.calculateUserStats(user.id);
          setStats(userStats);

          // Generate achievements based on real data
          const generatedAchievements = [
            { 
              id: 1, 
              title: 'First Sale', 
              icon: Trophy, 
              completed: userStats.successfulSales >= 1 
            },
            { 
              id: 2, 
              title: 'Top Seller', 
              icon: Crown, 
              completed: userStats.successfulSales >= 10 
            },
            { 
              id: 3, 
              title: 'Plant Expert', 
              icon: Leaf, 
              completed: userStats.diagnosisCount >= 5 
            },
            { 
              id: 4, 
              title: 'Super Star', 
              icon: Star, 
              completed: userStats.totalEarnings >= 50000 
            }
          ];
          setAchievements(generatedAchievements);
        }
      } catch (error) {
        console.error('Error loading user data:', error);
      } finally {
        setIsLoading(false);
      }
    };

    loadUserData();
  }, []);

  if (isLoading || !userData || !stats) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="w-8 h-8 border-2 border-green-500 border-t-transparent rounded-full animate-spin mx-auto mb-2"></div>
          <p className="text-gray-600">Loading profile...</p>
        </div>
      </div>
    );
  }

  const StatCard = ({ title, value, icon: Icon, color = "bg-blue-500" }) => (
    <div className="bg-white rounded-xl p-4 border border-gray-200">
      <div className="flex items-center space-x-3">
        <div className={`w-10 h-10 ${color} rounded-lg flex items-center justify-center`}>
          <Icon className="w-5 h-5 text-white" />
        </div>
        <div>
          <div className="text-lg font-semibold text-gray-900">{value}</div>
          <div className="text-sm text-gray-600">{title}</div>
        </div>
      </div>
    </div>
  );

  const MenuItem = ({ icon: Icon, title, subtitle, onClick, rightElement = null }) => (
    <button
      onClick={onClick}
      className="w-full flex items-center justify-between p-4 hover:bg-gray-50 rounded-xl transition-colors"
    >
      <div className="flex items-center space-x-3">
        <div className="w-10 h-10 bg-gray-100 rounded-lg flex items-center justify-center">
          <Icon className="w-5 h-5 text-gray-600" />
        </div>
        <div className="text-left">
          <div className="font-medium text-gray-900">{title}</div>
          {subtitle && <div className="text-sm text-gray-500">{subtitle}</div>}
        </div>
      </div>
      {rightElement || <ChevronRight className="w-5 h-5 text-gray-400" />}
    </button>
  );

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <div className="bg-white border-b border-gray-200">
        <div className="px-4 py-4 flex items-center justify-between">
          <button 
            onClick={() => navigate('/')}
            className="w-10 h-10 bg-gray-100 rounded-lg flex items-center justify-center"
          >
            <ArrowLeft className="w-5 h-5 text-gray-700" />
          </button>
          <h1 className="text-lg font-semibold text-gray-900">Profile</h1>
          <button className="w-10 h-10 bg-gray-100 rounded-lg flex items-center justify-center">
            <Share2 className="w-5 h-5 text-gray-700" />
          </button>
        </div>
      </div>

      <div className="p-4 space-y-6">
        {/* Profile Info */}
        <div className="bg-white rounded-xl p-6 border border-gray-200">
          <div className="flex items-start space-x-4">
            <div className="relative">
              <div className="w-20 h-20 bg-blue-500 rounded-full flex items-center justify-center text-white text-2xl font-semibold">
                {userData.name.charAt(0)}
              </div>
              <div className="absolute -bottom-1 -right-1 w-6 h-6 bg-green-500 rounded-full flex items-center justify-center">
                <BadgeCheck className="w-4 h-4 text-white" />
              </div>
            </div>
            <div className="flex-1">
              <div className="flex items-center justify-between mb-2">
                <h2 className="text-xl font-semibold text-gray-900">{userData.name}</h2>
                <button 
                  onClick={() => navigate('/profile/edit')}
                  className="bg-blue-500 text-white px-4 py-2 rounded-lg text-sm font-medium flex items-center space-x-1"
                >
                  <Edit3 className="w-4 h-4" />
                  <span>Edit</span>
                </button>
              </div>
              <div className="space-y-1 text-sm text-gray-600">
                <div className="flex items-center space-x-2">
                  <MapPin className="w-4 h-4" />
                  <span>{userData.location}</span>
                </div>
                <div className="flex items-center space-x-2">
                  <Phone className="w-4 h-4" />
                  <span>{userData.phone}</span>
                </div>
              </div>
              <div className="mt-3 flex items-center space-x-6 text-sm">
                <div>
                  <span className="font-semibold text-gray-900">{stats.successfulSales}</span>
                  <span className="text-gray-500"> sales</span>
                </div>
                <div>
                  <span className="font-semibold text-gray-900">{stats.level}</span>
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* Quick Stats */}
        <div className="grid grid-cols-2 gap-4">
          <StatCard 
            title="Total Earnings" 
            value={`${(stats.totalEarnings/1000).toFixed(0)}K`} 
            icon={Wallet}
            color="bg-green-500"
          />
          <button 
            onClick={() => navigate('/sell?tab=active')}
            className="bg-white rounded-xl p-4 border border-gray-200 hover:bg-gray-50 transition-colors text-left"
          >
            <div className="flex items-center space-x-3">
              <div className="w-10 h-10 bg-blue-500 rounded-lg flex items-center justify-center">
                <Package className="w-5 h-5 text-white" />
              </div>
              <div>
                <div className="text-lg font-semibold text-gray-900">{stats.activeListings}</div>
                <div className="text-sm text-gray-600">Active Listings</div>
              </div>
            </div>
          </button>
          <button 
            onClick={() => navigate('/treatments')}
            className="bg-white rounded-xl p-4 border border-gray-200 hover:bg-gray-50 transition-colors text-left"
          >
            <div className="flex items-center space-x-3">
              <div className="w-10 h-10 bg-orange-500 rounded-lg flex items-center justify-center">
                <Activity className="w-5 h-5 text-white" />
              </div>
              <div>
                <div className="text-lg font-semibold text-gray-900">{stats.activeTreatments}</div>
                <div className="text-sm text-gray-600">Active Treatments</div>
              </div>
            </div>
          </button>
          <StatCard 
            title="Markets" 
            value={marketData.length} 
            icon={Store}
            color="bg-purple-500"
          />
        </div>

        {/* Achievements */}
        <div className="bg-white rounded-xl p-6 border border-gray-200">
          <div className="flex items-center justify-between mb-4">
            <h3 className="text-lg font-semibold text-gray-900">Achievements</h3>
            <div className="text-sm text-gray-500">
              {achievements.filter(a => a.completed).length}/{achievements.length} unlocked
            </div>
          </div>
          <div className="grid grid-cols-4 gap-4">
            {achievements.map((achievement) => (
              <div key={achievement.id} className="text-center">
                <div className={`w-12 h-12 ${achievement.completed ? 'bg-yellow-100' : 'bg-gray-100'} rounded-lg flex items-center justify-center mx-auto mb-2`}>
                  <achievement.icon className={`w-6 h-6 ${achievement.completed ? 'text-yellow-600' : 'text-gray-400'}`} />
                </div>
                <div className="text-xs font-medium text-gray-700">{achievement.title}</div>
              </div>
            ))}
          </div>
        </div>

        {/* Quick Actions */}
        <div className="bg-white rounded-xl p-6 border border-gray-200">
          <h3 className="text-lg font-semibold text-gray-900 mb-4">Quick Actions</h3>
          <div className="grid grid-cols-2 gap-4">
            {[
              { title: 'Sell Crops', icon: ShoppingBag, color: 'bg-green-500', route: '/sell' },
              { title: 'Market Prices', icon: BarChart3, color: 'bg-blue-500', route: '/market-prices' },
              { title: 'Weather', icon: Cloud, color: 'bg-sky-500', route: '/weather' },
              { title: 'Diagnose Plant', icon: Activity, color: 'bg-orange-500', route: '/diagnose' }
            ].map((action, index) => (
              <button
                key={index}
                onClick={() => navigate(action.route)}
                className="p-4 border border-gray-200 rounded-lg hover:bg-gray-50 transition-colors text-center"
              >
                <div className={`w-10 h-10 ${action.color} rounded-lg flex items-center justify-center mx-auto mb-2`}>
                  <action.icon className="w-5 h-5 text-white" />
                </div>
                <div className="text-sm font-medium text-gray-700">{action.title}</div>
              </button>
            ))}
          </div>
        </div>

        {/* Menu Items */}
        <div className="bg-white rounded-xl border border-gray-200 overflow-hidden">
          <MenuItem
            icon={Leaf}
            title="My Advisory"
            subtitle={`${stats.advisorySaved} tips saved`}
            onClick={() => navigate('/advisory')}
            rightElement={<ChevronRight className="w-5 h-5 text-gray-400" />}
          />
          <MenuItem
            icon={FileText}
            title="Sales Report"
            subtitle={`${(stats.totalEarnings/1000).toFixed(0)}K earned`}
            onClick={() => navigate('/sell?tab=sold')}
            rightElement={<ChevronRight className="w-5 h-5 text-gray-400" />}
          />
          <MenuItem
            icon={Package}
            title="Sold Listings"
            subtitle={`${stats.successfulSales} items sold`}
            onClick={() => navigate('/sell?tab=sold')}
            rightElement={<ChevronRight className="w-5 h-5 text-gray-400" />}
          />
        </div>

        {/* Settings */}
        <div className="bg-white rounded-xl border border-gray-200 overflow-hidden">
          <div className="flex items-center justify-between p-4">
            <div className="flex items-center space-x-3">
              <div className="w-10 h-10 bg-gray-100 rounded-lg flex items-center justify-center">
                <Bell className="w-5 h-5 text-gray-600" />
              </div>
              <div>
                <div className="font-medium text-gray-900">Notifications</div>
                <div className="text-sm text-gray-500">Push notifications</div>
              </div>
            </div>
            <button
              onClick={() => setNotificationsEnabled(!notificationsEnabled)}
              className={`relative inline-flex h-6 w-11 items-center rounded-full transition-colors ${
                notificationsEnabled ? 'bg-blue-500' : 'bg-gray-300'
              }`}
            >
              <span
                className={`inline-block h-4 w-4 transform rounded-full bg-white transition-transform ${
                  notificationsEnabled ? 'translate-x-6' : 'translate-x-1'
                }`}
              />
            </button>
          </div>
          <MenuItem
            icon={HelpCircle}
            title="Help & Support"
            subtitle="Get assistance"
            onClick={() => {}}
            rightElement={<ChevronRight className="w-5 h-5 text-gray-400" />}
          />
          <MenuItem
            icon={Shield}
            title="Privacy & Security"
            subtitle="Manage account security"
            onClick={() => {}}
            rightElement={<ChevronRight className="w-5 h-5 text-gray-400" />}
          />
          <MenuItem
            icon={Settings}
            title="App Settings"
            subtitle="Preferences"
            onClick={() => {}}
            rightElement={<ChevronRight className="w-5 h-5 text-gray-400" />}
          />
        </div>

        {/* Logout */}
        <button className="w-full bg-white border border-red-200 rounded-xl p-4 text-red-600 font-medium hover:bg-red-50 transition-colors">
          <div className="flex items-center justify-center space-x-2">
            <LogOut className="w-5 h-5" />
            <span>Logout</span>
          </div>
        </button>
      </div>
    </div>
  );
};

export default Profile;

src/pages/ProfileEdit.tsx.content:
import { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { 
  ArrowLeft, 
  Save, 
  Camera,
  User,
  Phone, 
  MapPin, 
  Calendar,
  Globe,
  Mail,
  Sprout
} from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { toast } from '@/components/ui/use-toast';
import CustomInput from '@/components/CustomInput';
import LocationPicker from '@/components/LocationPicker';

const ProfileEdit = () => {
  const navigate = useNavigate();
  const [userData, setUserData] = useState({
    name: '',
    phone: '',
    email: '',
    birthDate: '',
    location: '',
    state: '',
    district: '',
    pincode: '',
    language: 'English',
    farmSize: '',
    experience: '',
    crops: ''
  });
  const [isLoading, setIsLoading] = useState(true);

  // Load current user data
  useEffect(() => {
    const loadCurrentUser = () => {
      try {
        const currentUser = localStorage.getItem('kisanmitra_user');
        if (currentUser) {
          const user = JSON.parse(currentUser);
          setUserData({
            name: user.name || '',
            phone: user.phone || user.mobile ? `+91 ${user.mobile}` : '',
            email: user.email || '',
            birthDate: user.birthDate || '',
            location: user.location || user.district || '',
            state: user.state || '',
            district: user.district || user.location || '',
            pincode: user.pincode || '',
            language: user.language || 'English',
            farmSize: user.farmSize || '',
            experience: user.experience || '',
            crops: user.crops || ''
          });
        }
      } catch (error) {
        console.error('Error loading user data:', error);
      } finally {
        setIsLoading(false);
      }
    };

    loadCurrentUser();
  }, []);

  const getNameValidation = () => {
    if (userData.name.length === 0) return undefined;
    const isValid = userData.name.trim().length >= 2;
    return {
      isValid,
      message: isValid ? "Valid name" : "Name must be at least 2 characters"
    };
  };

  const getPhoneValidation = () => {
    if (userData.phone.length === 0) return undefined;
    const phoneNumber = userData.phone.replace(/\D/g, '');
    const isValid = phoneNumber.length === 12 && phoneNumber.startsWith('91');
    return {
      isValid,
      message: isValid ? "Valid phone number" : "Enter a valid Indian mobile number"
    };
  };

  const handleSave = async () => {
    try {
      // Validate required fields
      if (!userData.name.trim()) {
        toast({
          title: "Validation Error",
          description: "Name is required",
          variant: "destructive"
        });
        return;
      }

      // Get current user data and update it
      const currentUser = JSON.parse(localStorage.getItem('kisanmitra_user') || '{}');
      const phoneNumber = userData.phone.replace(/\D/g, '').replace(/^91/, '');

      const updatedUser = {
        ...currentUser,
        name: userData.name.trim(),
        phone: userData.phone,
        mobile: phoneNumber,
        email: userData.email,
        birthDate: userData.birthDate,
        location: userData.location || userData.district,
        state: userData.state,
        district: userData.district || userData.location,
        pincode: userData.pincode,
        language: userData.language,
        farmSize: userData.farmSize,
        experience: userData.experience,
        crops: userData.crops,
        updatedAt: new Date().toISOString()
      };

      // Save to localStorage
      localStorage.setItem('kisanmitra_user', JSON.stringify(updatedUser));

      // Also update auth token if phone changed
      if (currentUser.pin) {
        const authToken = btoa(`${phoneNumber}:${currentUser.pin}`);
        localStorage.setItem('kisanmitra_auth_token', authToken);
      }

      toast({
        title: "Profile Updated! ",
        description: "Your profile has been successfully updated."
      });

      navigate('/profile');

    } catch (error) {
      console.error('Error saving profile:', error);
      toast({
        title: "Save Failed",
        description: "Failed to save profile. Please try again.",
        variant: "destructive"
      });
    }
  };

  const handleInputChange = (field: string, value: string) => {
    setUserData(prev => ({
      ...prev,
      [field]: value
    }));
  };

  return (
    <div className="mobile-container bg-background">
      {/* Header */}
      <div className="mobile-header bg-white">
        <div className="flex items-center space-x-3">
          <button 
            onClick={() => navigate('/profile')}
            className="w-10 h-10 bg-agri-light rounded-full flex items-center justify-center"
          >
            <ArrowLeft className="w-5 h-5 text-agri-accent" />
          </button>
          <h1 className="text-xl font-bold text-agri-accent">Edit Profile</h1>
        </div>

        <Button 
          onClick={handleSave}
          className="bg-agri-primary hover:bg-agri-secondary text-white"
        >
          <Save className="w-4 h-4 mr-2" />
          Save
        </Button>
      </div>

      <div className="mobile-content space-y-6 pt-4">
        {/* Profile Photo */}
        <div className="bg-white rounded-2xl p-6 shadow-soft">
          <div className="text-center">
            <div className="w-20 h-20 bg-agri-primary rounded-full flex items-center justify-center text-white text-2xl font-bold mx-auto mb-4">
              RJ
            </div>
            <Button variant="outline" className="border-agri-primary text-agri-primary">
              <Camera className="w-4 h-4 mr-2" />
              Change Photo
            </Button>
          </div>
        </div>

        {/* Personal Information */}
        <div className="bg-white rounded-2xl p-6 shadow-soft">
          <div className="flex items-center space-x-2 mb-6">
            <User className="w-5 h-5 text-agri-primary" />
            <h3 className="text-lg font-bold text-agri-accent">Personal Information</h3>
          </div>

          <div className="space-y-4">
            <CustomInput
              label="Full Name"
              icon={<User className="w-5 h-5" />}
              value={userData.name}
              onChange={(value) => handleInputChange('name', value)}
              placeholder="Enter your full name"
              type="text"
              validation={getNameValidation()}
              required
            />

            <CustomInput
              label="Mobile Number"
              icon={<Phone className="w-5 h-5" />}
              value={userData.phone.replace('+91 ', '')}
              onChange={(value) => handleInputChange('phone', `+91 ${value}`)}
              placeholder="Enter 10 digit mobile"
              type="tel"
              prefix=" +91"
              maxLength={10}
              validation={getPhoneValidation()}
              required
            />

            <div>
              <Label htmlFor="email" className="text-agri-accent font-medium">Email Address (Optional)</Label>
              <Input
                id="email"
                type="email"
                value={userData.email}
                onChange={(e) => handleInputChange('email', e.target.value)}
                placeholder="your.email@example.com"
                className="mt-2 border-agri-primary/20 focus:border-agri-primary"
              />
            </div>

            <div>
              <Label htmlFor="birthDate" className="text-agri-accent font-medium">Date of Birth</Label>
              <Input
                id="birthDate"
                type="date"
                value={userData.birthDate}
                onChange={(e) => handleInputChange('birthDate', e.target.value)}
                className="mt-2 border-agri-primary/20 focus:border-agri-primary"
              />
            </div>
          </div>
        </div>

        {/* Location Information */}
        <div className="bg-white rounded-2xl p-6 shadow-soft">
          <div className="flex items-center space-x-2 mb-6">
            <MapPin className="w-5 h-5 text-agri-primary" />
            <h3 className="text-lg font-bold text-agri-accent">Location Details</h3>
          </div>

          <LocationPicker
            state={userData.state}
            district={userData.district}
            onStateChange={(value) => handleInputChange('state', value)}
            onDistrictChange={(value) => handleInputChange('district', value)}
          />

          <div className="mt-4">
            <Label htmlFor="pincode" className="text-agri-accent font-medium">PIN Code (Optional)</Label>
            <Input
              id="pincode"
              value={userData.pincode}
              onChange={(e) => handleInputChange('pincode', e.target.value.replace(/\D/g, '').slice(0, 6))}
              placeholder="Enter 6-digit PIN code"
              maxLength={6}
              className="mt-2 border-agri-primary/20 focus:border-agri-primary"
            />
          </div>
        </div>

        {/* Farm Information */}
        <div className="bg-white rounded-2xl p-6 shadow-soft">
          <div className="flex items-center space-x-2 mb-6">
            <Sprout className="w-5 h-5 text-agri-primary" />
            <h3 className="text-lg font-bold text-agri-accent">Farm Details</h3>
          </div>

          <div className="space-y-4">
            <div>
              <Label htmlFor="farmSize" className="text-agri-accent font-medium">Farm Size (Optional)</Label>
              <Input
                id="farmSize"
                value={userData.farmSize}
                onChange={(e) => handleInputChange('farmSize', e.target.value)}
                placeholder="e.g., 2.5 acres, 1 hectare"
                className="mt-2 border-agri-primary/20 focus:border-agri-primary"
              />
            </div>

            <div>
              <Label htmlFor="experience" className="text-agri-accent font-medium">Farming Experience (Optional)</Label>
              <Input
                id="experience"
                value={userData.experience}
                onChange={(e) => handleInputChange('experience', e.target.value)}
                placeholder="e.g., 5 years, New farmer"
                className="mt-2 border-agri-primary/20 focus:border-agri-primary"
              />
            </div>

            <div>
              <Label htmlFor="crops" className="text-agri-accent font-medium">Primary Crops (Optional)</Label>
              <Input
                id="crops"
                value={userData.crops}
                onChange={(e) => handleInputChange('crops', e.target.value)}
                placeholder="e.g., Rice, Wheat, Tomato"
                className="mt-2 border-agri-primary/20 focus:border-agri-primary"
              />
            </div>
          </div>
        </div>

        {/* Preferences */}
        <div className="bg-white rounded-2xl p-6 shadow-soft">
          <div className="flex items-center space-x-2 mb-6">
            <Globe className="w-5 h-5 text-agri-primary" />
            <h3 className="text-lg font-bold text-agri-accent">Preferences</h3>
          </div>

          <div>
            <Label htmlFor="language" className="text-agri-accent font-medium">Preferred Language</Label>
            <Select value={userData.language} onValueChange={(value) => handleInputChange('language', value)}>
              <SelectTrigger className="mt-2 border-agri-primary/20 focus:border-agri-primary">
                <SelectValue />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="English"> English</SelectItem>
                <SelectItem value="Hindi">  (Hindi)</SelectItem>
                <SelectItem value="Telugu">  (Telugu)</SelectItem>
                <SelectItem value="Tamil">  (Tamil)</SelectItem>
                <SelectItem value="Kannada">  (Kannada)</SelectItem>
                <SelectItem value="Marathi">  (Marathi)</SelectItem>
                <SelectItem value="Gujarati">  (Gujarati)</SelectItem>
                <SelectItem value="Bengali">  (Bengali)</SelectItem>
                <SelectItem value="Punjabi">  (Punjabi)</SelectItem>
              </SelectContent>
            </Select>
          </div>
        </div>

        {/* Save Button */}
        <div className="pb-8">
          <Button 
            onClick={handleSave}
            className="w-full bg-agri-primary hover:bg-agri-secondary text-white h-12 text-lg font-semibold"
          >
            <Save className="w-5 h-5 mr-2" />
            Save Changes
          </Button>
        </div>
      </div>
    </div>
  );
};

export default ProfileEdit;

src/pages/SellProduce.tsx.content:
import { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { dataService, type Listing } from '@/lib/dataService';
import {
  ArrowLeft,
  Plus,
  MapPin,
  Clock,
  Eye,
  Truck,
  CheckCircle,
  X,
  TrendingUp,
  Check,
  AlertCircle,
  Timer
} from 'lucide-react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Badge } from '@/components/ui/badge';
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogDescription } from '@/components/ui/dialog';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Switch } from '@/components/ui/switch';
import { marketData as realMarketData, Crop } from '@/lib/marketData';

const SellProduce = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const [selectedTab, setSelectedTab] = useState('create');
  const [showCreateListingDialog, setShowCreateListingDialog] = useState(false);
  const [selectedCrop, setSelectedCrop] = useState('');
  const [selectedMarket, setSelectedMarket] = useState('');
  const [quantity, setQuantity] = useState('');
  const [needTransport, setNeedTransport] = useState(false);
  const [activeListings, setActiveListings] = useState<Listing[]>([]);
  const [soldListings, setSoldListings] = useState<Listing[]>([]);
  const [currentUser, setCurrentUser] = useState<any>(null);
  const [isLoading, setIsLoading] = useState(true);

  // Use real Chennai market data
  const marketData = realMarketData;

  // Get market-specific price for selected crop and market (convert from string X/kg to number)
  const getMarketPrice = () => {
    if (!selectedMarket || !selectedCrop) return 0;
    const market = marketData.find(m => m.name === selectedMarket);
    const crop = market?.crops.find(c => c.name === selectedCrop);
    if (!crop?.price) return 0;
    // Extract number from price string (e.g., "31/kg" -> 31, "31.5/kg" -> 31.5)
    const priceMatch = crop.price.match(/([\d.]+)/);
    return priceMatch ? parseFloat(priceMatch[1]) : 0;
  };

  // Auto-calculated price based on quantity and market-specific price
  const calculateTotalPrice = () => {
    if (!selectedCrop || !quantity || !selectedMarket) return 0;
    const marketPrice = getMarketPrice();
    return parseFloat(quantity) * marketPrice;
  };

  // Handle URL tab parameter
  useEffect(() => {
    const urlParams = new URLSearchParams(location.search);
    const tabParam = urlParams.get('tab');
    if (tabParam && ['create', 'active', 'sold'].includes(tabParam)) {
      setSelectedTab(tabParam);
    }
  }, [location.search]);

  // Load real user data and listings
  useEffect(() => {
    const loadUserData = async () => {
      setIsLoading(true);
      try {
        const user = dataService.getCurrentUser();
        if (user) {
          setCurrentUser(user);
          await loadUserListings(user.id);
        }
      } catch (error) {
        console.error('Error loading user data:', error);
      } finally {
        setIsLoading(false);
      }
    };

    loadUserData();
  }, []);

  const loadUserListings = async (userId: string) => {
    try {
      const allListings = dataService.getUserListings(userId);
      const active = allListings.filter(l => l.status === 'active');
      const sold = allListings.filter(l => l.status === 'sold');

      setActiveListings(active);
      setSoldListings(sold);
    } catch (error) {
      console.error('Error loading listings:', error);
    }
  };

  // Helper function to get market approval status
  const getMarketStatus = (listing: Listing) => {
    // Simulate market approval logic based on listing age and other factors
    const createdDate = new Date(listing.postedDate);
    const now = new Date();
    const hoursSinceCreated = (now.getTime() - createdDate.getTime()) / (1000 * 60 * 60);

    // For demo purposes, simulate realistic market approval timing
    if (hoursSinceCreated < 2) {
      return 'pending';
    } else if (hoursSinceCreated < 24) {
      // 80% chance of acceptance based on good price and demand
      const pricePerKg = typeof listing.pricePerKg === 'string' ? parseFloat(listing.pricePerKg) : listing.pricePerKg;
      const avgMarketPrice = getAverageMarketPrice(listing.crop);
      const priceRatio = pricePerKg / avgMarketPrice;

      // Better prices (competitive with market) have higher acceptance rate
      if (priceRatio <= 1.2 && priceRatio >= 0.8) {
        return Math.random() > 0.2 ? 'accepted' : 'rejected';
      } else if (priceRatio > 1.2) {
        return Math.random() > 0.6 ? 'rejected' : 'accepted';
      } else {
        return 'accepted'; // Very low prices always get accepted
      }
    } else {
      // Older listings have been reviewed
      return Math.random() > 0.3 ? 'accepted' : 'rejected';
    }
  };

  // Helper function to get average market price for a crop
  const getAverageMarketPrice = (cropName: string) => {
    let totalPrice = 0;
    let count = 0;

    marketData.forEach(market => {
      market.crops.forEach(crop => {
        if (crop.name === cropName) {
          const priceMatch = crop.price.match(/([\d.]+)/);
          if (priceMatch) {
            totalPrice += parseFloat(priceMatch[1]);
            count++;
          }
        }
      });
    });

    return count > 0 ? totalPrice / count : 50; // Default to 50/kg if not found
  };

  // Component to render market status badge
  const MarketStatusBadge = ({ status }: { status: string }) => {
    switch (status) {
      case 'pending':
        return (
          <div className="inline-flex items-center space-x-1 bg-yellow-100 text-yellow-800 px-2 py-1 rounded-full text-xs font-medium">
            <Timer className="w-3 h-3" />
            <span>Under Review</span>
          </div>
        );
      case 'accepted':
        return (
          <div className="inline-flex items-center space-x-1 bg-green-100 text-green-800 px-2 py-1 rounded-full text-xs font-medium">
            <Check className="w-3 h-3" />
            <span>Market Approved</span>
          </div>
        );
      case 'rejected':
        return (
          <div className="inline-flex items-center space-x-1 bg-red-100 text-red-800 px-2 py-1 rounded-full text-xs font-medium">
            <AlertCircle className="w-3 h-3" />
            <span>Review Needed</span>
          </div>
        );
      default:
        return null;
    }
  };

  // Helper function to get crop emoji - Updated for all Chennai vegetables
  const getCropEmoji = (cropName) => {
    const emojiMap = {
      'Tomato': '',
      'Onion': '', 
      'Onion Big': '',
      'Onion Small': '',
      'Onion Green': '',
      'Potato': '',
      'Carrot': '',
      'Cabbage': '',
      'Cauliflower': '',
      'Spinach': '',
      'Radish': '',
      'Brinjal': '',
      'Ladies Finger': '',
      'Bottle Gourd': '',
      'Bitter Gourd': '',
      'Ridge Gourd': '',
      'Snake Gourd': '',
      'Ivy Gourd': '',
      'Ash Gourd': '',
      'Cucumber': '',
      'Pumpkin': '',
      'Green Chilli': '',
      'Capsicum': '',
      'French Beans': '',
      'Broad Beans': '',
      'Butter Beans': '',
      'Cluster Beans': '',
      'Green Peas': '',
      'Beetroot': '',
      'Sweet Potato': '',
      'Elephant Yam': '',
      'Colocasia': '',
      'Corn': '',
      'Baby Corn': '',
      'Drumsticks': '',
      'Coconut': '',
      'Lemon': '',
      'Ginger': '',
      'Garlic': '',
      'Mushroom': '',
      'Amla': '',
      'Raw Mango': '',
      'Raw Banana': '',
      'Banana Flower': '',
      'Shallot': '',
      'Curry Leaves': '',
      'Coriander Leaves': '',
      'Mint Leaves': '',
      'Fenugreek Leaves': '',
      'Dill Leaves': '',
      'Mustard Leaves': '',
      'Amaranth Leaves': '',
      'Sorrel Leaves': '',
      'Colocasia Leaves': '',
      'Okra': ''
    };
    return emojiMap[cropName] || '';
  };

  // Get highest price for each vegetable across all Chennai markets
  const getHighestPricesAcrossMarkets = () => {
    const cropPrices: { [key: string]: { price: number; emoji: string; name: string } } = {};

    // Extract all unique crops with their highest prices
    marketData.forEach(market => {
      market.crops.forEach(crop => {
        const priceMatch = crop.price.match(/([\d.]+)/);
        const price = priceMatch ? parseFloat(priceMatch[1]) : 0;

        if (!cropPrices[crop.name] || price > cropPrices[crop.name].price) {
          cropPrices[crop.name] = {
            price,
            emoji: getCropEmoji(crop.name),
            name: crop.name
          };
        }
      });
    });

    // Sort by price (highest first) and show at least 20 crops
    return Object.values(cropPrices)
      .sort((a, b) => b.price - a.price)
      .slice(0, 20) // Show top 20 highest priced vegetables
      .map(crop => ({
        crop: `${crop.emoji} ${crop.name}`,
        avgPrice: crop.price,
        emoji: crop.emoji,
        name: crop.name
      }));
  };

  const marketPrices = getHighestPricesAcrossMarkets();

  const totalEarnings = soldListings.reduce((sum, listing) => sum + listing.soldPrice, 0);

  const handleCreateListing = async () => {
    if (!currentUser) {
      alert('Please login first to create listings');
      return;
    }

    if (!selectedMarket || !selectedCrop || !quantity) {
      alert('Please fill all required fields');
      return;
    }

    try {
      const marketPrice = getMarketPrice();
      const totalPrice = calculateTotalPrice();
      const cropEmoji = getCropEmoji(selectedCrop);

      // Create new listing with real database structure
      const newListing: Listing = {
        id: `listing_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
        userId: currentUser.id,
        crop: `${cropEmoji} ${selectedCrop}`,
        quantity: `${quantity} kg`,
        pricePerKg: marketPrice,
        market: selectedMarket,
        transport: needTransport ? 'Yes' : 'No',
        views: 0,
        inquiries: 0,
        totalPrice: totalPrice,
        status: 'active',
        postedDate: 'Just now',
        createdAt: new Date().toISOString()
      };

      // Save to real database service
      dataService.addListing(newListing);

      // Update local state
      setActiveListings(prev => [...prev, newListing]);

      // Show success message
      alert(` Listing created successfully!\n\nCrop: ${selectedCrop}\nQuantity: ${quantity} kg\nMarket: ${selectedMarket}\nTotal Value: ${totalPrice.toLocaleString()}`);

      // Switch to active listings tab to show the new listing
      setSelectedTab('active');
      setShowCreateListingDialog(false);

      // Reset form
      setSelectedCrop('');
      setSelectedMarket('');
      setQuantity('');
      setNeedTransport(false);
    } catch (error) {
      console.error('Error creating listing:', error);
      alert('Failed to create listing. Please try again.');
    }
  };

  const prefillFromMarketPrice = (crop: any) => {
    setSelectedCrop(crop.name);
    setShowCreateListingDialog(true);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-green-50 to-blue-50 pb-20">
      {/* Header - Mobile Optimized */}
      <div className="bg-white shadow-sm border-b border-green-100 sticky top-0 z-10">
        <div className="px-4 py-3">
          <div className="flex items-center space-x-3">
            <button
              onClick={() => navigate(-1)}
              className="p-2 hover:bg-gray-100 rounded-xl transition-colors"
            >
              <ArrowLeft className="w-5 h-5 text-gray-600" />
            </button>
            <div>
              <h1 className="text-xl font-bold text-gray-900">Sell Crops</h1>
              <p className="text-xs text-gray-600">Create listings and track sales</p>
            </div>
          </div>
        </div>
      </div>

      {/* Top Section - Mobile Optimized Tabs and Earnings */}
      <div className="px-4 py-4">
        <div className="flex items-center justify-between mb-4 min-h-[48px]">
          {/* Tabs - Mobile Sized */}
          <div className="flex bg-white rounded-2xl p-1 shadow-sm border h-12">
            <button
              onClick={() => setSelectedTab('create')}
              className={`px-3 py-2 rounded-xl font-bold text-sm transition-all flex items-center justify-center ${
                selectedTab === 'create'
                  ? 'bg-green-500 text-white shadow-sm'
                  : 'text-gray-600 hover:text-green-600'
              }`}
            >
              Sell
            </button>
            <button
              onClick={() => setSelectedTab('active')}
              className={`px-3 py-2 rounded-xl font-bold text-sm transition-all flex items-center justify-center ${
                selectedTab === 'active'
                  ? 'bg-green-500 text-white shadow-sm'
                  : 'text-gray-600 hover:text-green-600'
              }`}
            >
              Active
            </button>
            <button
              onClick={() => setSelectedTab('sold')}
              className={`px-3 py-2 rounded-xl font-bold text-sm transition-all flex items-center justify-center ${
                selectedTab === 'sold'
                  ? 'bg-green-500 text-white shadow-sm'
                  : 'text-gray-600 hover:text-green-600'
              }`}
            >
              Sold
            </button>
          </div>

          {/* Counts and Earnings Display */}
          <div className="flex space-x-2">
            <div className="text-center bg-blue-50 px-2 py-1.5 rounded-xl border border-blue-200 min-w-[50px]">
              <p className="text-xs text-blue-600 font-medium leading-none">Active</p>
              <p className="text-base font-bold text-blue-700 mt-0.5">{activeListings.length}</p>
            </div>
            <div className="text-center bg-orange-50 px-2 py-1.5 rounded-xl border border-orange-200 min-w-[50px]">
              <p className="text-xs text-orange-600 font-medium leading-none">Sold</p>
              <p className="text-base font-bold text-orange-700 mt-0.5">{soldListings.length}</p>
            </div>
            <div className="text-center bg-green-50 px-2 py-1.5 rounded-xl border border-green-200 min-w-[60px]">
              <p className="text-xs text-green-600 font-medium leading-none">Earned</p>
              <p className="text-xs font-bold text-green-700 mt-0.5">{totalEarnings.toLocaleString()}</p>
            </div>
          </div>
        </div>

        {/* Listings Section - Mobile Optimized */}
        <div className="space-y-3 mb-6">
          {selectedTab === 'create' && (
            <>
              <div className="mb-6">
                <h2 className="text-lg font-bold text-gray-900 mb-3"> Select Crop to Sell</h2>
                <p className="text-sm text-gray-600 mb-4">Choose from current Chennai market prices. Tap a crop to create your listing.</p>
                <div className="grid grid-cols-2 gap-3">
                  {marketPrices.map((crop, index) => (
                    <Card 
                      key={index}
                      className="bg-white rounded-2xl shadow-sm border border-gray-100 cursor-pointer hover:shadow-md transition-all duration-200 active:scale-95"
                      onClick={() => prefillFromMarketPrice(crop)}
                    >
                      <CardContent className="p-4 text-center">
                        <div className="text-3xl mb-2">{crop.emoji}</div>
                        <h3 className="font-bold text-sm text-gray-900 truncate mb-1">{crop.name}</h3>
                        <p className="text-green-600 font-bold text-sm">{crop.avgPrice}/kg</p>
                        <p className="text-xs text-gray-500 mt-1">Market Price</p>
                      </CardContent>
                    </Card>
                  ))}
                </div>
              </div>
            </>
          )}

          {selectedTab === 'active' && (
            <>
              {activeListings.length === 0 ? (
                <div className="text-center py-12">
                  <div className="text-6xl mb-4"></div>
                  <h3 className="text-lg font-bold text-gray-900 mb-2">No Active Listings</h3>
                  <p className="text-gray-600 mb-4">You don't have any active listings yet.</p>
                  <Button 
                    onClick={() => setSelectedTab('create')}
                    className="bg-green-500 hover:bg-green-600 text-white px-6 py-2 rounded-xl"
                  >
                    <Plus className="w-4 h-4 mr-2" />
                    Create Your First Listing
                  </Button>
                </div>
              ) : (
                activeListings.map((listing) => (
                <Card key={listing.id} className="bg-white rounded-2xl shadow-sm border border-gray-100">
                  <CardContent className="p-4">
                    {/* Mobile Layout: Vertical Stack */}
                    <div className="flex items-start justify-between mb-3">
                      <div className="flex items-center space-x-3 flex-1">
                        <div className="text-3xl">{listing.crop.split(' ')[0]}</div>
                        <div className="flex-1">
                          <h3 className="font-bold text-lg text-gray-900 leading-tight">{listing.crop}</h3>
                          <p className="text-sm text-gray-500">{listing.quantity}  {listing.pricePerKg}/kg  Transport: {listing.transport}</p>
                        </div>
                      </div>
                    </div>

                    {/* Market Status Badge */}
                    <div className="mb-3">
                      <MarketStatusBadge status={getMarketStatus(listing)} />
                    </div>

                    <div className="flex items-center justify-between bg-gray-50 rounded-xl p-3">
                      <div className="flex items-center space-x-4">
                        <div className="flex items-center space-x-1 text-blue-600">
                          <Eye className="w-4 h-4" />
                          <span className="text-xs font-medium">{listing.views}</span>
                        </div>
                        <div className="flex items-center space-x-1 text-orange-600">
                          <Clock className="w-4 h-4" />
                          <span className="text-xs font-medium">{listing.inquiries}</span>
                        </div>
                      </div>
                      <p className="text-xs text-gray-500">{listing.postedDate}</p>
                    </div>
                  </CardContent>
                </Card>
                ))
              )}
            </>
          )}

          {selectedTab === 'sold' && (
            <>
              {soldListings.length === 0 ? (
                <div className="text-center py-12">
                  <div className="text-6xl mb-4"></div>
                  <h3 className="text-lg font-bold text-gray-900 mb-2">No Sales Yet</h3>
                  <p className="text-gray-600 mb-4">You haven't sold any items yet.</p>
                  <Button 
                    onClick={() => setSelectedTab('create')}
                    className="bg-green-500 hover:bg-green-600 text-white px-6 py-2 rounded-xl"
                  >
                    <Plus className="w-4 h-4 mr-2" />
                    Create Your First Listing
                  </Button>
                </div>
              ) : (
                soldListings.map((listing) => (
                <Card key={listing.id} className="bg-white rounded-2xl shadow-sm border border-gray-100">
                  <CardContent className="p-4">
                    {/* Mobile Layout: Vertical Stack */}
                    <div className="flex items-start justify-between mb-3">
                      <div className="flex items-center space-x-3 flex-1">
                        <div className="text-3xl">{listing.crop.split(' ')[0]}</div>
                        <div className="flex-1">
                          <h3 className="font-bold text-lg text-gray-900 leading-tight">{listing.crop}</h3>
                          <p className="text-sm text-gray-500">{listing.quantity}  {listing.pricePerKg}/kg  Transport: {listing.transport}</p>
                          <p className="text-lg font-bold text-green-600 mt-1">Earned: {listing.soldPrice.toLocaleString()}</p>
                        </div>
                      </div>
                    </div>
                    <div className="flex items-center justify-between bg-green-50 rounded-xl p-3 border border-green-200">
                      <div className="flex items-center space-x-2 text-green-600">
                        <CheckCircle className="w-4 h-4" />
                        <span className="text-sm font-medium">Sold to {listing.buyer}</span>
                      </div>
                      <p className="text-xs text-gray-500">{new Date(listing.soldDate).toLocaleDateString()}</p>
                    </div>
                  </CardContent>
                </Card>
                ))
              )}
            </>
          )}
        </div>

        {/* Floating Action Button - Only show in create tab */}
        {selectedTab === 'create' && (
          <div className="fixed bottom-24 right-6 z-20">
            <Button
              onClick={() => setShowCreateListingDialog(true)}
              className="w-16 h-16 rounded-full bg-green-500 hover:bg-green-600 text-white transition-all duration-300 flex items-center justify-center group hover:scale-110 floating-fab hover:!animate-none"
            >
              <Plus className="w-7 h-7 text-white group-hover:rotate-90 transition-transform duration-300" />
            </Button>
          </div>
        )}

      </div>


      {/* Create New Listing Dialog - Mobile Optimized */}
      <Dialog open={showCreateListingDialog} onOpenChange={setShowCreateListingDialog}>
        <DialogContent className="max-w-sm mx-auto m-4 rounded-2xl">
          <DialogHeader className="text-center">
            <DialogTitle className="text-lg font-bold text-gray-900">Create New Listing</DialogTitle>
          </DialogHeader>
          <div className="space-y-3 py-2">
            {/* Crop Selection - First Step */}
            <div>
              <label className="block text-sm font-bold text-gray-700 mb-2">Choose Crop</label>
              <Select value={selectedCrop} onValueChange={(value) => { setSelectedCrop(value); setSelectedMarket(''); }}>
                <SelectTrigger className="w-full h-12 text-base">
                  <SelectValue placeholder="Select crop to sell" />
                </SelectTrigger>
                <SelectContent>
                  {Array.from(new Set(marketData.flatMap(market => market.crops.map(crop => crop.name)))).sort().map((cropName) => (
                    <SelectItem key={cropName} value={cropName} className="text-base py-3">
                      <div className="flex items-center space-x-2">
                        <span>{getCropEmoji(cropName)}</span>
                        <span>{cropName}</span>
                      </div>
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>

            {/* Market Selection - Second Step */}
            <div>
              <label className="block text-sm font-bold text-gray-700 mb-2">Choose Market</label>
              <Select value={selectedMarket} onValueChange={setSelectedMarket} disabled={!selectedCrop}>
                <SelectTrigger className="w-full h-12 text-base">
                  <SelectValue placeholder={selectedCrop ? "Select market" : "Select crop first"} />
                </SelectTrigger>
                <SelectContent>
                  {selectedCrop && marketData
                    .filter(market => market.crops.some(crop => crop.name === selectedCrop))
                    .map((market) => {
                      const crop = market.crops.find(c => c.name === selectedCrop);
                      return (
                        <SelectItem key={market.id} value={market.name} className="text-base py-3">
                          <div className="flex justify-between items-center w-full">
                            <div className="flex flex-col">
                              <span className="font-medium">{market.name}</span>
                              <span className="text-xs text-gray-500">{market.location}</span>
                            </div>
                            <span className="text-green-600 font-bold">{crop?.price}/kg</span>
                          </div>
                        </SelectItem>
                      )
                    })}
                </SelectContent>
              </Select>
            </div>

            {/* Quantity */}
            <div>
              <label className="block text-sm font-bold text-gray-700 mb-2">Quantity (kg)</label>
              <Input
                type="number"
                placeholder="Enter quantity"
                value={quantity}
                onChange={(e) => setQuantity(e.target.value)}
                className="w-full h-12 text-base"
              />
            </div>

            {/* Price Display (Auto-calculated) */}
            <div>
              <label className="block text-sm font-bold text-gray-700 mb-2">Price Information</label>
              <div className="bg-gray-50 rounded-xl p-4 border">
                {selectedMarket && selectedCrop && (
                  <div className="space-y-2">
                    <div className="flex justify-between">
                      <span className="text-sm text-gray-600">Market:</span>
                      <span className="text-sm font-medium">{selectedMarket}</span>
                    </div>
                    <div className="flex justify-between">
                      <span className="text-sm text-gray-600">Market price:</span>
                      <span className="text-sm font-medium">{getMarketPrice()}/kg</span>
                    </div>
                    {quantity && (
                      <>
                        <div className="flex justify-between">
                          <span className="text-sm text-gray-600">Quantity:</span>
                          <span className="text-sm font-medium">{quantity} kg</span>
                        </div>
                        <div className="flex justify-between border-t pt-2">
                          <span className="text-sm font-bold text-gray-900">Total Price:</span>
                          <span className="text-lg font-bold text-green-600">{calculateTotalPrice().toLocaleString()}</span>
                        </div>
                      </>
                    )}
                  </div>
                )}
                {(!selectedMarket || !selectedCrop) && (
                  <p className="text-sm text-gray-500 text-center">
                    {!selectedMarket ? "Select a market and crop to see price calculation" : "Select a crop to see price calculation"}
                  </p>
                )}
              </div>
            </div>

            {/* Transport Toggle */}
            <div className="bg-gray-50 rounded-xl p-3">
              <div className="flex items-center justify-between">
                <label className="text-sm font-bold text-gray-700">Need Transport?</label>
                <div className="flex items-center space-x-3">
                  <span className={`text-sm font-medium ${!needTransport ? 'text-gray-900' : 'text-gray-500'}`}>No</span>
                  <Switch
                    checked={needTransport}
                    onCheckedChange={setNeedTransport}
                  />
                  <span className={`text-sm font-medium ${needTransport ? 'text-gray-900' : 'text-gray-500'}`}>Yes</span>
                </div>
              </div>
            </div>

            {/* Submit Button */}
            <Button
              onClick={handleCreateListing}
              disabled={!selectedMarket || !selectedCrop || !quantity}
              className="w-full py-4 bg-green-500 hover:bg-green-600 text-white font-bold text-lg rounded-2xl h-12 mt-4"
            >
              Create Listing
            </Button>
          </div>
        </DialogContent>
      </Dialog>
    </div>
  );
};

export default SellProduce;

src/pages/Shop.tsx.content:
import { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { 
  ArrowLeft, 
  Search, 
  Filter, 
  ShoppingCart, 
  Star, 
  Heart,
  Plus,
  Minus,
  Package,
  Truck,
  Shield,
  Zap,
  Sparkles,
  Sprout
} from 'lucide-react';
import { cartManager } from '@/lib/cart';

const Shop = () => {
  const navigate = useNavigate();
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('all');
  const [cartItems, setCartItems] = useState({});
  const [wishlist, setWishlist] = useState({});
  const [cartCount, setCartCount] = useState(0);
  const [cartTotal, setCartTotal] = useState(0);

  useEffect(() => {
    updateCartDisplay();
    const unsubscribe = cartManager.onCartChange(() => {
      updateCartDisplay();
    });
    return unsubscribe;
  }, []);

  const updateCartDisplay = () => {
    const cart = cartManager.getCart();
    const itemCounts = {};
    cart.forEach(item => {
      itemCounts[item.id] = item.quantity;
    });
    setCartItems(itemCounts);
    setCartCount(cartManager.getCartCount());
    setCartTotal(cartManager.getCartTotal());
  };

  const categories = [
    { id: 'all', name: 'All Products', icon: Package },
    { id: 'fertilizers', name: 'Fertilizers', icon: Zap },
    { id: 'seeds', name: 'Seeds', icon: Sprout },
    { id: 'pesticides', name: 'Pesticides', icon: Shield },
    { id: 'tools', name: 'Tools', icon: Package }
  ];

  const products = [
    {
      id: '1',
      name: 'Organic NPK Fertilizer',
      brand: 'FarmGrow',
      price: 245,
      originalPrice: 299,
      rating: 4.5,
      reviews: 1243,
      category: 'fertilizers',
      inStock: true,
      bestseller: true,
      organic: true,
      size: '1kg',
      features: ['Organic', 'Fast Acting', 'All Crops']
    },
    {
      id: '2',
      name: 'Tomato Hybrid Seeds',
      brand: 'SeedMaster',
      price: 120,
      originalPrice: 150,
      rating: 4.8,
      reviews: 856,
      category: 'seeds',
      inStock: true,
      bestseller: false,
      organic: false,
      size: '50g',
      features: ['Hybrid', 'Disease Resistant', 'High Yield']
    },
    {
      id: '3',
      name: 'Copper Fungicide Spray',
      brand: 'CropCare',
      price: 180,
      originalPrice: 210,
      rating: 4.3,
      reviews: 634,
      category: 'pesticides',
      inStock: true,
      bestseller: false,
      organic: false,
      size: '500ml',
      features: ['Fungicide', 'Long Lasting', 'Safe']
    },
    {
      id: '4',
      name: 'Premium Garden Spade',
      brand: 'ToolMaster',
      price: 350,
      originalPrice: 420,
      rating: 4.6,
      reviews: 432,
      category: 'tools',
      inStock: false,
      bestseller: false,
      organic: false,
      size: 'Standard',
      features: ['Steel', 'Ergonomic', 'Durable']
    },
    {
      id: '5',
      name: 'Bio Compost Fertilizer',
      brand: 'EcoGrow',
      price: 180,
      originalPrice: 220,
      rating: 4.7,
      reviews: 965,
      category: 'fertilizers',
      inStock: true,
      bestseller: true,
      organic: true,
      size: '2kg',
      features: ['Bio-Compost', 'Soil Health', 'Eco-Friendly']
    }
  ];

  const filteredProducts = products.filter(product => {
    const matchesSearch = product.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
                         product.brand.toLowerCase().includes(searchQuery.toLowerCase());
    const matchesCategory = selectedCategory === 'all' || product.category === selectedCategory;
    return matchesSearch && matchesCategory;
  });

  const addToCart = (productId) => {
    const product = products.find(p => p.id === productId);
    if (product) {
      cartManager.addToCart(product);
    }
  };

  const removeFromCart = (productId) => {
    cartManager.removeFromCart(productId);
  };

  const toggleWishlist = (productId) => {
    setWishlist(prev => ({
      ...prev,
      [productId]: !prev[productId]
    }));
  };

  const getTotalItems = () => {
    return cartCount;
  };

  const getDiscountPercentage = (original, current) => {
    return Math.round(((original - current) / original) * 100);
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <div className="sticky top-0 z-40 bg-white shadow-sm border-b border-gray-100">
        <div className="flex items-center justify-between px-4 py-4">
          <div className="flex items-center space-x-3">
            <button 
              onClick={() => navigate('/home')}
              className="p-2 -ml-2 hover:bg-gray-100 rounded-full transition-colors"
            >
              <ArrowLeft className="w-6 h-6 text-gray-700" />
            </button>
            <h1 className="text-xl font-bold text-gray-900">Shop</h1>
          </div>

          <div className="relative">
            <button 
              onClick={() => navigate('/cart')}
              className="p-2 hover:bg-gray-100 rounded-full transition-colors"
            >
              <ShoppingCart className="w-6 h-6 text-gray-700" />
              {getTotalItems() > 0 && (
                <div className="absolute -top-1 -right-1 w-5 h-5 bg-red-500 rounded-full flex items-center justify-center">
                  <span className="text-xs font-bold text-white">{getTotalItems()}</span>
                </div>
              )}
            </button>
          </div>
        </div>
      </div>

      <div className="px-4 py-6 space-y-6 pb-24">
        {/* Search Bar */}
        <div className="relative">
          <div className="absolute inset-y-0 left-0 pl-4 flex items-center pointer-events-none">
            <Search className="h-5 w-5 text-gray-400" />
          </div>
          <input
            type="text"
            placeholder="Search fertilizers, seeds, tools..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            className="w-full pl-12 pr-12 py-4 text-base bg-white border border-gray-200 rounded-2xl focus:ring-2 focus:ring-green-500 focus:border-transparent shadow-sm outline-none"
          />
          <div className="absolute inset-y-0 right-0 pr-4 flex items-center">
            <button className="p-1 hover:bg-gray-100 rounded-lg transition-colors">
              <Filter className="h-5 w-5 text-gray-400" />
            </button>
          </div>
        </div>

        {/* Category Tabs */}
        <div className="flex space-x-3 overflow-x-auto pb-2" style={{scrollbarWidth: 'none', msOverflowStyle: 'none'}}>
          {categories.map((category) => (
            <button
              key={category.id}
              onClick={() => setSelectedCategory(category.id)}
              className={`flex items-center space-x-2 px-5 py-3 rounded-full whitespace-nowrap font-medium transition-all duration-200 ${
                selectedCategory === category.id
                  ? 'bg-green-600 text-white shadow-lg'
                  : 'bg-white text-gray-600 border border-gray-200 hover:border-green-300 hover:text-green-600'
              }`}
            >
              <category.icon className="w-4 h-4" />
              <span className="text-sm">{category.name}</span>
            </button>
          ))}
        </div>


        {/* Products Section */}
        <div className="space-y-4">
          <div className="flex items-center justify-between">
            <h2 className="text-xl font-bold text-gray-900">
              {selectedCategory === 'all' ? 'All Products' : categories.find(c => c.id === selectedCategory)?.name}
            </h2>
            <span className="text-sm text-gray-500 bg-gray-100 px-3 py-1 rounded-full">
              {filteredProducts.length} products
            </span>
          </div>

          {/* Product Grid */}
          <div className="space-y-4">
            {filteredProducts.map((product) => (
              <div 
                key={product.id} 
                className="bg-white rounded-2xl p-5 shadow-sm border border-gray-100 hover:shadow-md transition-shadow duration-200"
              >
                <div className="flex space-x-4">
                  {/* Product Icon */}
                  <div className="flex-shrink-0">
                    <div className="w-24 h-24 bg-gradient-to-br from-green-50 to-green-100 rounded-2xl flex items-center justify-center border border-green-200">
                      {product.category === 'fertilizers' ? (
                        <Zap className="w-10 h-10 text-green-600" />
                      ) : product.category === 'seeds' ? (
                        <Sprout className="w-10 h-10 text-green-600" />
                      ) : product.category === 'pesticides' ? (
                        <Shield className="w-10 h-10 text-green-600" />
                      ) : (
                        <Package className="w-10 h-10 text-green-600" />
                      )}
                    </div>
                  </div>

                  {/* Product Info */}
                  <div className="flex-1 min-w-0">
                    <div className="flex justify-between items-start">
                      <div className="flex-1 min-w-0">
                        <h3 className="font-bold text-gray-900 text-base leading-tight mb-1">
                          {product.name}
                        </h3>
                        <p className="text-sm text-gray-500 mb-3">
                          {product.brand}  {product.size}
                        </p>

                        {/* Rating */}
                        <div className="flex items-center space-x-2 mb-3">
                          <div className="flex items-center space-x-1">
                            <Star className="w-4 h-4 fill-yellow-400 text-yellow-400" />
                            <span className="text-sm font-semibold text-gray-700">{product.rating}</span>
                          </div>
                          <span className="text-sm text-gray-400">({product.reviews} reviews)</span>
                        </div>

                        {/* Feature Tags */}
                        <div className="flex flex-wrap gap-2 mb-4">
                          {product.features.slice(0, 3).map((feature, index) => (
                            <span 
                              key={index}
                              className="text-xs px-2 py-1 border border-green-200 text-green-700 bg-green-50 font-medium rounded-full"
                            >
                              {feature}
                            </span>
                          ))}
                        </div>

                        {/* Price */}
                        <div className="flex items-center space-x-3 mb-4">
                          <span className="text-xl font-bold text-gray-900">{product.price}</span>
                          <span className="text-base text-gray-400 line-through">{product.originalPrice}</span>
                          <span className="bg-green-100 text-green-800 text-xs font-semibold px-2 py-1 rounded-full">
                            {getDiscountPercentage(product.originalPrice, product.price)}% OFF
                          </span>
                        </div>
                      </div>

                      {/* Wishlist Button */}
                      <button
                        onClick={() => toggleWishlist(product.id)}
                        className="p-2 hover:bg-gray-50 rounded-full transition-colors"
                      >
                        <Heart 
                          className={`w-5 h-5 ${
                            wishlist[product.id] 
                              ? 'fill-red-500 text-red-500' 
                              : 'text-gray-400 hover:text-red-400'
                          }`} 
                        />
                      </button>
                    </div>

                    {/* Add to Cart Section */}
                    <div className="flex items-center justify-end">
                      {product.inStock ? (
                        <div className="flex items-center space-x-3">
                          {cartItems[product.id] > 0 ? (
                            <div className="flex items-center space-x-3 bg-gray-50 rounded-full p-1">
                              <button
                                onClick={() => removeFromCart(product.id)}
                                className="w-8 h-8 bg-white rounded-full flex items-center justify-center shadow-sm hover:shadow-md transition-shadow"
                              >
                                <Minus className="w-4 h-4 text-gray-600" />
                              </button>
                              <span className="font-bold text-gray-900 min-w-[24px] text-center">
                                {cartItems[product.id]}
                              </span>
                              <button
                                onClick={() => addToCart(product.id)}
                                className="w-8 h-8 bg-green-600 rounded-full flex items-center justify-center shadow-sm hover:shadow-md transition-shadow"
                              >
                                <Plus className="w-4 h-4 text-white" />
                              </button>
                            </div>
                          ) : (
                            <button
                              onClick={() => addToCart(product.id)}
                              className="bg-green-600 hover:bg-green-700 text-white px-6 py-2 rounded-full font-semibold shadow-sm hover:shadow-md transition-all duration-200"
                            >
                              Add to Cart
                            </button>
                          )}
                        </div>
                      ) : (
                        <span className="text-red-600 border border-red-200 bg-red-50 px-3 py-1 rounded-full text-sm">
                          Out of Stock
                        </span>
                      )}
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>

      {/* Floating Cart Button */}
      {getTotalItems() > 0 && (
        <div className="fixed bottom-6 left-4 right-4 z-50">
          <button 
            onClick={() => navigate('/cart')}
            className="w-full bg-green-600 hover:bg-green-700 text-white py-4 rounded-2xl shadow-2xl flex items-center justify-between text-lg font-bold px-6"
          >
            <span className="flex items-center space-x-3">
              <ShoppingCart className="w-6 h-6" />
              <span>View Cart ({getTotalItems()} items)</span>
            </span>
            <span>{cartTotal}</span>
          </button>
        </div>
      )}
    </div>
  );
};

export default Shop;

src/pages/Signup.tsx.content:
import React, { useState, useEffect } from "react";
import { User, Phone, Sprout } from "lucide-react";
import { toast } from "@/hooks/use-toast";
import { useNavigate } from "react-router-dom";
import LanguageSwitcher from '@/components/LanguageSwitcher';
import CustomInput from '@/components/CustomInput';
import LocationPicker from '@/components/LocationPicker';
import PINInput from '@/components/PINInput';
import AuthButton from '@/components/AuthButton';


const Signup = () => {
  const [formData, setFormData] = useState({
    name: "",
    mobile: "",
    state: "",
    district: "",
    pin: "",
    confirmPin: ""
  });
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleInputChange = (field: string, value: string) => {
    setFormData(prev => ({ ...prev, [field]: value }));
  };

  // Validation functions
  const getNameValidation = () => {
    if (formData.name.length === 0) return undefined;
    const isValid = formData.name.trim().length >= 2 && /^[A-Za-z\s]+$/.test(formData.name);
    return {
      isValid,
      message: isValid ? "Valid name" : "Name must contain only letters and be at least 2 characters"
    };
  };

  const getMobileValidation = () => {
    if (formData.mobile.length === 0) return undefined;
    const isValid = formData.mobile.length === 10 && /^[6-9]\d{9}$/.test(formData.mobile);
    return {
      isValid,
      message: isValid ? "Valid mobile number" : "Enter a valid 10-digit Indian mobile number"
    };
  };

  const isFormValid = () => {
    return formData.name.trim() !== "" &&
           formData.mobile.length === 10 &&
           formData.state !== "" &&
           formData.district.trim() !== "" &&
           formData.pin.length === 4 &&
           formData.confirmPin.length === 4 &&
           formData.pin === formData.confirmPin;
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (formData.pin !== formData.confirmPin) {
      toast({
        title: "PIN Mismatch",
        description: "PIN and Confirm PIN must match",
        variant: "destructive"
      });
      return;
    }

    if (!isFormValid()) {
      toast({
        title: "Missing Information",
        description: "Please fill all required fields",
        variant: "destructive"
      });
      return;
    }

    setLoading(true);

    try {
      // Simulate API call
      await new Promise(resolve => setTimeout(resolve, 2000));

      // Store comprehensive user data
      const userData = {
        id: Date.now().toString(),
        name: formData.name.trim(),
        mobile: formData.mobile,
        phone: `+91 ${formData.mobile}`,
        state: formData.state,
        district: formData.district,
        location: formData.district,
        pin: formData.pin,
        joinedDate: new Date().toLocaleDateString('en-US', { month: 'long', year: 'numeric' }),
        farmSize: '2.5 acres',
        experience: '5 years',
        avatar: null,
        isLoggedIn: true,
        createdAt: new Date().toISOString()
      };

      localStorage.setItem('kisanmitra_user', JSON.stringify(userData));
      localStorage.setItem('kisanmitra_auth_token', btoa(`${formData.mobile}:${formData.pin}`));

      const welcomeToast = toast({
        title: "Account Created! ",
        description: `Welcome to KisanMitra, ${formData.name}!`,
        duration: 10000, // Auto-dismiss after 10 seconds
      });

      // Dismiss on any interaction
      let isToastDismissed = false;
      const handleInteraction = () => {
        if (!isToastDismissed) {
          isToastDismissed = true;
          welcomeToast.dismiss();
          cleanupListeners();
        }
      };

      const cleanupListeners = () => {
        document.removeEventListener('click', handleInteraction);
        document.removeEventListener('keydown', handleInteraction);
        document.removeEventListener('touchstart', handleInteraction);
      };

      document.addEventListener('click', handleInteraction);
      document.addEventListener('keydown', handleInteraction);
      document.addEventListener('touchstart', handleInteraction);

      // Cleanup listeners after 10 seconds (when toast auto-dismisses)
      setTimeout(() => {
        isToastDismissed = true;
        cleanupListeners();
      }, 10000);

      navigate("/home");

    } catch (error) {
      toast({
        title: "Registration failed",
        description: "Please try again",
        variant: "destructive"
      });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="mobile-container min-h-screen bg-gradient-to-br from-agri-light to-white overflow-y-auto">
      {/* Subtle Background Pattern */}
      <div className="absolute inset-0 opacity-5">
        <div className="absolute inset-0" style={{
          backgroundImage: `url("data:image/svg+xml,%3Csvg width='40' height='40' viewBox='0 0 40 40' xmlns='http://www.w3.org/2000/svg'%3E%3Cg fill='%23059669' fill-opacity='0.1'%3E%3Cpath d='M20 20c0-5.5-4.5-10-10-10s-10 4.5-10 10 4.5 10 10 10 10-4.5 10-10zm10 0c0-5.5-4.5-10-10-10s-10 4.5-10 10 4.5 10 10 10 10-4.5 10-10z'/%3E%3C/g%3E%3C/svg%3E")`,
        }} />
      </div>

      <div className="relative z-10 min-h-screen flex flex-col">
        {/* Top Bar */}
        <div className="flex items-center justify-between p-4 bg-white/80 backdrop-blur-sm">
          <div className="flex items-center space-x-3">
            <div className="w-12 h-12 bg-white rounded-2xl shadow-md flex items-center justify-center p-1">
              <img 
                src="/assets/kisanmitra-logo-clean.png" 
                alt="KisanMitra Logo" 
                className="w-full h-full object-contain"
              />
            </div>
            <div>
              <h1 className="text-lg font-bold text-green-900">KisanMitra</h1>
              <p className="text-xs text-green-700">Smart Farming Assistant</p>
            </div>
          </div>
          <LanguageSwitcher />
        </div>

        {/* Header */}
        <div className="text-center px-6 pt-8 pb-6">
          <div className="w-24 h-24 bg-gradient-to-br from-green-400 to-green-600 rounded-3xl mx-auto mb-6 flex items-center justify-center shadow-xl">
            <Sprout className="w-12 h-12 text-white" />
          </div>
          <h1 className="text-3xl font-bold bg-gradient-to-r from-green-600 to-green-500 bg-clip-text text-transparent mb-3">
             Join 50,000+ Smart Farmers
          </h1>
          <p className="text-lg text-gray-600 leading-relaxed mb-2">
            Create your farming account
          </p>
          <p className="text-sm text-green-600 font-medium">
            Location helps us provide region-based crop advice
          </p>
        </div>

          {/* Form */}
          <div className="flex-1 px-6">
            <div className="bg-white rounded-3xl p-8 shadow-2xl border border-green-100">
              <form onSubmit={handleSubmit} className="space-y-6">
                {/* Name Field */}
                <div className="space-y-2">
                  <label className="text-sm font-semibold text-gray-700">Full Name</label>
                  <div className="relative">
                    <div className="absolute left-4 top-1/2 transform -translate-y-1/2">
                      <User className="w-5 h-5 text-green-500" />
                    </div>
                    <input
                      type="text"
                      value={formData.name}
                      onChange={(e) => handleInputChange("name", e.target.value)}
                      placeholder="Enter your full name"
                      className="w-full h-14 pl-12 pr-4 text-lg font-medium bg-gray-50 border-2 border-gray-200 rounded-2xl focus:outline-none focus:border-green-500 focus:ring-4 focus:ring-green-100 transition-all duration-200"
                    />
                  </div>
                  {getNameValidation() && (
                    <p className={`text-sm ${getNameValidation()?.isValid ? 'text-green-600' : 'text-red-500'}`}>
                      {getNameValidation()?.message}
                    </p>
                  )}
                </div>

                {/* Mobile Field */}
                <div className="space-y-2">
                  <label className="text-sm font-semibold text-gray-700">Mobile Number</label>
                  <div className="relative">
                    <div className="absolute left-4 top-1/2 transform -translate-y-1/2 flex items-center space-x-2 z-10">
                      <Phone className="w-5 h-5 text-green-500" />
                      <span className="text-gray-500 font-medium"> +91</span>
                    </div>
                    <input
                      type="tel"
                      value={formData.mobile}
                      onChange={(e) => handleInputChange("mobile", e.target.value.replace(/\D/g, '').slice(0, 10))}
                      placeholder="10 digit number"
                      className="w-full h-14 pl-24 pr-4 text-lg font-medium bg-gray-50 border-2 border-gray-200 rounded-2xl focus:outline-none focus:border-green-500 focus:ring-4 focus:ring-green-100 transition-all duration-200"
                    />
                  </div>
                  {getMobileValidation() && (
                    <p className={`text-sm ${getMobileValidation()?.isValid ? 'text-green-600' : 'text-red-500'}`}>
                      {getMobileValidation()?.message}
                    </p>
                  )}
                </div>

                {/* Location Fields */}
                <LocationPicker
                  state={formData.state}
                  district={formData.district}
                  onStateChange={(value) => handleInputChange("state", value)}
                  onDistrictChange={(value) => handleInputChange("district", value)}
                />

                {/* PIN Entry */}
                <div className="space-y-4">
                  <div className="space-y-2">
                    <label className="text-sm font-semibold text-gray-700">4-Digit PIN</label>
                    <PINInput
                      value={formData.pin}
                      onChange={(value) => handleInputChange("pin", value)}
                      placeholder="Create PIN"
                      showStrengthIndicator={true}
                    />
                  </div>

                  <div className="space-y-2">
                    <label className="text-sm font-semibold text-gray-700">Confirm PIN</label>
                    <PINInput
                      value={formData.confirmPin}
                      onChange={(value) => handleInputChange("confirmPin", value)}
                      placeholder="Confirm PIN"
                    />
                    {formData.confirmPin.length > 0 && (
                      <p className={`text-sm ${formData.pin === formData.confirmPin ? 'text-green-600' : 'text-red-500'}`}>
                        {formData.pin === formData.confirmPin ? " PINs match" : " PINs don't match"}
                      </p>
                    )}
                  </div>
                </div>

                {/* Create Account Button - Full Width Green Gradient */}
                <div className="pt-6">
                  <button
                    type="submit"
                    disabled={loading || !isFormValid()}
                    className="w-full h-16 bg-gradient-to-r from-green-500 to-green-600 hover:from-green-600 hover:to-green-700 text-white text-xl font-bold rounded-2xl shadow-lg hover:shadow-xl transform hover:scale-[1.02] active:scale-[0.98] transition-all duration-200 disabled:opacity-50 disabled:cursor-not-allowed"
                  >
                    {loading ? (
                      <div className="flex items-center justify-center space-x-2">
                        <div className="w-5 h-5 border-2 border-white border-t-transparent rounded-full animate-spin"></div>
                        <span>Creating Account...</span>
                      </div>
                    ) : (
                      'Create My Account'
                    )}
                  </button>
                </div>
              </form>
            </div>

            {/* Login Link */}
            <div className="text-center mt-6 mb-6">
              <div className="flex items-center justify-center space-x-2 text-gray-600">
                <span>Already have an account?</span>
                <button
                  type="button"
                  onClick={() => navigate('/login')}
                  className="text-green-600 font-bold hover:text-green-700 hover:underline transition-colors"
                >
                  Login 
                </button>
              </div>
            </div>
          </div>
      </div>
    </div>
  );
};

export default Signup;

